  @if $include-html-clearing-classes {
    // We decided to not create a mixin for Clearing because it relies
    // on predefined classes and structure to work properly.
    // The variables above should give enough control.

    /* Clearing Styles */
    .clearing-thumbs, #{data('clearing')} {
      @include clearfix;
      list-style: none;
      margin-#{$default-float}: 0;
      margin-bottom: 0;

      li {
        float: $default-float;
        margin-#{$opposite-direction}: 10px;
      }

      &[class*="block-grid-"] li {
        margin-#{$opposite-direction}: 0;
      }
    }

    .clearing-blackout {
      background: $clearing-bg;
      height: 100%;
      position: fixed;
      top: 0;
      width: 100%;
      z-index: 998;
      #{$default-float}: 0;

      .clearing-close { display: block; }
    }

    .clearing-container {
      height: 100%;
      margin: 0;
      overflow: hidden;
      position: relative;
      z-index: 998;
    }

    .clearing-touch-label {
      color: $base;
      font-size: .6em;
      left: 50%;
      position: absolute;
      top: 50%;
    }

    .visible-img {
      height: 95%;
      position: relative;

      img {
        position: absolute;
        #{$default-float}: 50%;
        top: 50%;
        @if $default-float == left {
