layouts/body/body.scss:
// =============================================================================
// BODY (CSS)
//
// 1. Header is fixed on small screens, use the content body to create the whitespace
//    between it and the header on all situations. If you change the spacing here,
//    please update .heroCarousel too.
//
// =============================================================================


.body {
    display: block;
    position: relative;
    background-color: $body-bg;
    margin-top: 15px;
    margin-bottom: 100px;

    .page-type-page_contact_form & {
        margin-top: 0;
    }

    .page-type-default &,
    .page-type-category &,
    .page-type-cart & {
        margin-bottom: 50px;
    }

    .page-type-product & {
        margin-bottom: 80px;
    }

    .page-type-default & {
        margin-top: 15px;
    }

    .page-type-default.home-layout-2 &,
    .page-type-default.home-layout-3 &,
    .page-type-default.home-layout-4 & {
        margin-top: 0;
        margin-bottom: 0;
    }
}

.page-type-category,
.page-type-search,
.page-type-brand {
    .breadcrumb-wrapper {
        margin-bottom: 15px;
        padding-top: 0;
    }
}


.page {
    @include grid-row($behavior: nest);
    margin: 0;
}

.page-content,
.page-content--full {
    @include grid-column(12);
    position: relative;
    padding: 0;
}

.page-content--centered {

    @include breakpoint("medium") {
        @include grid-column($columns: 12,
            $center: true);
    }
}

.page-content--textCenter {
    text-align: center;
}

.halo-page-content {
    margin-top: 22px;
}

.page-sidebar {
    position: fixed;
    top: 0;
    left: -101%;
    bottom: 0;
    width: 100%;
    height: 100%;
    z-index: 102;
    max-width: 340px;
    transition: all .5s ease;
    background-color: $color-white;
    box-shadow: rgba(50, 50, 50, 0.2) 0 0 10px 0;

    &.has-toolbar {
        @media(min-width: 1025px) {
            margin-top: -67px;

            .page-listing & {
                padding-top: 0;
            }
        }
    }

    .facetedSearch-refineFilters {
        padding: 0;
        margin: 0;
    }

    .page-sidebar-close {
        position: relative;
        padding: 15px 15px 10px;
        margin-bottom: 10px;

        .title {
            display: block;
            font-size: $fontSize-root + 4px;
            font-weight: $fontWeight-bold;
            letter-spacing: $letterSpacing-base;
            line-height: 25px;
            color: stencilColor('color-textBase');
            text-transform: capitalize;
            margin: 0;
        }

        .close {
            position: absolute;
            top: 50%;
            right: 15px;
            transform: translateY(-50%);
            line-height: 1;

            svg {
                width: 16px;
                height: 16px;
                fill: $color-textBase;
            }
        }
    }

    >nav {
        display: block;
        width: 100%;
        height: 100%;
        overflow-y: auto;
        padding: 40px 30px 100px 30px;
    }

    .page-sidebar-content {
        display: block;
        width: 100%;
        height: 100%;
        overflow-y: auto;
        padding: 40px 30px 100px 30px;
    }

    .accordion--navList {
        .accordion-block {
            margin-bottom: 20px;
            padding-bottom: 0;

            .accordion-title {
                font-size: $fontSize-root;
                margin-bottom: 20px;
            }

            .accordion-navigation-actions {
                .icon {
                    padding: 3px;
                }
            }

            .toggleLink-text {
                transform: rotate(90deg);
            }

            .accordion-navigation {
                &.is-open {
                    .toggleLink-text {
                        transform: rotate(-90deg);
                    }
                }
            }
        }
    }

    @media (max-width: 1024px) {
        &.is-open {
            .openSidebar & {
                left: 0;
            }
        }

        >nav {
            padding: 15px 15px 100px;
        }

        .page-sidebar-content {
            padding: 15px 15px 100px;
        }
    }

    @media (min-width: 1025px) {
        position: relative;
        top: auto;
        bottom: auto;
        left: auto;
        z-index: 5;
        float: left;
        width: 270px;
        display: inline-block;
        box-shadow: none;
        background-color: transparent;
        max-width: none;
        transition: unset;

        .page-sidebar-close {
            display: none;
        }

        .page-sidebar-content {
            padding: 0;
            overflow: visible;
        }

        >nav {
            padding: 0;
            overflow: visible;
        }

        +.page-content {
            padding-left: 30px;
            width: calc(100% - 270px);
        }

        &.u-hiddenVisually {
            +.page-content {
                padding-left: 0;
                width: 100%;
            }
        }

        &.u-hidden {
            +.page-content {
                padding-left: 0;
                width: 100%;
            }
        }

        .page-custom-sidebar & {
            float: right;
            width: 350px;
            padding: 45px 40px;
            border: 1px solid $color-custom-grey;
            border-right-width: 0;

            +.page-content {
                width: calc(100% - 350px);
                padding-left: 0;
                padding-right: 30px;
            }
        }

        .page-listing & {
            position: sticky;
            top: 80px;
            padding-top: 17px;
            padding-bottom: 42px;
        }
    }

    @media (min-width: 1600px) {
        +.page-content {
            padding-left: 40px;
        }

        .page-custom-sidebar & {
            padding-top: 53px;
            padding-bottom: 45px;
            padding-right: calc((100vw - 1485px)/2);
            padding-left: 95px;
            width: 542px;

            +.page-content {
                width: calc(100% - 542px);
                padding-right: 95px;
            }
        }
    }

    @media (min-width: 1920px) {
        .page-custom-sidebar & {
            padding-right: calc((100vw - 1585px)/2);
        }
    }
}

.page-sidebar-mobile-wrapper {
    display: block;
    position: relative;
    margin: 0 0 30px 0;

    .page-sidebar-mobile {
        position: relative;
        display: inline-block;
        z-index: 2;
        cursor: pointer;
        pointer-events: auto;

        .text {
            display: none;
            vertical-align: middle;
            font-size: $fontSize-root - 2px;
            font-weight: $fontWeight-bold;
            letter-spacing: $letterSpacing-base;
            margin: 0;
            padding: 0;
            line-height: 1;
            color: $color-textBase;
            text-transform: uppercase;

            .page-blog & {
                display: inline-block;
                margin-right: 10px;
            }
        }

        .icon {
            display: inline-block;
            vertical-align: middle;
            width: 46px;
            height: 46px;
            padding: 14px;
            fill: $color-textBase;
            stroke: transparent;
            pointer-events: none;
            background: #e5e5e5;
        }
    }

    @media (min-width: 992px) {
        .page-sidebar-mobile {
            .text {
                display: inline-block;
                margin-right: 20px;
            }
        }
    }

    @media (min-width: 1025px) {
        display: none;
        margin-bottom: 0;
    }
}

.page-heading {
    position: relative;
    margin: 0;
    text-transform: uppercase;
    font-size: 30px;
    font-weight: $fontWeight-semibold;
    color: $color-textBase;
    line-height: 40px;
    letter-spacing: $letterSpacing-base;
    text-align: left;

    +.page-description {
        margin-top: 12px;
    }

    &[data-cart-page-title] {
        margin-bottom: 22px;

        span {
            font-family: $fontFamily-sans;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            color: $color-textBase;
            line-height: 25px;
            margin: 0 0 0 15px;
            text-transform: capitalize;
            opacity: .7;
        }
    }

    .page-errors & {
        .heading {
            display: block;
            text-align: center;
            font-size: 100px;
            color: $color-textBase;
            font-weight: $fontWeight-medium;
            letter-spacing: $letterSpacing-base;
            line-height: 1;
            margin: 0;
        }

        .text {
            display: block;
            text-align: center;
            font-size: 24px;
            font-weight: $fontWeight-medium;
            letter-spacing: $letterSpacing-base;
            text-transform: capitalize;
            line-height: 1;
            margin: 30px 0 0 0;
        }
    }

    @media (min-width: 551px) {
        .page-errors & {
            .text {
                font-size: 40px;
            }
        }
    }

    @media (min-width: 1025px) {
        font-size: 24px;

        .page-errors & {
            .heading {
                font-size: 200px;
            }
        }
    }
}

body {
    overflow-y: auto;

    &::-webkit-scrollbar {
        width: 10px;
    }

    &::-webkit-scrollbar-track {
        background: #f5f5f5;
    }

    &::-webkit-scrollbar-thumb {
        background: #a6a6a6;
    }

    &::-webkit-scrollbar-thumb:hover {
        background: #787878;
    }

    &.has-activeModal {
        @media (min-width: 1025px) {
            padding-right: 10px;
        }
    }
}

layouts/header/header.scss:
// =============================================================================
// HEADER (CSS)
//
// 1. Setting font size of header-logo to 0, since wrapper element may vary, and
//    inline-block whitespace messes with header height. Font-size is reset to
//    appropriate size on header-logo-text.
// 2. Needs left/right margin so it doesn't cover left/right nav bar toggles
// 3. Offset the letter-spacing on the last character by adding a right negative
//    margin. This centers the text logo properly.
// 4. When the word "cart" is added to the mobile header's cart link, add more
//    margin to compensate.
// 5. When logo size is set to "original", we don't have advance knowledge of the
//    image size, so we can't use absolute positioning + padding to reserve space
//    for lazy loading.
// 6. When logo size is set to "original" and switch to mobile version, it keeps
//    content in center regardless its size.
//
// -----------------------------------------------------------------------------

.skip-to-main-link {
    background: $header-bg;
    color: $color-textBase;
    font-weight: 700;
    left: 50%;
    padding: 4px;
    position: absolute;
    transform: translate(-50%, calc(-100% - #{$header-height}));
    transition: transform 0.3s;
    z-index: zIndex('highest');

    &:focus {
        transform: translate(-50%, 0%);
    }
}

.header {
    background-color: $header-bg;
    border-bottom: none;
    box-shadow: 0 1px 2px 1px #e5e5e5;
    position: relative;
    z-index: 99;
    @include clearfix;

    &.header-layout-2,
    &.header-layout-3,
    &.header-layout-4 {
        box-shadow: none;
    }

    &.header-layout-4 {
        .page-type-default & {
            position: absolute;
            top: 0px;
            width: 100%;
            background-color: transparent;
            transition: all .35s;

            &:not(.is-sticky) {

                .header-logo-image:not(.no-logo-transparent) {
                    display: none;

                    &.header-logo-transparent {
                        display: block;
                    }
                }

                .halo-topHeader {
                    background-color: transparent;
                    color: $color-white;

                    .navUser-action {
                        padding: 10px 15px 9px;
                        color: $color-white;

                        a {
                            color: $color-white;
                        }

                        svg {
                            fill: $color-white;
                            stroke: $color-white;
                        }
                    }
                }

                .halo-middleHeader {
                    border-top: 1px solid $color-white;
                    border-bottom: 1px solid $color-white;

                    .mobileMenu-toggle {
                        .mobileMenu-toggleIcon {
                            background: $color-white;

                            &:before,
                            &:after {
                                background: $color-white;
                            }
                        }
                    }

                    .navUser-action {
                        color: $color-white;

                        a {
                            color: $color-white;
                        }

                        svg {
                            fill: $color-white;
                            stroke: $color-white;
                        }
                    }
                }

                .halo-bottomHeader {
                    border-top: 1px solid $color-white;
                    border-bottom: 1px solid $color-white;

                    .bottomHeader-item {
                        .navUser-action {

                            .navUser-icon,
                            .navUser-item-cartLabel {
                                svg {
                                    fill: $color-white;
                                    stroke: $color-white;
                                }
                            }
                        }
                    }

                    .navPages-list {
                        >.navPages-item {
                            >.navPages-action {
                                span:not(.navPages-label) {
                                    color: $color-white;
                                }
                            }
                        }
                    }

                    .navUser-action {
                        color: $color-white;

                        a {
                            color: $color-white;
                        }

                        svg {
                            fill: $color-white;
                            stroke: $color-white;
                        }
                    }

                    .countPill {
                        color: $color-white;
                    }
                }

                @media(min-width: 1025px) {
                    &:hover,
                    &:focus-within {
                        background-color: $header-bg;

                        .header-logo-image {
                            display: block;

                            &.header-logo-transparent {
                                display: none;
                            }
                        }

                        .halo-topHeader {
                            color: stencilColor('navUser-color');

                            .navUser-action {
                                color: stencilColor('navUser-color');

                                a {
                                    color: stencilColor('navUser-color');
                                }

                                svg {
                                    fill: stencilColor('navUser-color');
                                    stroke: stencilColor('navUser-color');
                                }
                            }
                        }

                        .halo-bottomHeader {
                            border-top: 1px solid $color-textBase;
                            border-bottom: 1px solid $color-textBase;

                            .bottomHeader-item {
                                .navUser-action {

                                    .navUser-icon,
                                    .navUser-item-cartLabel {
                                        svg {
                                            fill: stencilColor('navUser-color');
                                            stroke: stencilColor('navUser-color');
                                        }
                                    }
                                }
                            }

                            .navPages-list {
                                >.navPages-item {
                                    >.navPages-action {
                                        span:not(.navPages-label) {
                                            color: stencilColor("navPages-color");
                                        }
                                    }
                                }
                            }

                            .navUser-action {
                                color: stencilColor('navUser-color');

                                a {
                                    color: stencilColor('navUser-color');
                                }

                                svg {
                                    fill: stencilColor('navUser-color');
                                    stroke: stencilColor('navUser-color');
                                }
                            }
                        }

                        .countPill {
                            color: $color-textBase;
                        }
                    }
                }
            }
        }

        .halo-bottomHeader {
            border-top: 1px solid $color-textBase;
            border-bottom: 1px solid $color-textBase;

            .bottomHeader-item {
                .navUser-action {

                    .navUser-icon,
                    .navUser-item-cartLabel {
                        svg {
                            fill: stencilColor('navUser-color');
                            stroke: stencilColor('navUser-color');
                        }
                    }
                }
            }

            .countPill {
                top: calc(50% + 3px);
                right: calc(50% - 1px);
                transform: translate(50%, -50%);
                color: $color-textBase;
                background-color: transparent;
            }
        }

        .header-logo-transparent {
            display: none;
        }

        .navUser-section--link {
            .navUser-action {
                font-size: $fontSize-root;
                font-weight: 800;
            }
        }

        &.is-sticky {
            background-color: $header-bg;
            box-shadow: none;
            position: fixed;
            top: 0;

            .halo-bottomHeader {
                border-bottom: 1px solid $color-black;
            }

            .header-logo-transparent {
                display: none;

                +.header-logo-image,
                +.header-logo-image-unknown-size {
                    display: block;
                }
            }
        }
    }
}

.header-logo {
    font-size: 0; // 1
    margin: 0; // 2
    height: $header-height;

    @include breakpoint("small") {
        // 4
        margin-left: remCalc($header-toggle-width * 1.5);
        margin-right: remCalc($header-toggle-width * 1.5);
    }

    @media(min-width: 1025px) {
        margin: (spacing("double") + spacing("base")) auto (spacing("double") + spacing("single"));
        padding: 0;
        height: auto;

        .header.fixed & {
            background-color: color("greys", "lightest");
            margin: 0;
            padding: spacing("quarter") 0;
            position: absolute;
            top: 0;
            width: 100%;
        }
    }

    &__link {
        @include clearfix;
        color: $storeName-color;
        height: inherit;
        display: flex;
        justify-content: center; // 6
        align-items: center;
        margin: 0 auto;
        position: relative;
        text-decoration: none;
        width: 70%;
        z-index: zIndex("low");

        // scss-lint:disable NestingDepth
        @include breakpoint("medium") {
            background: none;
            border-bottom: 0;
            display: block;
            padding: 0;
            width: auto;
        }

        &:hover {
            color: color("link", "hover");
        }

        &:active {
            color: color("link", "active");
        }
    }
}

.header-logo--left {
    text-align: left;

    @include breakpoint("medium") {
        margin-left: remCalc(40px);
    }

    .header-logo-image {
        right: unset;
    }
}

.header-logo--right {
    text-align: right;

    @include breakpoint("medium") {
        margin-right: remCalc(40px);
    }

    .header-logo-image {
        left: unset;
    }
}

.header-logo-text {
    display: block;
    font-family: $fontFamily-hero;
    font-size: $fontSize-root + 2px; // 1
    line-height: 1.2;
    font-weight: stencilFontWeight("headings-font");
    letter-spacing: remCalc(2px);
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap;

    @include breakpoint("small") {
        font-size: $fontSize-logo-medium;
    }

    @include breakpoint("medium") {
        display: inline;
        font-size: $fontSize-logo-large;
        max-width: none;
        white-space: normal;
        padding: 0 10px;

        .header.fixed & {
            font-size: fontSize("larger");
        }
    }
}

.header-logo__link {
    min-height: get-height(stencilString('logo_size'));
    display: flex;
    align-items: center;
}

.header-logo-image-container {
    display: block;
    position: relative;
    width: 100%;
    min-height: get-height(stencilString('logo_size'));
    min-width: get-width(stencilString('logo_size'));
}

.header-logo-image-container:after {
    content: '';
    display: block;
}

.header-logo-image {
    max-height: remCalc($header-height) - $header-logo-marginVertical * 2;
    display: block;
    margin: 0 auto;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;

    @include breakpoint("medium") {
        max-height: none;
    }
}

.header-logo-image-unknown-size {
    // 5
    max-height: remCalc($header-height) - $header-logo-marginVertical * 2;

    @include breakpoint("medium") {
        max-height: none;
    }
}

.halo-topHeader {
    position: relative;
    z-index: 12;
    padding: 0;
    background-color: $header-top-color;
    color: $header-color;

    .container-full {
        max-width: 100%;
        margin: 0 auto;
        padding-right: 22px;

        .header-layout-2 & {
            padding-left: 22px;
        }

        .header-layout-4 & {
            @include breakpoint("medium") {
                max-width: 1550px;
                padding: 0 75px
            }
        }
    }

    .navUser {
        display: flex;
        flex-wrap: nowrap;
        align-items: center;
        justify-content: space-between;
        padding: 0;

        &:before,
        &:after {
            display: none;
        }

        >* {

            .header-layout-2 &,
            .header-layout-3 &,
            .header-layout-4 & {
                width: 33.3334%;
            }
        }

        @media(max-width: 1024px) {
            &.no-menu-tab {
                display: inline-block;

                .navUser-section--alt {
                    float: none;
                    text-align: center;
                    padding: 12px 0;

                    .home-layout-3 &,
                    .home-layout-4 & {
                        margin: 0 auto;
                        padding: 0;
                        width: 100%
                    }
                }

                &.no-top-promotion {
                    display: none;
                }
            }
        }
    }

    .top-menu-tab {
        position: relative;

        ul {
            display: flex;

            li {
                background-color: $header-top-color;

                a {
                    font-weight: 700;
                    padding: 15px 19px 16px;
                    display: -ms-flexbox;
                    display: -webkit-flex;
                    display: flex;
                    -ms-flex-align: center;
                    -webkit-align-items: center;
                    align-items: center;
                    min-height: 60px;
                    color: $header-color;
                    text-transform: uppercase;
                }

                &.active {
                    background-color: $header-color;

                    a {
                        color: $header-top-color;
                    }
                }
            }
        }

        .no-menu-tab {
            display: none;
        }
    }

    .navUser-section--alt {
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 14px;

        &.navUser-section--center {
            justify-content: center;
        }

        &.navUser-section--right {
            justify-content: right;
        }
    }

    .navUser-action {
        padding: 10px 8px;
        color: $header-color;
        font-weight: 400;

        a {
            color: $header-color;
        }

        svg {
            fill: $header-color;
            stroke: $header-color;
        }

        .header-layout-3 & {
            padding: 3px 8px;
            font-size: $fontSize-root - 2;
            font-weight: $fontWeight-bold;
        }
    }

    .navUser-item--text {
        padding-right: 50px;

        .header-layout-2 &,
        .header-layout-3 &,
        .header-layout-4 & {
            padding-right: 0;
        }

        .navUser-action {
            text-transform: unset;

            a {
                background-image: linear-gradient(transparent 95%, $header-color 5%);
                background-repeat: repeat-x;
                background-position-y: -1px;
            }
        }
    }

    @media (max-width: 1024px) {
        .navUser {
            position: relative;
            top: 0;
            transform: unset;
        }

        .top-menu-tab {
            ul {
                li {
                    a {
                        padding: 15px 10px 16px;
                    }
                }
            }
        }

        .navUser-item {
            display: none;

            &.navUser-item--text {
                display: block;
                padding-right: 0;
            }
        }

        .navUser-action {
            padding: 0 10px;
            text-align: center;

        }

        .container-full {
            padding-right: 0;
        }
    }
}

.halo-middleHeader {
    z-index: 11;
    padding: 0;
    margin: 0;
    position: relative;

    .container {
        padding-left: 10px;
        padding-right: 10px;
        width: 100%;
        max-width: 100%;
        display: flex;
        flex-wrap: nowrap;
        align-items: center;
        justify-content: space-between;

        .middleHeader-item {
            display: flex;
            flex-wrap: nowrap;
            align-items: center;
            justify-content: space-between;
            padding: 0;

            &__item {
                display: block;
                width: 100%;

                &:not(.item--logo) {
                    width: auto;
                }

                &.item--account,
                &.item--cart {
                    display: block;
                }

                +.middleHeader-item__item {
                    &:not(.item--logo) {
                        margin-left: 15px;
                        margin-right: 12px;
                    }
                }

                &.item--account {
                    margin-left: 0 !important;
                }
            }

            &.text-center {
                flex: 1;
            }
        }
    }

    .navUser-action {
        padding: 0;
        transition: unset;

        .navUser-icon {
            display: inline-block;
            vertical-align: top;
            position: relative;

            svg {
                width: 25px;
                height: 25px;
                fill: stencilColor('color-textBase');
                margin: 0;
                transition: unset;
            }
        }
    }

    @media (min-width: 1025px) {
        margin-top: -40px;
        display: none;

        .container {
            align-items: flex-end;

            .middleHeader-item {
                flex: 0 0 125px;

                &__item {

                    &.item--account {
                        display: none;
                    }
                }

                &.text-left {
                    opacity: 0;
                    visibility: visible;
                    pointer-events: none;
                }

                &.text-center {
                    flex: 0 0 calc(100% - 250px);
                }

                &.text-right {
                    opacity: 0;
                    visibility: visible;
                    pointer-events: none;
                }
            }
        }

        .navUser-action {
            svg {
                width: 20px;
                height: 20px;
            }
        }
    }
}

.header-layout-2 {
    &.is-sticky {
        .halo-bottomHeader {
            .container.bottomHeader-container {
                padding-top: 0;
                position: unset;

                .bottomHeader-item {
                    display: none;
                }
            }

            .container.nav-container {
                padding-right: 20px;

                @media (min-width: 1400px) {
                    padding-right: 60px;
                }
            }
        }

        .quickSearch {
            .form-input {
                width: 270px;
            }
        }
    }
}

.halo-bottomHeader {
    display: none;
    position: relative;
    z-index: -1;
    opacity: 0;
    visibility: hidden;
    pointer-events: none;

    @media (min-width: 1025px) {
        display: block;
        z-index: unset;
        opacity: 1;
        visibility: visible;
        pointer-events: auto;

        .container {
            display: flex;
            flex-wrap: nowrap;
            align-items: center;
            justify-content: space-between;

            .header-layout-1 & {
                padding: 0 30px;
            }

            .header-layout-4 & {
                max-width: 1550px;
                position: unset;
            }

            &.nav-container {
                max-width: 100%;
                padding-left: 0;
                padding-right: 0;
                display: -ms-flexbox;
                display: flex;
                -ms-flex-wrap: nowrap;
                flex-wrap: nowrap;
                -ms-flex-align: center;
                align-items: center;
                -ms-flex-pack: justify;
                justify-content: space-between;

                >.text-left,
                >.text-right {
                    display: none;
                }

                .is-sticky & {
                    padding-left: 5px;
                    padding-right: 5px;

                    >.text-left,
                    >.text-right {
                        display: block;
                    }

                    >.text-left {
                        padding-right: 20px;
                    }
                }
            }

            &.bottomHeader-container {
                .header-layout-2 & {
                    max-width: 100%;
                    padding-top: 23px;
                    padding-left: 5px;
                    padding-right: 5px;
                }
            }

            .bottomHeader-item {
                display: flex;
                flex-wrap: nowrap;
                align-items: center;
                justify-content: left;
                padding: 0;

                &__item {
                    display: block;
                    width: 100%;

                    &:not(.item--sticky-logo) {
                        padding-left: 35px;

                        .header-layout-3 & {
                            padding-left: 0;
                        }
                    }

                    .header-logo {
                        margin: 0;
                    }
                }

                &.text-right {
                    .navUser {
                        .navUser-section {
                            display: none;
                            vertical-align: middle;
                            float: unset;

                            .header-layout-2 & {
                                display: block;
                            }

                            .header-layout-3 &,
                            .header-layout-4 & {
                                display: inline-block;
                                margin-left: 10px;
                            }
                        }
                    }
                }

                &.text-center {
                    flex: 1;

                    .header-layout-2 & {
                        .navUser-section--link {
                            margin: 0 0 0 35px;

                            .navUser-action {
                                font-weight: $fontWeight-normal;
                                transition: none;

                                .text {
                                    min-width: 100px;
                                    display: block;
                                }

                                span:not(.text) {
                                    background-position: 0 90%;
                                    background-repeat: no-repeat;
                                    background-size: 0 2px;
                                    transition: background-size .5s;
                                    padding: 0 0 5px 0;
                                }

                                @media(min-width: 1025px) {
                                    &:hover,
                                    &:focus-within {
                                        font-weight: $fontWeight-medium;

                                        span:not(.text) {
                                            background-image: linear-gradient(currentColor, currentColor);
                                            background-size: 100% 1px;
                                        }
                                    }
                                }
                            }
                        }

                        .navUser {
                            width: auto;
                            margin-left: 30px;
                        }
                    }

                    .header-layout-3 & {
                        flex: unset;
                    }
                }

                .halo-cart {
                    display: none;
                }
            }
        }

        &.header_logo--center {
            .container {
                .bottomHeader-item {
                    &.text-center {
                        width: 280px;
                    }

                    &.text-left,
                    &.text-right {
                        width: calc((100% - 280px)/ 2);
                    }

                    &.text-right {
                        justify-content: right;

                        .navUser {
                            width: auto;
                        }
                    }
                }
            }
        }

        &:not(.header_logo--center) {
            .bottomHeader-item {

                &.text-left,
                &.text-right {
                    .home-layout-3 & {
                        @media (min-width: 1200px) {
                            min-width: auto;
                        }
                    }
                }

                &.text-right {
                    .home-layout-3 & {
                        .navUser-section {
                            li:last-child .navUser-action {
                                padding-right: 0;
                            }
                        }
                    }
                }
            }
        }

        .navUser-action {
            padding: 10px 4px;
            position: relative;
        }

        .bottomHeader-item {
            &.text-right {
                .navUser-action {
                    padding: 8px 8px;

                    &.navUser-action--currencySelector {
                        padding-left: 10px;
                        padding-right: 10px;
                    }

                    >svg {
                        width: 22px;
                        height: 22px;
                        fill: $color-textBase;
                        stroke: $color-textBase;
                        stroke-width: 2;
                    }

                    .navUser-icon,
                    .navUser-item-cartLabel {
                        svg {
                            width: 22px;
                            height: 22px;
                            fill: $color-textBase;
                            stroke: $color-textBase;
                            stroke-width: 2;
                        }
                    }
                }
            }
        }
    }

    @media (min-width: 1400px) {
        .container {
            &.nav-container {
                padding-left: 40px;
                padding-right: 40px;

                .is-sticky & {
                    padding-left: 45px;
                    padding-right: 45px;
                }
            }

            &.bottomHeader-container {
                .header-layout-2 & {
                    padding-left: 45px;
                    padding-right: 45px;
                }
            }

            .bottomHeader-item {
                &.text-right {
                    .navUser {
                        .navUser-section {
                            .header-layout-4 & {
                                margin-left: 22px;
                            }
                        }
                    }
                }
            }
        }

        .bottomHeader-item {
            &.text-right {
                .navUser-action {
                    padding: 10px 10px;

                    &.navUser-action--currencySelector {
                        padding-left: 20px;
                        padding-right: 20px;
                    }
                }
            }
        }
    }
}

.header-sticky {
    &.is-sticky {
        position: fixed;
        top: 0;
        z-index: 99;
        width: 100%;
        padding: 0;
        animation-duration: 0.5s;
        box-shadow: 0 1px 3px 0px #e5e5e5;

        .halo-topHeader {
            display: none;
        }

        .has-stickyToolbar & {
            &~.body {
                .halo-toolbar.sticky-down {
                    top: 55px;
                }
            }
        }
    }

    @media (min-width: 1025px) {
        &.is-sticky {
            .halo-middleHeader {
                display: none;
            }

            .navPages-list {
                .navPages-item {
                    >.navPages-action {
                        padding-top: 12px;
                        padding-bottom: 12px;

                        .text {
                            padding-top: 0;
                        }
                    }
                }
            }
        }
    }
}

.halo-topHeader,
.halo-bottomHeader {
    .navUser-section--alt {
        .navUser-item {
            svg {
                width: 20px;
                height: 20px;
            }

            .previewCart {
                .previewCartItem-remove {
                    svg {
                        width: 18px;
                        height: 18px;
                    }
                }
            }

            .previewCart {
                .previewCartItem-edit {
                    .icon {
                        width: 16px;
                        height: 16px;
                    }
                }
            }
        }

        @media(max-width: 1024px) {
            +.navUser-section--alt {
                display: none;
            }
        }
    }
}

.stickyNavigation {
    .header-sticky {
        &.sticky-down {
            position: fixed;
            top: 0;
            width: 100%;
            animation-name: fadeInDown;
            animation-duration: .5s;
            animation-fill-mode: both;
            border-bottom: 0;
            box-shadow: 0 0 0 1px rgba(0, 0, 0, .06), 0 1px 2px 0 rgba(0, 0, 0, .1);
            background-color: $header-color;

            .bottomHeader-item {

                .halo-cart {
                    display: block;

                    .countPill {
                        color: $header-color;
                        background-color: $color-textBase;
                    }
                }
            }

            .header-logo__link {
                padding: 10px 0;
            }

            .header-logo-image-container {
                .header-logo-image {
                    object-fit: contain;
                }

                &:after {
                    padding-bottom: 50px;
                }
            }

            .halo-topHeader {
                display: none;
            }

            @media (min-width: 1025px) {
                z-index: 99;

                .halo-middleHeader {
                    display: none;
                }

                .navPages-list {
                    .navPages-item {
                        >.navPages-action {
                            padding-top: 12px;
                            padding-bottom: 12px;

                            .text {
                                padding-top: 0;
                            }
                        }
                    }
                }
            }

            @media (min-width: 1025px) and (max-width: 1280px) {
                .bottomHeader-item {

                    .header-logo-text {
                        font-size: 18px;
                    }
                }
            }

            @media (min-width: 1281px) and (max-width: 1499px) {
                .bottomHeader-item {

                    .header-logo-text {
                        font-size: 22px;
                    }
                }
            }
        }
    }
}

//
// Mobile Menu Toggle
//
// 1. Animate lines from 'x' to hamburger
// -----------------------------------------------------------------------------

.mobileMenu-toggle {
    width: 30px;
    height: 30px;
    font-size: 0;
    overflow: hidden;
    position: relative;
    display: block;
    z-index: 1;
    outline: none;
    box-shadow: none;

    .mobileMenu-toggleIcon,
    .mobileMenu-toggleIcon::before,
    .mobileMenu-toggleIcon::after {
        content: "";
        background: stencilColor('color-textBase');
        display: block;
        height: 2px;
        width: 100%;
        position: absolute;
        transform: rotate(0deg);
        transition: transform 100ms ease-in, top 100ms ease-in 150ms, bottom 100ms ease-in 150ms;
    }

    .mobileMenu-toggleIcon {
        top: 50%;
        width: calc(100%);
        transform: translateY(-50%);
        transition: background-color 10ms ease-in 100ms;
    }

    .mobileMenu-toggleIcon::before {
        top: -10px;
    }

    .mobileMenu-toggleIcon::after {
        bottom: -10px;
    }

    &.is-open {
        .mobileMenu-toggleIcon {
            background-color: transparent;
            transition-delay: 100ms;

            &::before,
            &::after {
                bottom: auto;
                top: auto;
                transition: transform 100ms ease-in 150ms, top 100ms ease-in, bottom 100ms ease-in;
            }

            &::before {
                top: 0;
                transform: rotate(45deg);
            }

            &::after {
                bottom: 0;
                transform: rotate(-45deg);
            }
        }
    }

    @media (min-width: 768px) {
        width: 30px;
        height: 30px;
    }
}

layouts/footer/footer.scss:
// =============================================================================
// FOOTER (CSS)
// =============================================================================


// Layout and columns
// -----------------------------------------------------------------------------
//
// 1. To enable the grid's display: inline-block; to work properly, we need to
//    remove the white-space that it creates between columns with font-size: 0;
// 2. Re-set the font-size that was reduced to 0 in point 1 for children.
//
// -----------------------------------------------------------------------------

.footer {
    background-color: $footer-background;
    border-top: none;
    position: relative;

    .home-layout-3 & {
        .container {
            @media(min-width: 1400px) {
                padding-left: calc((100% - 1370px)/2);
                padding-right: calc((100% - 1370px)/2);
            }
        }
    }
}

.footer-title-sr-only {
    position: absolute;
    left: -10000px;
    top: auto;
    width: 1px;
    height: 1px;
    overflow: hidden;
}

.footer-info {
    margin: 0 -15px;
    max-width: none;
    width: auto;
    font-size: 0; // 1
    padding: 60px 0;

    .home-layout-2 & {
        padding: 55px 0 45px;
    }

    .home-layout-3 & {
        padding: 10px 0;

        @media (min-width: 1025px) {
            padding: 50px 0 30px;
        }

        @media (min-width: 1200px) {
            margin: 0 -5px;
        }
    }

    .home-layout-4 & {
        padding: 65px 0 40px;
    }

    @media(max-width: 1024px) {
        margin: 0;

        .footer-info-col {
            padding-left: 0;
            padding-right: 0;
        }
    }
}

.footer-info-col {
    padding-left: 15px;
    padding-right: 15px;
    color: stencilColor("color-textSecondary");
    display: inline-block;
    float: none;
    font-size: fontSize("base"); // 2
    margin-bottom: spacing("double");
    text-align: left;
    vertical-align: top;
    -ms-word-break: break-all;
    overflow-wrap: break-word;

    @include breakpoint("small") {
        width: width("6/12");
    }

    @include breakpoint("medium") {
        width: width("4/12");
    }

    > :first-child {
        margin-top: 0;
    }

    > :last-child {
        margin-bottom: 0;
    }

    @media (min-width: 1200px) {
        .home-layout-3 & {
            padding-left: 5px;
            padding-right: 5px;
        }
    }
}

.footer-info-col--small {
    @include breakpoint("medium") {
        width: width("2/12");
    }
}

.footer-info-col--social {
    width: 100%;

    +.footer-info-heading {
        margin-top: 22px;
    }
}

.footer-info-col--left {
    @include breakpoint("small") {
        padding: 0;
    }
}

.footer-info-col--right {
    @include breakpoint("small") {
        left: 50%;
        position: inherit;
        width: 50%;
    }

    @include breakpoint("medium") {
        left: 0;
        padding: 0;
        text-align: right;
        width: 100%;
    }
}

.footer-info-col {

    @media(min-width: 1200px) {
        margin-bottom: 2px;

        &[data-section-type="storeInfo"] {
            width: 22.22222222222222%;
        }

        &[data-section-type="footer-categories"],
        &[data-section-type="footer-webPages"],
        &[data-section-type="footer-brands"] {
            width: 16.66666666666667%;
        }

        &[data-section-type="newsletterSubscription"] {
            width: 27.77777777777778%;
            float: right;

            .form-prefixPostfix.wrap {
                -ms-flex-wrap: nowrap;
                flex-wrap: nowrap;

                .home-layout-4 & {
                    @media(max-width: 1599px) {
                        -ms-flex-wrap: wrap;
                        flex-wrap: wrap;

                        .form-input {
                            border-width: 2px 2px 0 2px;
                            text-align: center;
                        }

                        .button {
                            min-width: 100%;
                        }
                    }
                }
            }
        }
    }

    @media(min-width: 768px) {

        &[data-section-type="footer-categories"],
        &[data-section-type="footer-webPages"],
        &[data-section-type="footer-brands"] {
            .footer-info-list {
                display: block !important;
            }
        }
    }

    @media(max-width: 1199px) {
        width: 33.33333333333333%;

        &[data-section-type="newsletterSubscription"] {
            width: 66.66666666666667%;
            max-width: 450px;
        }
    }

    @media(max-width: 767px) {
        width: 100%;

        &[data-section-type="newsletterSubscription"] {
            width: 100%;
            max-width: 100%;
            margin-top: 4rem;
        }

        &[data-section-type="footer-categories"],
        &[data-section-type="footer-webPages"],
        &[data-section-type="footer-brands"] {
            margin-bottom: 0;

            .footer-info-heading {
                border-bottom: 1px solid stencilColor("footer_text_color");
                margin-bottom: 0;
                padding: 15px 0;
                position: relative;

                &:hover {
                    cursor: pointer;
                }

                &:before {
                    top: 50%;
                    right: 5px;
                    z-index: 2;
                    content: "";
                    width: 2px;
                    height: 12px;
                    position: absolute;
                    transition: all .4s ease;
                    transform: translateY(-50%);
                    background-color: stencilColor("footer_text_color");
                }

                &:after {
                    top: 50%;
                    right: 0;
                    z-index: 2;
                    content: "";
                    width: 12px;
                    height: 2px;
                    position: absolute;
                    transform: translateY(-50%);
                    background-color: stencilColor("footer_text_color");
                }
            }

            &.open-dropdown .footer-info-heading:before {
                transform: translateY(-50%) rotate(90deg);
            }

            .footer-info-list {
                display: none;
                margin-bottom: 30px;
                margin-top: 12px;
            }
        }
    }
}

.footer-info {
    &.footer-info-show-all {
        .footer-info-col {
            @media(min-width: 1200px) {

                &[data-section-type="storeInfo"],
                &[data-section-type="footer-categories"],
                &[data-section-type="footer-webPages"],
                &[data-section-type="footer-brands"] {
                    width: 14%;
                }

                &[data-section-type="newsletterSubscription"] {
                    width: 30%;
                }
            }

            @media(max-width: 1199px) {
                &[data-section-type="newsletterSubscription"] {
                    width: 100%;
                    max-width: 100%;
                }
            }
        }
    }

    &.footer-info-no-show-all {
        @media(min-width: 1200px) {
            display: flex;
            justify-content: space-between;
        }
    }
}

.footer-bottom {
    position: relative;
    background-color: stencilColor("footer-bgBottom-color");
    padding: 35px 0 30px;

    .home-layout-2 & {
        border-top: 1px solid #d3d3d3;
        padding: 30px 0 14px;
    }

    .home-layout-3 & {
        padding: 20px 0 30px;

        @media(min-width: 1025px) {
            padding: 27px 0 55px;
        }

        &:before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: 10px;
            width: calc(100% - 20px);
            height: 1px;
            background-color: $color-white;

            @media(min-width: 1280px) {
                left: 30px;
                width: calc(100% - 60px);
            }

            @media(min-width: 1400px) {
                left: calc((100% - 1370px)/2);
                width: calc(100% - (100% - 1370px));
            }
        }

        .footer-copyright {
            a {
                position: relative;

                &:before {
                    content: none;
                    display: block;
                    position: absolute;
                    bottom: -3px;
                    left: 0;
                    width: 100%;
                    height: 1px;
                    background-color: $color-white;
                    opacity: 0.5;
                }

                @media(min-width: 1025px) {
                    color: stencilColor("footer_text_color");

                    &:hover {
                        &:before {
                            content: '';
                        }
                    }
                }
            }
        }
    }

    .home-layout-4 & {
        padding: 27px 0 7px;

        &:before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: 10px;
            width: calc(100% - 20px);
            height: 2px;
            background-color: $color-white;

            @media (min-width: 1280px) {
                left: 30px;
                width: calc(100% - 60px);
            }

            @media (min-width: 1400px) {
                left: 60px;
                width: calc(100% - 120px);
            }

            @media (min-width: 1600px) {
                left: 75px;
                width: calc(100% - 150px);
            }
        }

        .footer-copyright {
            a {
                position: relative;

                &:before {
                    content: '';
                    display: block;
                    position: absolute;
                    bottom: -3px;
                    left: 0;
                    width: 100%;
                    height: 1px;
                    background-color: $color-white;
                    opacity: 0.5;
                }

                @media(min-width: 1025px) {
                    color: stencilColor("footer_text_color");

                    &:hover {
                        &:before {
                            content: none;
                        }
                    }
                }
            }
        }
    }

    .footer-copyright {
        display: inline-block;
    }

    .footer-bottom__container {
        font-size: 0;
    }

    .footer-bottom__left {
        font-size: stencilNumber("fontSize-root");
        width: 50%;
        display: inline-block;
        vertical-align: middle;
        padding-right: 15px;
        text-align: left;
        margin-bottom: 15px;

        .home-layout-2 & {
            font-size: stencilNumber("fontSize-root") - 2;
        }
    }

    .footer-bottom__center {
        font-size: stencilNumber("fontSize-root");
        width: 50%;
        display: inline-block;
        vertical-align: middle;
        padding-right: 15px;
        text-align: center;
        margin-bottom: 15px;
    }

    .footer-bottom__right {
        font-size: stencilNumber("fontSize-root");
        width: 50%;
        display: inline-block;
        vertical-align: middle;
        padding-left: 15px;
        text-align: right;

        .footer-payment-icons {
            margin-top: 0;
        }
    }

    .has-item-center {

        .footer-bottom__left,
        .footer-bottom__center,
        .footer-bottom__right {
            width: 33.33334%;
        }
    }

    @media(max-width: 1199px) {

        .footer-bottom__left,
        .footer-bottom__center,
        .footer-bottom__right {
