// Home banner
// -----------------------------------------------------------------------------
.home-banner-image {
    position: relative;
    overflow: hidden;

    img {
        position: absolute;
        top: 50%;
        left: 50%;
        width: auto;
        height: auto;
        transform: translate3d(-50%, -50%, 0);

        &.lazyload,
        &.lazyloading {
            width: auto;
            height: auto;
            object-fit: contain;
            margin: auto;
        }

        &.lazyloaded {
            width: auto;
            height: auto;
            min-width: 100%;
            transition: all .7s ease;
        }
    }

    .image-mobile {
        @media (max-width: 767px) {
            display: block;

            +img {
                display: none;
            }
        }

        @media(min-width: 768px) {
            display: none;
            width: auto;
        }
    }

    >a {
        background: $color-custom-loading;

        &:after {
            content: '';
            display: block;
            height: 0;
            width: 100%;
        }
    }
}

.home-banner-carousel {
    .slick-arrow {
        border-color: transparent;
        background: transparent;

        &:before {
            height: 30px;
            width: 15px;
        }

        &.slick-next {
            right: 0;

            &:hover {
                &:before {
                    @include carousel-arrow-color($slick-arrow-color, 'next');
                }
            }
        }

        &.slick-prev {
            left: 0;

            &:hover {
                &:before {
                    @include carousel-arrow-color($slick-arrow-color, 'prev');
                }
            }
        }

        @media(min-width: 768px) {
            &:before {
                height: 40px;
                width: 22px;
            }

            &.slick-next {
                right: 40px;
            }

            &.slick-prev {
                left: 40px;
            }
        }
    }

    &:not(.slick-initialized) {
        .homeBanner2__img:nth-child(n+2) {
            display: none;
        }
    }
}

