// Product Banner
// -----------------------------------------------------------------------------
.product-banner-image {
    position: relative;
    overflow: hidden;

    img {
        position: absolute;
        top: 50%;
        left: 50%;
        width: auto;
        height: auto;
        transform: translate3d(-50%, -50%, 0);

        &.lazyload,
        &.lazyloading {
            width: auto;
            height: auto;
            object-fit: contain;
            margin: auto;
        }

        &.lazyloaded {
            width: auto;
            height: auto;
            min-width: auto;
            transition: all .7s ease;

            @media (max-width: 767px) {
                min-width: 100%;
            }
        }
    }

    .image-mobile {
        @media (max-width: 767px) {
            display: block;

            +img {
                display: none;
            }
        }

        @media(min-width: 768px) {
            display: none;
            width: auto;
        }
    }

    a {
        background: $color-custom-loading;

        &:after {
            content: '';
            display: block;
            height: 0;
            width: 100%;
        }
    }
}

#productBanner {
    .productBanner__container {
        width: 100%;
        max-width: 100%;
    }

    .product-banner-image {
        background: $color-custom-loading;
        max-width: 1370px;
        margin: 0 auto;
    }

    a {
        &:after {
            padding-bottom: 27.0073%;

            @media (max-width: 767px) {
                padding-bottom: 91.66667%;
            }
        }
    }
}

// Site Map
// -----------------------------------------------------------------------------
.page-type-sitemap {
    .page-content {
        margin-top: 22px;
    }
}

.sitemap-list {
    margin: 0;

    &--item {
        display: block;
        position: relative;
        margin: 0;
        padding: 0;
        list-style: none;

        +.sitemap-list--item {
            margin-top: 30px;
            padding-top: 25px;
        }
    }

    &--title {
        font-size: $h4-font-size + 2px;
        font-weight: $fontWeight-bold;
        letter-spacing: $letterSpacing-base;
        line-height: 1;
        color: $color-textBase;
        margin: 0 0 20px 0;
        text-transform: capitalize;
        padding-bottom: 10px;
        border-bottom: 1px solid $color-custom-grey;
    }

    &--subItem {
        display: block;

        >a {
            font-size: $fontSize-root + 2px;
            font-weight: $fontWeight-bold;
            line-height: 45px;
            letter-spacing: $letterSpacing-base;
            text-transform: capitalize;
            color: $color-textBase;
            transition: unset;

            span {
                padding: 0 0 1px 0;
            }
        }

        >ul {
            margin-left: 2.4rem;
        }

        li {
            a {
                font-size: $fontSize-root + 2px;
                font-weight: $fontWeight-normal;
                padding: 0;
                margin: 0;
                color: $color-textBase;
                text-transform: capitalize;
                letter-spacing: $letterSpacing-base;
                line-height: 45px;
                transition: unset;

                span {
                    padding: 0 0 1px 0;
                }
            }
        }

        &.sitemap-list--viewAll {
            >a {
                font-style: italic;
                color: $color-textLink;
                text-transform: capitalize;

                span {
                    padding: 0 0 1px 0;
                    background-image: linear-gradient(transparent 97%, $color-textLink 3%);
                    background-repeat: repeat-x;
                    background-position-y: 0;
                }
            }
        }
    }

    @media (min-width: 1025px) {
        li {
            a {
                &:hover {
                    color: $color-textLink-hover;

                    span {
                        background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                        background-repeat: repeat-x;
                        background-position-y: 0;
                    }
                }
            }
        }
    }
}

// Halo Product
// -----------------------------------------------------------------------------
