        }

        @media(min-width: 1400px) {
            padding-top: 60px;
            padding-bottom: 70px;
            padding-left: 75px;
        }

        .halo-block-header {
            text-align: left;

            .content {
                max-width: 100%;
            }

            .title {
                margin-bottom: 18px;
            }

            +.productCarousel {
                .slick-arrow {
                    top: -65px;

                    @media(min-width: 1025px) {
                        top: -95px;
                    }

                    @media(min-width: 1400px) {
                        top: -105px;
                    }
                }
            }

            @media(min-width: 1025px) {
                padding-right: 30px;
            }

            @media(min-width: 1400px) {
                padding-right: 60px;
            }
        }

        .slick-arrow {
            top: -50px;
            transform: none;

            &.slick-prev {
                left: auto;
                right: 75px;

                @media (min-width: 1025px) {
                    right: 115px;
                }

                @media (min-width: 1400px) {
                    right: 160px;
                }
            }

            &.slick-next {
                right: 10px;

                @media (min-width: 1025px) {
                    right: 30px;
                }

                @media (min-width: 1400px) {
                    right: 75px;
                }
            }
        }

        @media(min-width: 1025px) {
            .slick-list {
                padding-right: 60px;
            }
        }

        @media(min-width: 1400px) {
            .slick-list {
                padding-right: 60px;
            }
        }

        @media(min-width: 1600px) {
            .slick-list {
                padding-right: 60px;
            }
        }
    }

    .slick-arrow {
        &.slick-next {
            @media (min-width: 1025px) {
                .home-layout-2 & {
                    right: 30px;
                }
            }

            @media (min-width: 1400px) {
                .home-layout-2 & {
                    right: 50px;
                }
            }
