        }
    }

    .productCarousel {
        .productCarousel-slide {
            width: 20%;
        }

        &:not(.slick-initialized) {
            white-space: nowrap;

            .productCarousel-slide {
                width: 20%;
                display: inline-block;
                white-space: normal;

                &:nth-child(n+6) {
                    display: none;
                }

                @media(max-width: 1399px) {
                    width: 25%;

                    &:nth-child(n+5) {
                        display: none;
                    }
                }

                @media(max-width: 1024px) {
                    width: 33.3334%;

                    &:nth-child(n+4) {
                        display: none;
                    }
                }

                @media(max-width: 767px) {
                    width: 50%;

                    &:nth-child(n+3) {
                        display: none;
                    }
                }

                @media(max-width: 320px) {
                    width: 100%;

                    &:nth-child(n+2) {
