            padding-bottom: 30px;

            .halo-block-header {
                text-align: left;
            }

            +.halo-block-product {
                padding-top: 45px;
            }
        }

        [data-dots-bar="true"] {
            .slick-list {
                padding-right: 210px;
            }
        }
    }

    @media (min-width: 1400px) {
        [data-dots-bar="true"] {
            .slick-list {
                padding-right: 255px;
            }
        }
    }

    @media (min-width: 1600px) {
        [data-dots-bar="true"] {
            .slick-list {
                padding-right: 315px;
            }
        }
    }
}

.productCarousel-tabs {
    .tabs {
        display: block;
        font-size: 0;
        letter-spacing: 0;
        padding: 0;
        border: 0;
        margin: 0;
        list-style: none;
        white-space: nowrap;
        overflow-x: auto;
        text-align: center;

        .tab {
            display: inline-block;
            vertical-align: top;
            margin: 0 0 2px 10px;
            background: 0;
            border: 0;
            float: none;

            &:first-child {
                margin-left: 0;
            }

            &.is-active {
                .tab-title {
                    font-weight: $fontWeight-semibold;
                    color: $color-white;
                    border-color: $color-textBase;
                    background-color: $color-textBase;
                }
            }
        }

        .tab-title {
            color: $color-textBase;
            font-size: $fontSize-root;
            font-weight: $fontWeight-semibold;
            padding: 13px 10px 14px;
            border: 1px solid $color-custom-grey;
            background-color: $color-white;
            border-radius: 3px;
            background: transparent;
            text-transform: capitalize;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            border-radius: 30px;
            min-width: 226px;
            text-align: center;

            @media(min-width: 1025px) {
                &:hover {
                    font-weight: $fontWeight-semibold;
                    color: $color-white;
                    border-color: $color-textBase;
                    background-color: $color-textBase;
                }
            }
        }

        @media (max-width: 767px) {
            margin: 0 -10px;
            padding: 0 10px;

            .tab {
                &:last-child {
                    padding-right: 20px;
                }
            }

            &:before {
                content: '';
                display: block;
                position: absolute;
                top: 0;
                left: 0;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 12px;
                height: 55px;
                z-index: 1;
                transform: rotate(180deg);
            }

            &:after {
                content: '';
                display: block;
                position: absolute;
                top: 0;
                right: 0;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 50px;
                height: 55px;
                z-index: 1;
            }
        }
    }

    .tabs-contents {
        position: relative;
        margin-bottom: 0;

        .tab-content {
            &:not(.is-active) {
                display: none;
            }
        }
    }

    .loadingOverlay {
        &:before {
            top: 100%;
        }
    }

    @media (min-width: 551px) {
        .tabs {
            padding-bottom: 14px;

            .tab {
                width: auto;
                margin-bottom: 0;
            }

            .tab-title {
                padding-left: 25px;
                padding-right: 25px;
                line-height: 1.625;
                white-space: unset;
                overflow: unset;
                text-overflow: unset;
            }
        }
    }
}

.productCarousel-wrapper {
    display: flex;
    flex-direction: column-reverse;
    justify-content: space-between;

    .productCarousel {
        width: auto;
        max-width: inherit;
        min-width: auto;
    }

    .productBanner {
        width: 100%;
        max-width: 100%;
        position: relative;
        margin: 0 0 15px 0;
    }

    .bannerContent {
        overflow: hidden;
        position: relative;
        height: 250px;
    }

    .image {
        display: block;
        width: 100%;
        height: 100%;
        text-align: center;

        img {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            object-fit: cover;
            height: 100%;
            max-height: 100%;
            width: 100%;
            max-width: 100%;

            &.lazyload,
            &.lazyloading {
                width: auto;
                height: auto;
                object-fit: contain;
            }
        }
    }

    .content {
        position: absolute;
        top: 50%;
        left: 50%;
        z-index: 2;
        transform: translate(-50%, -50%);
        width: 100%;
        padding: 0px 35px;

        .sub-heading {
            display: block;
            font-size: $fontSize-root - 2px;
            font-weight: $fontWeight-normal;
            line-height: 28px;
            color: $color-white;
            text-transform: uppercase;
            margin: 0 0 5px 0;
        }

        .heading {
            font-size: 24px;
            font-weight: $fontWeight-normal;
            line-height: 25px;
            color: $color-white;
            margin: 0 0 5px 0;
        }

        .desc {
            display: block;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 1.625;
            color: $color-white;
            margin: 0 0 10px 0;
        }

        .button {
            border-color: $color-white;
            background: $color-white;
            color: #f0bc93;
            min-width: 142px;
        }
    }

    @media (min-width: 768px) {
        flex-direction: unset;

        .productCarousel {
            width: 100% !important;
            max-width: calc(100% - 290px);
        }

        .productBanner {
            max-width: 290px;
            margin-bottom: 0;
        }

        .bannerContent {
            overflow: unset;
            height: auto;
        }

        .image {
            img {
                position: relative;
                top: unset;
                left: unset;
                transform: unset;
                height: auto;
                object-fit: unset;
            }
        }

        .content {
            .heading {
                font-size: 2 * $fontSize-root;
                margin: 0 0 25px 0;
            }

            .desc {
                margin-bottom: 32px;
            }
        }
    }

    @media (min-width: 1025px) {
        .bannerContent {

            &:before,
            &:after {
                position: absolute;
                content: '';
                opacity: 0;
                top: 10px;
                right: 10px;
                bottom: 10px;
                left: 10px;
                z-index: 1;
                transition: opacity 0.35s, transform 0.35s;
            }

            &:before {
                border-top: 1px solid $color-white;
                border-bottom: 1px solid $color-white;
                transform: scale(0, 1);
                transform-origin: 0 0;
            }

            &:after {
                border-right: 1px solid $color-white;
                border-left: 1px solid $color-white;
                transform: scale(1, 0);
                transform-origin: 100% 0;
            }

            &:hover {

                &:before,
                &:after {
                    opacity: 1;
                    transform: scale(1);
                }
            }
        }

        .content {
            .button {
                &:hover {
                    background: transparent;
                    color: $color-white;
                }
            }
        }
    }
}

