                        }
                    }
                }

                &:hover {
                    .image-with-overlay {
                        img {
                            transform: scale3d(1.1, 1.1, 1);
                        }

                        &:before {
                            top: 0;
                        }

                        span {
                            opacity: 1;
                            overflow: visible;
                            visibility: visible;
                            transform: translate(-50%, -50%) scale(1) rotate(0);
                        }
                    }
                }
            }
        }
    }

    @media (min-width: 1025px) {
        padding-top: 0;
        padding-bottom: 50px;
    }
}

