        height: 30px;
        min-width: 140px;
        background-color: rgba(0, 0, 0, 0.7);
        border-radius: 5px;
        opacity: 0;
        visibility: hidden;
        transition: opacity .3s ease-in-out;

        a {
            color: $color-white;
            transition: opacity .3s ease, transform .3s ease;

            &:before {
                content: '';
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                z-index: 1;
            }
        }

        .icon_plus {
            position: relative;
            font-size: 0px;
            width: 11px;
            height: 30px;
            padding: 0 15px;
            transition: transform .3s ease;

            // opacity: 1;
            // overflow: visible;
            // visibility: visible;
            // transform: translate(-50%, -50%) scale(1) rotate(0);

            &:before {
                position: absolute;
                content: "";
                top: 50%;
                left: 50%;
                -moz-transform: translate(-50%, -50%);
                -o-transform: translate(-50%, -50%);
                -ms-transform: translate(-50%, -50%);
                -webkit-transform: translate(-50%, -50%);
                transform: translate(-50%, -50%);
                background: $carousel-title-color;
                width: 2px;
                height: 12px;
            }

            &:after {
                position: absolute;
                content: "";
                top: 50%;
                left: 50%;
                -moz-transform: translate(-50%, -50%);
                -o-transform: translate(-50%, -50%);
                -ms-transform: translate(-50%, -50%);
                -webkit-transform: translate(-50%, -50%);
                transform: translate(-50%, -50%);
                background: $carousel-title-color;
                width: 12px;
                height: 2px;
            }
        }

        .hide_products {
            font-size: 0;
            opacity: 0;
            visibility: hidden;
        }

        &.is-show {
            .icon_plus {
                transform: rotate(45deg);
            }

            .show_products {
                font-size: 0;
                opacity: 0;
                visibility: hidden;
            }

            .hide_products {
                font-size: $fontSize_root - 2;
                opacity: 1;
                visibility: visible;
            }
        }
    }

    @media(min-width: 1025px) {
        padding: 65px 0 40px;
    }
}

.lookbook-popup {
    width: auto;
    max-width: 100%;
    position: fixed;
    right: 0;
    bottom: 0;
    background-color: $color-white;
    z-index: 103;
    opacity: 0;
    visibility: hidden;
    transform: translate3d(0, 100%, 0);
    transition: transform .3s ease, opacity .3s ease, visibility .3s ease;

    .openLookbookPopup & {
        opacity: 1;
        visibility: visible;
        transform: translate3d(0, 0, 0);
    }

    .close {
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        right: 0;
        right: 15px;
        width: 15px;

        svg {
            fill: $color-textBase;
            width: 100%;
            height: 100%;
            vertical-align: top;
        }
    }

    .lookbook-popup-wrapper {
        box-shadow: 0 -1px 4px 0 rgba(32, 33, 36, .28);
    }

    .lookbook-popup-title {
        padding: 12px 40px;
        border-bottom: 1px solid #dfdfdf;
        position: relative;
        text-align: center;

        .title {
            margin: 0 auto;
            max-width: 270px;
        }
    }

    .lookbook-popup-content {
        width: max-content;
        display: flex;
        white-space: nowrap;
        padding: 20px;
        margin: 0 auto;
        overflow-y: auto;

        .productCarousel-slide {
            white-space: normal;
            padding: 0 5px;
            width: 250px;
            max-width: 100%;

            &.is-hidden {
                display: none;
            }

            &.is-active {
                .card-title {
                    a {
                        color: $card-title-color-hover;

                        span {
                            background-size: 100% 1px;
                        }
                    }
                }

                .card-price-wrapper {
                    .icon {
                        opacity: 1;
                        visibility: visible;
                    }
                }
            }

            .card-img-container {
                >a {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    width: auto;
                    height: auto;
                    min-width: 100%;
                    transform: translate3d(-50%, -50%, 0);
                }
            }

            .card-price-wrapper {
                display: flex;

                .icon {
                    display: inline-block;
                    opacity: 0;
                    visibility: hidden;
                    fill: $color-black;
                    width: 22px;
                    height: 25px;
                    margin-left: 8px;
                    transition: opacity .3s ease, transform .3s ease;
                }
            }

            @media (min-width: 1025px) {
                &:hover {
                    .card-title {
                        >a {
                            color: $card-title-color-hover;

                            span:not(.card-name) {
                                background-size: 100% 1px;
                            }
                        }
                    }

                    .icon {
                        opacity: 1;
                        visibility: visible;
                        transform: translate(5px);
                    }
                }
            }
        }
    }

    .no-products {
        svg {
            width: 100px;
            height: 100px;
            fill: rgba(stencilColor('color-primary'), .1);
        }

        .text {
            display: block;
            margin-top: 30px;
        }
    }

    @media(min-width: 1025px) {
        transform: translate3d(100%, 0, 0);
        min-width: 350px;
    }
}

