        text-transform: none;
    }
}

// Form input
// -----------------------------------------------------------------------------

.form-input {
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    -webkit-text-size-adjust: 100%;
    border-radius: 0;
    max-width: 100%;
    min-height: 50px;
    height: 50px;
    line-height: 48px;
    padding: 0 15px;
    margin: 0;
    border-color: $color-greyLighter;
    -webkit-appearance: none;
    -moz-appearance: none;

    &[rows] {
        padding-top: 15px;
        padding-bottom: 15px;
    }

    &::-webkit-input-placeholder {
        color: stencilColor('input-font-color');
        left: 0;
        opacity: 1;
        visibility: visible;
        position: relative;
        text-transform: unset;
        transition: $anchor-transition;
        letter-spacing: $letterSpacing-base;
        font-style: normal !important;
    }

    &:-ms-input-placeholder {
        color: stencilColor('input-font-color');
        left: 0;
        opacity: 1;
        visibility: visible;
        position: relative;
        text-transform: unset;
        transition: $anchor-transition;
        letter-spacing: $letterSpacing-base;
        font-style: normal !important;
    }

    &::placeholder {
        color: stencilColor('input-font-color');
        left: 0;
        opacity: 1;
        visibility: visible;
        position: relative;
        text-transform: unset;
        transition: $anchor-transition;
        letter-spacing: $letterSpacing-base;
        font-style: normal !important;
    }

    &:focus {
        &::-webkit-input-placeholder {
            left: 5%;
            opacity: 0;
            visibility: hidden;
        }

        &:-ms-input-placeholder {
            left: 5%;
            opacity: 0;
            visibility: hidden;
        }

        &::placeholder {
            left: 5%;
            opacity: 0;
            visibility: hidden;
        }
