    .home-layout-3 & {
        font-size: $fontSize-root - 1;
        min-height: 40px;
        height: 40px;
        line-height: 38px;
    }

    .home-layout-2 & {
        border-radius: 4px;
        background-color: $color-custom-greyLightest;
        border-color: $color-custom-greyLightest;
        padding: 0 20px;
    }
