components/foundation/pagination/paginat:
// =============================================================================
// PAGINATION (CSS)
// =============================================================================

.pagination {
    display: block;
    text-align: center;
    margin: 30px 0 0 0;

    .pagination-info {
        display: inline-block;
        vertical-align: top;
        text-align: center;
        margin: 0 0 20px 0;
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        color: #424242;
        letter-spacing: $letterSpacing-base;
        line-height: 24px;
    }

    &.pagination-2 {
        display: block;

        .pagination-info {
            display: block;
            text-align: center;
            margin-bottom: 5px;
        }

        .halo-product-show-more {
            +.pagination-list {
                display: none;
            }
        }
    }

    @media (min-width: 768px) {
        display: flex;
        align-items: center;
        justify-content: space-between;
        text-align: left;

        .pagination-info {
            margin-bottom: 0;
            text-align: left;
        }

        .page-blog & {
            text-align: right;
            justify-content: flex-end;
        }
    }

    @media (min-width: 1025px) {
        margin-top: 50px;

        .page-blog & {
            margin-top: 60px;
        }
    }
}

// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Centred list via inline-block. Font-size: 0; to remove white-space.
// 2. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-list {
    display: block;
    font-size: 0;
    margin: 0;
    text-align: center;
    @include clearfix;

    .pagination-item {
        display: inline-block;
        vertical-align: middle;
        margin: 0 0 0 3px;
    }

    @media (min-width: 768px) {
        display: inline-block;
        vertical-align: top;
    }
}


// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-item {
    display: inline-block;
    vertical-align: middle;
    border: 0;
    margin: 0;

    .pagination-link {
        padding: 0 5px;
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        color: $color-textSecondary;
        min-width: 40px;
        height: 40px;
        line-height: 40px;
        text-align: center;
        text-decoration: none;
        transition: $pagination-link-transition;
        border: 1px solid transparent;
        border-radius: 50%;
        background-color: transparent;

        &:hover {
            color: $pagination-link-color-hover;
        }
    }

    &.pagination-item--current {
        .pagination-link {
            background-color: $color-custom-greyLight;
            border: 1px solid $color-custom-greyLight;
        }
    }

    &.pagination-item--previous,
    &.pagination-item--next {
        .pagination-link {
            padding-top: 0;
            padding-bottom: 0;
            border-color: #e1e1e1;
            background-color: transparent;
        }
    }

    @media (min-width: 1025px) {

        &.pagination-item--previous,
        &.pagination-item--next {
            .pagination-link {
                &:hover {
                    background-color: $buttonStyle-primary-backgroundColorHover;
                    border-color: $buttonStyle-primary-borderColorActive;

                    .icon {
                        fill: $buttonStyle-primary-colorActive;
                    }
                }
            }
        }
    }
}

.pagination-item--previous,
.pagination-item--next {
    .pagination-link {
        width: 40px;
        height: 40px;
        padding: 7px;

        .icon {
            display: inherit;
            margin: 0;
            width: 100%;
            height: 100%;
            fill: #787878;
        }
    }
}

// Simple/small pagination (Next/Previous arrows are the only way to navigate)
// -----------------------------------------------------------------------------

.pagination-list--small {
    position: relative;

    .pagination-item {
        color: $pagination-list--small-color;
        cursor: inherit;
        line-height: $pagination-list--small-lineHeight;
    }

    .pagination-link {
        padding: $pagination-list--small-link-padding;
    }

    .pagination-item--previous,
    .pagination-item--next {
        position: absolute;
        top: 0;
    }

    .pagination-item--previous {
        left: 0;
    }

    .pagination-item--next {
        right: 0;
    }
}

.pagination-total-progress {
    max-width: 255px;
    height: 3px;
    background-color: $color-custom-grey;
    text-align: left;
    margin: 7px auto 20px;
    display: block;

    .pagination-total-item {
        width: 0;
        height: 100%;
        background-color: $color-textLink-hover;
        display: block;
        transition: all ease 1s;
    }
}

components/foundation/breadcrumbs/breadc:
// =============================================================================
// BREADCRUMBS (CSS)
// =============================================================================
//
//
// 1. Hide breadcrumbs on mobile-sized screens to improve screen real estate.
//
// 2. Since our breadcrumbs are centred with a variable width, we need to use
//    display: inline-block; on the breadcrumbs. Hence needing to remove the
//    white-space with font-size: 0;
//    The font-size is reset in the breadcrumb settings file.
//
// -----------------------------------------------------------------------------

.breadcrumb-wrapper {
    padding: 0;
    background-color: transparent;
    text-align: left;

    @media (min-width: 1025px) {
        padding: 0;
    }
}

.breadcrumbs {
    display: inline-block;
    vertical-align: top;
    font-size: 0;
    margin: 0;
    padding: 0;
    border-radius: 0;
    background-color: transparent;

    @media (max-width: 767px) {
        display: block;
        white-space: nowrap;
        overflow: auto hidden;
        margin: 0 -10px;
        padding: 0 10px;

        .breadcrumb {
            &.is-active {
                .breadcrumb-label {
                    padding-right: 20px;
                }
            }
        }

        &:before {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            left: 0;
            background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
            width: 12px;
            height: 40px;
            z-index: 1;
            transform: rotate(180deg);
        }

        &:after {
            content: '';
            display: block;
            position: absolute;
            top: 0;
            right: 0;
            background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
            width: 50px;
            height: 40px;
            z-index: 1;
        }

        .breadcrumb {
            float: none;
        }
    }
}

.breadcrumb {
    display: inline-block;
    vertical-align: top;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    color: #6f6f6f;
    padding: 0;
    line-height: 35px;
    letter-spacing: $letterSpacing-base;
    text-transform: capitalize;

    .breadcrumb-label {
        color: inherit;
        font-size: inherit;
    }

    &:not(:first-child) {
        &:before {
            content: '/';
            font-size: inherit;
            font-weight: inherit;
            display: inline-block;
            vertical-align: initial;
            margin: 0 4px 0 6px;
            color: #6f6f6f;
            position: relative;
            top: 0px;
        }
    }

    &.is-active {
        .breadcrumb-label {
            color: #6f6f6f;
        }
    }
}

.page {
    .breadcrumb {
        color: #6f6f6f;
        padding-top: 2px;
    }
}

components/foundation/tabs/tabs.scss:
// =============================================================================
// TABS (CSS)
// =============================================================================
//
// Most of these styles deliberately reset Citadel's defaults for smaller
// screens since the styles in Stencil for smaller screens are more persistent
// across breakpoints.
//
// -----------------------------------------------------------------------------

.tab {
    border-top: $tab-active-border;
    float: none;

    @include breakpoint("medium") {
        border-top-color: $tab-title-border-color;
        float: left;
    }

    &.is-active {
        border-left-color: $tab-title-border-color;
        border-right-color: $tab-title-border-color;

        @include breakpoint("small") {
            margin-bottom: $tab-title-border-bottom-offset;
        }

        @include breakpoint("medium") {
            border: $tab-active-border;
            border-bottom: $tab-title-border;
        }
    }
}

.tab-title {
    padding: spacing("half") + spacing("quarter") 0;
    text-align: center;
    text-decoration: $tab-title-text-decoration;

    @include breakpoint("small") {
        text-align: left;
    }

    @include breakpoint("medium") {
        padding: spacing("half") spacing("single");
    }

    @include breakpoint("large") {
        padding: remCalc(11px) remCalc(18px);
    }
}

.tab-content {
    //
    // State for when tab-content has js generated of calculated content, like carousel
    //
    // Purpose: The content being display: none, means any js calculated dimensions
    // are incorrect as the elements inside the tab-content have no dimensions to grab.
    // Carousel is a prime example. It needs widths to calculate the layout and slides
    // -----------------------------------------------------------------------------
    &.has-jsContent {
        display: block;
        height: 0;
        overflow: hidden;
        padding: 0;
        visibility: hidden;

        // scss-lint:disable NestingDepth
        &.is-active {
            height: auto;
            overflow: visible;
            padding: $tabs-content-padding;
            visibility: visible;
        }
        // scss-lint:enable NestingDepth
    }


    //
    // Product review displays in tabs
    //
    // Purpose: Display product reviews within tabbed content on product pages.
    // -----------------------------------------------------------------------------
    .productReview {
        @include breakpoint("small") {
            width: grid-calc(6, $total-columns);
        }

        @include breakpoint("medium") {
            width: grid-calc(4, $total-columns);
        }

        @include breakpoint("large") {
            width: grid-calc(6, $total-columns);
        }
    }

    .productReviews {
        border-top: 0;
    }
}

components/foundation/buttons/buttons.sc:
// =============================================================================
// BUTTONS (CSS)
// =============================================================================

.button {
    font-family: $fontFamily-sans;
    font-size: $fontSize-root;
    font-weight: $fontWeight-semibold;
    font-style: normal;
    user-select: none;
    appearance: none;
    width: auto;
    text-decoration: none;
    text-align: center;
    text-transform: uppercase;
    border-radius: 0;
    margin: 0;
    padding: 0 15px;
    height: 50px;
    line-height: 48px;
    letter-spacing: 0.05rem;
    border: 1px solid $buttonStyle-primary-borderColor;
    color: $buttonStyle-primary-color;
    background-color: $buttonStyle-primary-backgroundColor;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    transition: $anchor-transition;

    .home-layout-2 & {
        border-radius: 4px;
        letter-spacing: 0;
    }

    .home-layout-3 & {
        height: 40px;
        line-height: 38px;
        letter-spacing: 0.01rem;
    }

    .home-layout-4 & {
        border-width: 2px;
        letter-spacing: $letterSpacing-base;
        font-weight: $fontWeight-extrabold;
    }

    &.button--icon {
        svg {
            fill: stencilColor("button--icon-svg-color");
        }
    }

    &.button--primary {
        &:active {
            color: $buttonStyle-primary-colorActive;
            border-color: $buttonStyle-primary-borderColorActive;
            background-color: $buttonStyle-primary-backgroundColorActive;
        }
    }

    &.button--secondary {
        color: $buttonStyle-default-color;
        background-color: $buttonStyle-default-backgroundColor;
        border-color: $buttonStyle-default-borderColor;

        &:active {
            color: $buttonStyle-default-colorActive;
            border-color: $buttonStyle-default-borderColorActive;
            background-color: $buttonStyle-default-backgroundColorActive;
        }
    }

    &.button--tertiary {
        color: $buttonStyle-tertiary-color;
        background-color: $buttonStyle-tertiary-backgroundColor;
        border-color: $buttonStyle-tertiary-borderColor;

        &:active {
            color: $buttonStyle-tertiary-colorHover;
            background-color: $buttonStyle-tertiary-backgroundColorHover;
            border-color: $buttonStyle-tertiary-borderColorHover;
        }
    }

    &.disable,
    &[disabled] {
        cursor: not-allowed;
        pointer-events: none;
        background: stencilColor('container-fill-dark');
        border-color: stencilColor('container-fill-dark');
        color: $color-textSecondary;
        outline: 0 !important;

        &:active {
            background: stencilColor('container-fill-dark');
            border-color: stencilColor('container-fill-dark');
            color: $color-textSecondary;
        }
    }

    @media (min-width: 1025px) {
        height: 50px;
        line-height: 48px;
        padding-left: 30px;
        padding-right: 30px;

        &.button--primary {

            &:hover,
            &:focus {
                color: $buttonStyle-primary-colorHover;
                border-color: $buttonStyle-primary-borderColorHover;
                background-color: $buttonStyle-primary-backgroundColorHover;
            }
        }

        &.button--secondary {

            &:hover,
            &:focus {
                color: $buttonStyle-default-colorHover;
                border-color: $buttonStyle-default-borderColorHover;
                background-color: $buttonStyle-default-backgroundColorHover;
            }
        }

        &.button--tertiary {

            &:hover,
            &:focus {
                color: $buttonStyle-tertiary-colorHover;
                background-color: $buttonStyle-tertiary-backgroundColorHover;
                border-color: $buttonStyle-tertiary-borderColorHover;
            }
        }

        &.disable,
        &[disabled] {

            &:hover,
            &:focus {
                background: stencilColor('container-fill-dark');
                border-color: stencilColor('container-fill-dark');
                color: $color-textSecondary;
            }
        }
    }
}

.button--checkout {
    background: stencilColor('button-checkout-bgColor');
    color: stencilColor('button-checkout-color');
    border-color: stencilColor('button-checkout-border-color');

    &:hover,
    &:focus,
    &:active {
        background: stencilColor('button-checkout-bgColor-hover');
        color: stencilColor('button-checkout-color-hover');
        border-color: stencilColor('button-checkout-border-color-hover');
    }
}

components/foundation/accordion/accordio:
// =============================================================================
// ACCORDION (CSS)
// =============================================================================

.accordion {
    box-shadow: none;
    border: 0;
    margin: 0;
}

.accordion-title {
    font-family: $fontFamily-headings;
    font-size: $h4-font-size;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    line-height: 1;
    color: $color-textBase;
    padding: 0;
    margin: 0 0 20px 0;
    text-transform: unset;
}

.accordion-content {
    padding: 0;
    border: 0;
}

.accordion-navigation {
    position: relative;
    text-decoration: none;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    margin: 0;
    padding: 0;
}

// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------

.accordion--navList {
    border: 0;
    border-radius: 0;

    .accordion-block {
        margin: 0 0 32px 0;
        padding: 0 0 10px 0;
        border-bottom: 1px solid #e0e0e0;

        &:last-child {
            border-bottom: 0;
        }
    }

    .accordion-navigation {
        border: 0;
        border-radius: 0;
        margin: 0;
        padding: 0;

        .accordion-navigation-actions {
            position: absolute;
            top: 0;
            right: 0;
            font-size: 0;

            .icon {
                vertical-align: top;
                width: 20px;
                height: 20px;
                stroke: transparent;
                background: transparent;
            }
        }
    }

    .accordion-title {
        padding: 0;
    }

    .accordion-content {
        .navList-item {
            a {
                margin: 0;
                padding: 0 0 0 26px;

                .navList-action-close {
                    position: absolute;
                    float: unset;
                    right: -25px;
                    top: 50%;
                    transform: translateY(-50%);
                    margin: 0;
                    width: 20px;
                    height: 20px;
                    padding: 6px;

                    .icon {
                        width: 100%;
                        height: 100%;
                    }
                }

                &:before {
                    background-color: white;
                    border: 1px solid #d0d0d0;
                    width: 14px;
                    height: 14px;
                    top: 50%;
                    bottom: auto;
                    left: 0;
                    margin: 0;
                    border-radius: 0;
                    transform: translateY(-50%);
                    z-index: 11;
                }

                &:after {
                    width: 12px;
                    height: 14px;
                    background-image: url("data:image/svg+xml,%3Csvg aria-hidden='true' focusable='false' data-prefix='far' data-icon='check' role='img' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' class='svg-inline--fa fa-check fa-w-16 fa-3x'%3E%3Cpath fill='%23ffffff' d='M435.848 83.466L172.804 346.51l-96.652-96.652c-4.686-4.686-12.284-4.686-16.971 0l-28.284 28.284c-4.686 4.686-4.686 12.284 0 16.971l133.421 133.421c4.686 4.686 12.284 4.686 16.971 0l299.813-299.813c4.686-4.686 4.686-12.284 0-16.971l-28.284-28.284c-4.686-4.686-12.284-4.686-16.97 0z' class=''%3E%3C/path%3E%3C/svg%3E");
                    background-repeat: no-repeat;
                    background-position: center center;
                    background-size: 100%;
                    z-index: 12;
                }

                &.is-selected {
                    &:before {
                        border-color: stencilColor('color-textBase');
                        background: stencilColor('color-textBase');
                    }
                }
            }
        }

        &.facetedSearch-content--rating {
            .navList-item {
                +.navList-item {
                    margin-top: 17px;
                }
            }
        }

        &.is-open {
            padding-bottom: 25px;
        }
    }
}

components/foundation/inlineList/inlineL:
// =============================================================================
// INLINE LIST (CSS)
// =============================================================================
// 1. It prevents from missing top outline on refine filters on focus;
//
// -----------------------------------------------------------------------------

ul.inlineList { // 1
    overflow: visible; 
    @include clearfix;
}

.inlineList--labels {
    margin-bottom: 0;
    margin-left: -1 * spacing("half");

    > li {
        margin-bottom: spacing("half");
        margin-left: spacing("half");
    }
}

components/foundation/alerts/alerts.scss:
// =============================================================================
// ALERTS
// =============================================================================

.alertBox {
    border-radius: $alert-radius;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    font-style: normal;
    color: stencilColor("alert-color");
    padding: 12px 15px 13px 15px;
    margin: 0 0 18px 0;

    .icon {
        fill: stencilColor("alert-color");

        svg {
            fill: stencilColor("alert-color");
        }
    }

    &.alertBox--warning {
        color: stencilColor("color-warning");

        .icon {
            fill: stencilColor("color-warning");

            svg {
                fill: stencilColor("color-warning");
            }
        }
    }

    &.alertBox--success {
        color: stencilColor("color-success");

        .icon {
            fill: stencilColor("color-success");

            svg {
                fill: stencilColor("color-success");
            }
        }
    }

    &.alertBox--error {
        color: stencilColor("color-error");

        .icon {
            fill: stencilColor("color-error");

            svg {
                fill: stencilColor("color-error");
            }
        }
    }

    &.alertBox--info {
        color: stencilColor("alert-color-alt");

        .alertBox-close {
            svg {
                fill: stencilColor("alert-color-alt");
            }
        }

        .icon {
            fill: stencilColor("alert-color-alt");

            svg {
                fill: stencilColor("alert-color-alt");
            }
        }
    }
}

.alertBox-icon {
    padding: 0 10px 0 0;

    .icon {
        @include square(25px);
    }
}

@mixin lazy-loaded-img() {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
}

@mixin lazy-loaded-padding($size-param) {
    &:after {
        content: '';
        display: block;
        height: 0;
        width: 100%;
        padding-bottom: get-padding(stencilString($size-param));
    }
}

@mixin cart-item-lazy-load-img-placeholder() {
    &:after {
        content: '';
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        height: 100%;
        width: 100%;
    }
}

components/stencil/ribbon/ribbon.scss:
// =============================================================================
// RIBBON (CSS)
// =============================================================================
//
// 1. A ribbon needs a container set to relative in order to position itself.
// 2. Top right text badge. Shows "On sale" as an example.
//
// -----------------------------------------------------------------------------

.has-ribbon { // 1
    position: relative;
}

.ribbon { // 2
    background-color: $ribbon-backgroundColor;
    color: $ribbon-color;
    cursor: $ribbon-cursor;
    font-weight: $ribbon-fontWeight;
    margin: $ribbon-margin;
    padding: $ribbon-padding;
    position: absolute;
    right: $ribbon-positionRight;
    text-transform: $ribbon-textTransform;
    top: $ribbon-positionTop;
    z-index: 1;
}

components/citadel/loading/loading.scss:
// =============================================================================
// LOADING (CSS)
// =============================================================================
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------

.loadingOverlay {
    background-color: rgba(stencilColor("loadingOverlay-backgroundColor"), 0.9);
    display: none;
    z-index: zIndex("low");

    &:before{
        content: '';
        background: url(../img/loading.svg) no-repeat;
        background-size: contain;
        position: absolute;
        width: 50px;
        height: 50px;
        top: 50%;
        left: 0;
        right: 0;
        border: 0;
        border-radius: 0;
        display: block;
        margin: 0 auto;
        transform: translateY(-50%) rotate(0);
        transform-style: preserve-3d;
        animation: unset;

        .home-layout-2 & {
            background: url(../img/loading_2.svg) no-repeat;
        }
    }
}

components/citadel/forms/forms.scss:
// =============================================================================
// FORMS (CSS)
// =============================================================================


// Form variants
// -----------------------------------------------------------------------------

.form--hiddenLabels {

    .form-label {
        @include u-hidden("visually");
    }
}


// Citadel inline messages
// -----------------------------------------------------------------------------

.form-inlineMessage {
    display: block;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    line-height: 1.75;
}


// Citadel field states
// -----------------------------------------------------------------------------

.form-field--error {

    .form-input {
        float: none;
    }

    .form-inlineMessage {

        // scss-lint:disable NestingDepth
        &:before {
            background: $form-error-icon no-repeat;
            background-size: $form-inlineMessage-backgroundSize;
            content: "";
            display: inline-block;
            height: $form-error-icon-height;
            left: $form-error-icon-left;
            margin-right: $form-error-icon-marginRight;
            position: relative;
            top: $form-error-icon-top;
            width: $form-error-icon-width;
        }
    }
}

.form-field--success {

    .form-input[type="text"] {
        background: {
            image: $form-success-icon;
            position: right $form-success-icon-right center;
            repeat: no-repeat;
        }

        padding-right: $form-success-input-paddingRight;
    }
}


// Citadel checkbox/radio positioning
// -----------------------------------------------------------------------------

.form-checkbox,
.form-radio {
    display: none;

    +.form-label {

        // scss-lint:disable NestingDepth
        &:last-child {
            display: block;
            margin-bottom: $form-checkRadio-label-last-marginBottom;
        }

        &::after {
            top: $form-checkRadio-labelAfter-top;
        }

        &::before {
            top: $form-checkRadio-labelBefore-top;
            width: 16px;
            height: 16px;
        }
    }
}

.form-checkbox {
    +.form-label {
        padding-left: 25px;

        &:before {
            top: 3px;
            border-radius: 0;
            border-color: #d0d0d0;
            width: 15px;
            height: 15px;
        }

        &:after {
            left: 0;
            top: 3px;
            width: 15px;
            height: 15px;
            background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'%3E%3Cpath d='M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z' fill='rgba(255, 255, 255, 1)' /%3E%3C/svg%3E");
            background-size: cover;
            background-position: center;
        }

        +.form-inlineMessage {
            margin-top: 0;
            margin-bottom: 10px;
        }
    }

    &:checked {
        +.form-label {
            &:before {
                background: stencilColor('color-textBase');
                border-color: stencilColor('color-textBase') !important;
            }
        }
    }
}


// Citadel form-actions
// -----------------------------------------------------------------------------

.form-actions {
    @include grid-row();
    display: block;
    text-align: center;

    .button,
    input,
    a {
        vertical-align: top;
        width: 100%;

        +.button,
        +input,
        +a {
            margin-top: spacing("half");
        }
    }
}

// Stencil form additions
// -----------------------------------------------------------------------------
//
// 1. Order has been placed here so that if a form error is shown, it will
//    appear before the button.
//
// -----------------------------------------------------------------------------

.form-label--alternate {
    font-family: fontFamily("headingSans");
    font-size: fontSize("smallest");
    margin-bottom: spacing("half");
}

.form-select--date {
    width: initial;

    +.form-select--date {
        margin-left: 15px;
    }
}

.form-select--small,
.form-select--date {
    background-position: right spacing("third") top spacing("third");
    background-size: remCalc(18px);
    font-size: $input-small-fontSize;
    height: $input-small-height;
    line-height: 1rem;
    max-width: 20rem;
    padding: $input-small-padding;
    padding-right: $select-small-paddingRight;
}

.form-select--short {
    max-width: 6rem;
    width: 4.5rem;
}

.form-file {
    font-size: $fontSize-root;
    margin-bottom: spacing("third");

    +label {
        input[type="checkbox"] {
            position: relative;
            top: 2px;
        }
    }
}

.form-fileDescription {
    color: $color-textBase;
    font-size: fontSize("smallest") + 2px;

    strong {
        font-weight: $fontWeight-medium;
    }
}

.form-field-group {
    border: $input-border-width $input-border-style $input-border-color;
    border-radius: $input-border-radius;
    padding: spacing("single");
}

// This component should live inside a .form-row
.form-column {
    @include grid-column(12);

    @include breakpoint("small") {
        @include grid-column(6);
    }
}

.form-prefixPostfix {

    &.wrap {
        flex-wrap: wrap;
    }

    .form-input,
    .button {
        display: block;
        width: 100%;
    }

    .form-input {

        .home-layout-3 & {
            background-color: #e3e3e3;
        }

        @media (min-width: 551px) {
            width: auto;
        }
    }

    .button {
        text-transform: uppercase;
        height: 50px;
        margin-top: 20px;

        @media (min-width: 551px) {
            width: auto;
            margin-top: 0;
            margin-left: 20px;
        }

        .home-layout-3 & {
            height: 40px;
            line-height: 38px;
        }
    }

    .form-inlineMessage {
        margin-bottom: spacing("quarter");

        @include breakpoint("large") {
            margin-bottom: 0;
        }
    }
}

.form-prefixPostfix-button--postfix {
    margin: spacing("half") 0 0;
    order: 1; // 1

    @include breakpoint("large") {
        margin: 0 0 0 spacing("half");
        order: 0; // 1
    }
}


// Increment field
// -----------------------------------------------------------------------------

.form-increment {
    position: relative;
    max-width: 122px;
    min-height: 50px;

    .button {
        margin: 0;
        padding: 0;
        border: none;
        width: 32px;
        height: 100%;
        position: absolute;
        background: 0 0;

        &[data-action=dec] {
            bottom: 0;
            left: 0;

            &:before,
            &:after {
                content: "";
                position: absolute;
                display: block;
                line-height: 22px;
                background-color: $color-custom-greyDarker;
            }

            &:before {
                top: 50%;
                left: 10px;
                transform: translateY(-50%);
                width: 12px;
                height: 2px;
            }
        }

        &[data-action=inc] {
            bottom: 0;
            right: 0;

            &:before,
            &:after {
                content: "";
                position: absolute;
                display: block;
                line-height: 22px;
                background-color: $color-custom-greyDarker;
            }

            &:before {
                top: 50%;
                right: 10px;
                transform: translateY(-50%);
                width: 12px;
                height: 2px;
            }

            &:after {
                top: 50%;
                right: 15px;
                width: 2px;
                height: 12px;
                transform: translateY(-50%);
            }
        }
    }
}

.form-input--incrementTotal {
    border: 0;
    display: inline-block;
    font-size: fontSize("smaller");
    font-weight: fontWeight("bold");
    height: auto;
    padding: 0;
    text-align: center;
    vertical-align: middle;
    width: remCalc(35px);
    -moz-appearance: textfield;

    &::-webkit-outer-spin-button,
    &::-webkit-inner-spin-button {
        -webkit-appearance: none;
        margin: 0;
    }
}


// Stencil form options
// -----------------------------------------------------------------------------
//
// 1. Issue with FastClick JS library workaround: https://github.com/ftlabs/fastclick/issues/351
// 2. Resetting font due to inline-block whitespace issue.
// 3. Added for STENCIL-3384. Did not change directly on .form-option-variant to avoid 1.
// 4. Added for STRF-4031. Breaks 3 for small screens.
//

.form-option {
    border: 1px solid stencilColor("input-border-color");
    color: stencilColor("form-label-font-color");
    cursor: pointer;
    display: inline-block;
    font-size: 0; // 2
    margin-bottom: spacing("third");
    margin-right: spacing("third");
    padding: 1px;
    transition: all 0.15s ease;

    &:hover {
        border-color: stencilColor("input-border-color-active");
    }

    .form-radio:checked+& {
        border-color: stencilColor("checkRadio-color");
        box-shadow: 0 0 0 1px stencilColor("checkRadio-color");
    }
}

.form-option-variant {
    display: inline-block;
    font-size: $body-fontSize; // 2
    min-width: remCalc(38px);
    padding: 1px spacing("quarter");
    pointer-events: none; // 1
    text-align: center;
}

.form-option-variant--color,
.form-option-variant--pattern {
    @include square(22);
    min-width: initial;
    padding: 0;

    @include breakpoint('small') {
        // 4
        pointer-events: inherit; // 3
    }
}

.form-option-variant--pattern {
    background-position: center;
    background-repeat: no-repeat;
}


// Form input (small)
// -----------------------------------------------------------------------------
.form-input--small {
    font-size: $input-small-fontSize + 4px;
    height: $input-small-height;
    line-height: 1rem;
    padding: $input-small-padding;
}


// Form min/max input row
// -----------------------------------------------------------------------------
.form-minMaxRow {
    @include clearfix();
    margin-left: -$form-minMaxRow-column-gutter;
    margin-right: -$form-minMaxRow-column-gutter;

    .form-field {
        @include grid-column(4);
        margin-bottom: 0;
        padding-left: $form-minMaxRow-column-gutter;
        padding-right: $form-minMaxRow-column-gutter;
    }

    .button {
        margin-bottom: 0;
        padding-left: 0;
        padding-right: 0;
        width: 100%;
    }

    .form-input {
        padding: 0 10px;
    }
}


// Form row modifications
// -----------------------------------------------------------------------------
//
// 1. If there are more than 2 fields per form-row--half, clear the left of every
// odd one.
//
// -----------------------------------------------------------------------------

.form-row--half>.form-field:nth-child(odd) {
    @include breakpoint("small") {
        clear: left;
    }
}

[data-product-attribute="set-radio"],
[data-product-attribute="product-list"] {
    .unavailable {
        opacity: $productOptions-disable-opacity * 2;
        text-decoration: line-through;
    }
}

.form-field--success .form-input,
.form-field--success .form-select,
.form-field--success .form-checkbox+.form-label::before,
.form-field--success .form-radio+.form-label::before,
.form-field--success .form-prefixPostfix-label {
    border-color: stencilColor('color-success');
}

input[type=number]::-webkit-inner-spin-button,
input[type=number]::-webkit-outer-spin-button {
    opacity: 1;
}

components/citadel/actionBar/actionBar.s:
// =============================================================================
// ACTION BAR (CSS)
// =============================================================================


.actionBar-section {

    .form-field {
        border: $actionBar-form-field-border;
        border-radius: $actionBar-form-field-radius;

        // scss-lint:disable NestingDepth
        &:hover {
            border-color: stencilColor("input-border-color-active");
        }
        // scss-lint:enable NestingDepth
    }

    .form-label {
        color: $actionBar-form-label-color;
        font-size: $actionBar-form-label-fontSize;
        line-height: $actionBar-form-label-lineHeight;
        margin-bottom: 0;
        padding-left: $actionBar-form-label-padding;
    }

    .form-input,
    .form-select {
        border: 0;
        padding-left: $actionBar-form-input-padding;
        width: auto;
    }

    .form-label,
    .form-input,
    .form-select {
        display: inline-block;
    }

    .form-select {
        color: stencilColor("color-textLink");
        height: $input-small-height - remCalc($input-border-width * 2);
    }
}

components/citadel/cards/cards.scss:
// =============================================================================
// CARDS (CSS)
// =============================================================================
//
// 1. Allow users to click the anchor tag on the image in the figcaption empty
//    space. This provides a much larger and simpler hit state for navigating
//    to the product details page. (Does not work in IE10 or earlier).
// 2. Using translate3d to counter a chrome specific bug: When using a masonry
//    styled layout via CSS column-count etc, transforms applied to descendants
//    are made invisible, except for 3d transforms...
//
// -----------------------------------------------------------------------------

.card {
    display: block;
    position: relative;
    margin: 0;
    padding: 0;

    .button {
        pointer-events: all;
    }

    .card-figcaption-top {
        position: absolute;
        top: 10px;
        right: -10px;
        text-align: right;
        transition: right .5s ease, opacity .3s ease;
        opacity: 0;
        visibility: hidden;

        .home-layout-2 & {
            top: 15px;
            right: -15px;
        }

        .button {
            border: none;
            font-size: 0;
            padding: 0;
            margin-right: 0;
            display: inline-block;
            background-color: transparent;
            min-width: 0;
            height: 36px;
            line-height: 26px;

            &.wishlist {
                display: block;
            }

            svg {
                width: 18px;
                height: 18px;
                display: inline-block;
                vertical-align: middle;

                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);

            }

            .text {
                display: inline-block;
                vertical-align: top;
                padding: 6px 10px 5px;
                border-radius: 4px;
                background-color: $color-white;
                margin: 5px 10px 5px 5px;
                color: $color-textBase;
                font-size: 12px;
                font-weight: 400;
                line-height: 16px;
                text-transform: uppercase;
                opacity: 0;
                visibility: hidden;
                -webkit-transition: all 0.3s ease;
                -moz-transition: all 0.3s ease;
                -ms-transition: all 0.3s ease;
                -o-transition: all 0.3s ease;
                transition: all 0.3s ease;
                -webkit-transform: translateX(-10px);
                -moz-transform: translateX(-10px);
                -ms-transform: translateX(-10px);
                -o-transform: translateX(-10px);
                transform: translateX(-10px);
                -webkit-box-shadow: 0px 1px 3px 0px rgba(89, 89, 89, .5);
                box-shadow: 0px 1px 3px 0px rgba(89, 89, 89, .5);
            }

            .icon-wrapper {
                background-color: $color-white;
                width: 36px;
                height: 36px;
                border-radius: 50%;
                display: inline-block;
                position: relative;
            }

            @media(max-width: 1024px) {

                &.wishlist,
                &.quickview {
                    display: none;

                    &.show-mobile {
                        display: inline-block;
                    }
                }

                &.wishlist:not(.show-mobile) {
                    +.quickview {
                        margin-top: 0;
                    }
                }
            }

            @media(min-width: 1025px) {

                &:hover,
                &:focus,
                &:active {

                    .text {
                        opacity: 1;
                        visibility: visible;
                        -webkit-transform: translateX(0);
                        -moz-transform: translateX(0);
                        -ms-transform: translateX(0);
                        -o-transform: translateX(0);
                        transform: translateX(0);
                    }
                }
            }
        }

        .wishlist+.quickview {
            margin-top: 10px;
            margin-left: 0;
        }

        @media(max-width: 1024px) {
            top: 5px;
            right: 5px;
            opacity: 1;
            visibility: visible;

            .wishlist+.quickview {
                margin-top: 5px;
            }
        }
    }

    .card-figcaption-bottom {
        position: absolute;
        left: 0;
        right: 0;
        bottom: -10px;
        padding-top: 15px;
        transition: bottom .3s ease, opacity .3s ease;
        opacity: 0;
        visibility: hidden;

        @media(min-width: 1025px) {
            background-color: $color-white;
        }

        .home-layout-2 & {
            padding-top: 20px;
            padding-bottom: 20px;

            .card-figcaption-button {
                font-weight: $fontWeight-medium;
                text-transform: capitalize;
            }
        }

        .home-layout-3 & {
            background-color: transparent;

            @media(min-width: 1025px) {
                .card-figcaption-button {
                    font-size: $fontSize-root + 2;
                    font-weight: $fontWeight-normal;
                    border: none;
                    background-color: transparent;
                    text-decoration: underline;
                    text-underline-offset: 2px;
                    text-decoration-thickness: 2px;
                }
            }

            .card-option {
                background-color: $color-white;
                min-height: auto;

                .form-field {
                    text-align: center;
                    min-height: 70px;

                    .form-option {
                        pointer-events: all;

                        .form-option-variant {
                            border: 1px solid $color-white;

                            &:after {
                                border: 4px solid $color-white;
                            }
                        }

                        &.active {
                            .form-option-variant {
                                border-color: $color-black;
                            }
                        }
                    }
                }
            }
        }

        .home-layout-4 & {
            padding-top: 20px;

            @media(min-width: 1025px) {
                padding-top: 0;
            }
        }

        .card-figcaption-button {
            margin: 0;
        }
    }

    &:focus-within {
        .card-figcaption {
            opacity: 1;
        }
    }

    &.focus-within {
        .card-figcaption {
            opacity: 1;
        }
    }

    &.card-custom {
        display: table;
        margin-bottom: 22px;

        .card-link {
            display: table-cell;
            vertical-align: top;
            position: relative;
            overflow: hidden;
            z-index: 1;
            width: 80px;
        }

        .card-img-container {
            @include lazy-loaded-padding('product_sidebar_size');
        }

        .card-content {
            display: table-cell;
            vertical-align: top;
            width: calc(100% - 80px);
            padding: 0 0 0 15px;
        }

        .card-title {
            position: relative;
            top: -6px;
            margin-top: 0;

            >a {
                margin-bottom: 0;
                line-height: 24px;
            }

            .card-ellipsis {
                -webkit-line-clamp: stencilString('halo_card_title-line');
            }
        }

        .card-price {
            justify-content: flex-start;
        }

        .card-option {
            .form-field {
                margin-bottom: 5px;
                text-align: left;
            }

            .home-layout-2 & {
                margin-top: 10px;
                margin-bottom: 5px;

                .form-field {
                    min-height: auto;

                    .form-option {
                        margin: 0 4px 0 0;

                        .form-option-variant {
                            width: 20px;
                            height: 20px;

                            &:after {
                                width: 10px;
                                height: 10px;
                            }
                        }
                    }

                    .showmore {
                        line-height: 20px;
                    }
                }
            }
        }

        .card-action {
            background: transparent !important;
            border: 0;
            font-size: $fontSize-root - 2px;
            line-height: 24px;
            width: 100%;
            color: $color-textLink;
            text-align: left;
            transition: unset;
            font-weight: $fontWeight-normal;

            span {
                position: relative;
                font-size: 16px;
                text-transform: capitalize;
                background-image: linear-gradient(transparent 97%, $color-textLink 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    background-position-y: 0;
                }
            }

            @media(min-width: 1025px) {
                &:hover {
                    color: $color-textLink-hover;

                    span {
                        background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                    }
                }
            }
        }
    }

    &.card-custom-2 {
        margin-bottom: 38px;

        .card-link {
            width: 85px;

            @media (min-width: 551px) {
                width: 100px;
            }
        }

        .card-option {
            .form-field {
                margin-bottom: 19px;
            }
        }

        .card-action {
            display: inline-block;
            vertical-align: top;
            min-width: 120px;
            width: auto;
            margin-left: 0;
            height: 50px;
            line-height: 50px;
            text-align: center;
            border-color: $buttonStyle-default-borderColor;
            color: $buttonStyle-default-borderColor;
            outline: 1px solid !important;
            outline-color: rgba(stencilColor('button--default-borderColor'), 0.5) !important;
            outline-offset: 0px !important;
            transition: all .7s cubic-bezier(0.19, 1, 0.22, 1);

            span {
                padding-bottom: 0;

                &:before,
                &:after {
                    background-color: $buttonStyle-default-borderColor;
                }
            }
        }
    }

    &.card-quick-search {
        .card-link {
            .card-img-container {
                @include lazy-loaded-padding('product_quicksearch_size');
            }
        }

        .card-body-button {
            position: relative;
            top: unset;
            left: unset;
            right: unset;
            bottom: unset;
            opacity: 1;
            visibility: visible;
            height: auto;
            line-height: 1;
            margin: 0;
        }

        .card-action {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            border: 0;
            background-color: transparent;
            color: $color-textLink;
            padding: 0;

            span {
                position: relative;
                padding-bottom: 1px;
                color: $color-textBase;
            }
        }

        .card-option {
            min-height: auto;
        }
    }

    &.card-lookbook {
        max-width: get-width(stencilString('product_lookbook_size'));
        background: $color-white;

        .card-link {
            display: block;

            .card-img-container {
                @include lazy-loaded-padding('product_lookbook_size');
            }
        }

        .card-body {
            padding: 5px 20px 20px 20px;
            text-align: left;
        }

        .card-title {
            >a {
                font-style: normal;
                color: stencilColor('card-title-color');
            }

            .card-ellipsis {
                -webkit-line-clamp: stencilString('halo_card_title-line');
            }
        }

        .card-body-button {
            position: relative;
            top: unset;
            left: unset;
            right: unset;
            bottom: unset;
            opacity: 1;
            visibility: visible;
            height: auto;
            line-height: 1;
            margin: 10px 0 0 0;
            text-align: left;
        }

        .card-action {
            font-size: $fontSize-root - 2px;
            font-style: normal;
            font-weight: $fontWeight-medium;
            line-height: 26px;
            border: 0;
            background-color: transparent;
            color: $color-textLink;
            padding: 0;
            text-align: center;
            text-transform: uppercase;

            span {
                position: relative;
                padding-bottom: 1px;
                background-image: linear-gradient(transparent 97%, $color-textLink 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    background-position-y: 0;
                }
            }
        }

        @media (max-width: 767px) {
            padding: 10px;
            width: 100%;
            max-width: 350px;
            border-radius: 2px;
            background-color: $color-white;
            box-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
            display: inline-block;

            .card-figure {
                width: 60px;
                float: left;

                .halo-badge {
                    padding: 0 5px;
                }
            }

            .card-body {
                float: left;
                width: calc(100% - 60px);
                padding: 0 10px;
                margin-top: -2px;
                text-align: left;

                .card-title {
                    .color-name {
                        margin-bottom: 5px;
                        text-align: left;
                    }
                }

                .card-price {
                    display: block;
                }

                .card-body-button {
                    text-align: left;
                    margin: 10px 0 0 0;
                }
            }
        }

        @media (max-width: 550px) {
            max-width: 300px;
        }
    }

    @media (min-width: 1025px) {
        &:hover {
            .card-figcaption {
                z-index: 4;
                opacity: 1;
            }

            .card-figcaption-top {
                right: 10px;
                opacity: 1;
                visibility: visible;

                .home-layout-2 & {
                    right: 15px;
                }
            }

            .card-figcaption-bottom {
                bottom: 0;
                opacity: 1;
                visibility: visible;
            }

            .card-compare {
                opacity: 1;
                z-index: 1;
                overflow: visible;
                display: inline-block;
            }
        }

        &.card-hover {
            &:hover {
                .card-link {
                    .card-image {
                        opacity: 0;
                        backface-visibility: visible;
                    }

                    .card-image-2 {
                        opacity: 1;
                        backface-visibility: visible;
                        z-index: 1;

                        img {
                            transform: scale3d(1.1, 1.1, 1);
                        }
                    }
                }
            }
        }

        &:not(.card-hover) {
            .card-image {
                transition: opacity .8s ease, transform 2s cubic-bezier(0, 0, .44, 1.18) 0s;
            }

            &:hover {
                .card-image {
                    transform: scale3d(1.1, 1.1, 1);
                }
            }
        }
    }
}

.card-figure {
    display: block;
    overflow: hidden;
    position: relative;
    z-index: 1;
    margin: 0;
}

.card-image {
    border: 0;
}

.card-img-container {
    max-width: get-width(stencilString('productgallery_size'));

    @include lazy-loaded-padding('productgallery_size');
}

.card-figcaption {
    display: block;
    margin: $card-figcaption-margin;
    pointer-events: none; // 1
    opacity: 1;
}

.card-figcaption-body {
    display: $card-figcaption-body-display;
    pointer-events: all; // 1
    transform: translate3d(0, -100%, 0);
    top: 100%;
    width: 100%;

    @media (min-width: 1025px) {
        display: $card-figcaption-body-display;
        pointer-events: all; // 1
        transform: translate3d(0, -50%, 0); // 2
        top: 50%;
        width: auto;
    }
}

.card-image {
    border: 0;
}

.card-title {
    padding: $card-title-margin;
    margin: 0;
    overflow-wrap: break-word;

    >a {
        display: block;
        color: $card-title-color;
        font-size: $card-title-fontSize;
        font-weight: $fontWeight-normal;
        line-height: 26px;
        letter-spacing: $letterSpacing-base;
        padding: 0 0 5px 0;
        text-transform: capitalize;
        text-decoration: $card-title-textDecoration;

        span {
            background-image: linear-gradient(currentColor, currentColor);
            background-position: 0 90%;
            background-repeat: no-repeat;
            background-size: 0 1px;
            transition: background-size .5s;
        }

        .home-layout-2 & {
            padding: 0 0 2px 0;

            span:not(.card-name) {
                background-position: 0 98%;
            }
        }

        .home-layout-3 &,
        .home-layout-4 & {
            font-size: $card-title-fontSize + 2;
        }

        // scss-lint:disable NestingDepth
        @media(min-width: 1025px) {
            &:hover {
                color: $card-title-color-hover;

                span {
                    background-size: 100% 1px;
                }
            }
        }
    }

    .card-ellipsis {
        overflow: hidden;
        display: -webkit-box;
        -webkit-line-clamp: stencilString('halo_card_title-line');
        text-overflow: ellipsis;
        -webkit-box-orient: vertical;
        /* autoprefixer: off */
    }

    .home-layout-2 & {
        padding: 0;
    }
}

.card-desc,
.card-rating-category {
    display: none;
}

.card-text {
    margin: 0;

    &[data-test-info-type="brandName"] {
        font-size: $fontSize-root;
        font-weight: $fontWeight-bold;
        color: $card-title-color;
        letter-spacing: $letterSpacing-base;
        line-height: 26px;
        text-transform: uppercase;

        +.card-title {
            margin-top: 0;
        }
    }
}

.card-figcaption-button {
    display: $card-figcaption-button-display;
    margin: $card-figcaption-button-margin;
    min-width: 160px;
    height: 45px;
    line-height: 42px;

    .home-layout-2 &,
    .home-layout-4 & {
        height: 50px;
        line-height: 48px;
    }

    &:active {
        background-color: $card-figcaption-button-background;
    }

    .card-figcaption-body & {
        &:focus {
            background-color: $card-figcaption-button-background;
            outline: revert;
        }
    }

    +.card-figcaption-button {
        margin: 0;

        @media (min-width: 1025px) {
            margin: $card-figcaption-button-margin;
        }
    }

    @media (max-width: 1025px) {
        margin: 0;
        min-width: 100%;

        &.quickview {
            display: none;
            font-size: 14px;
            font-weight: 400;
            line-height: 24px;
            letter-spacing: 0;
            color: $color-textBase;
            text-align: center;
            background-color: rgba(255, 255, 255, .8);
            text-transform: capitalize;
            border-radius: 0;
            width: 100%;
            padding: 3px 10px;
            transition: unset;
            height: 30px;

            &.card-quickview-mobile {
                display: block;
            }
        }
    }
}

.card-body {
    text-align: left;
    padding-top: 15px;

    .home-layout-3 & {
        text-align: center;
        padding-top: 12px;

        [data-test-info-type="brandName"] {
            font-weight: 300;
            text-transform: capitalize;
        }

        .card-wrapper {
            display: block;
        }

        .card-price {
            justify-content: center;
        }
    }

    .home-layout-4 & {
        padding-top: 18px;

        [data-test-info-type="brandName"] {
            font-size: $fontSize-root + 4;
            font-family: $fontFamily-headings;
            font-weight: $fontWeight-normal;
            text-transform: capitalize;
        }
    }

    .card-desc {
        display: none;
    }

    .card-wrapper {
        display: flex;
        align-items: center;
        justify-content: space-between;

        @media(max-width: 1024px) {
            flex-direction: column;

            .card-price,
            .card-text {
                width: 100%;
            }

            .card-text {
                order: -1;
            }
        }

        .home-layout-2 & {
            flex-direction: column;
            align-items: flex-start;

            .card-text {
                order: -1;
                margin: 0 0 5px;
            }
        }
    }

    .number-review {
        color: #7e7e7e;
        font-size: $fontSize-root - 2px;
        margin-left: 3px;
        position: relative;
        top: -1px;
    }

    .rating--small {
        .icon {
            width: 17px;
            margin-top: -5px;

            &.icon--ratingEmpty {
                svg {
                    fill: stencilColor("icon-ratingEmpty");
                }
            }

            .home-layout-2 & {
                width: 14px;
            }

            .home-layout-3 & {
                width: 18px;
                height: 16px;

                &.icon--ratingEmpty {
                    svg {
                        stroke: stencilColor("icon-ratingFull");
                        stroke-width: 10px;
                    }
                }
            }

            .home-layout-4 & {
                width: 18px;
                height: 18px;
            }
        }
    }

    .card-compare {
        position: absolute;
        left: 0;
        top: auto;
        bottom: 25px;
        line-height: 1;
        transform: unset;
        opacity: 0;
        z-index: -1;
        overflow: hidden;
        transition: unset;

        .compare {
            display: block;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 24px;
            letter-spacing: $letterSpacing-base;
            text-align: center;
            text-transform: capitalize;
            border-radius: 0;
            width: 100%;
            transition: unset;
            padding: 3px 0px 3px 24px;
            cursor: pointer;
            position: relative;

            span {
                color: $color-custom-black;
                font-size: stencilNumber("fontSize-root");
                font-weight: fontWeight("normal");
                letter-spacing: 0.02em;
                display: inline-block;
                vertical-align: middle;
                margin-left: 3px;
            }

            &:before {
                content: '';
                position: absolute;
                top: 7px;
                left: 0;
                width: 18px;
                height: 18px;
                background: transparent;
                border: 1px solid #d8d8d8;
            }

            &:after {
                content: '';
                display: block;
                width: 15px;
                height: 15px;
                position: absolute;
                left: 2px;
                top: 8px;
                background-image: url("data:image/svg+xml,%3Csvg aria-hidden='true' focusable='false' data-prefix='far' data-icon='check' role='img' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' class='svg-inline--fa fa-check fa-w-16 fa-3x'%3E%3Cpath fill='%23ffffff' d='M435.848 83.466L172.804 346.51l-96.652-96.652c-4.686-4.686-12.284-4.686-16.971 0l-28.284 28.284c-4.686 4.686-4.686 12.284 0 16.971l133.421 133.421c4.686 4.686 12.284 4.686 16.971 0l299.813-299.813c4.686-4.686 4.686-12.284 0-16.971l-28.284-28.284c-4.686-4.686-12.284-4.686-16.97 0z' class=''%3E%3C/path%3E%3C/svg%3E");
                background-repeat: no-repeat;
                background-position: center center;
                background-size: 100%;
            }
        }

        input {
            @include u-hidden("visually");

            &:checked {
                +label {
                    color: stencilColor('color-textHeading');

                    span {
                        background-image: linear-gradient(transparent 97%, #656565 3%);
                        background-repeat: repeat-x;
                        background-position-y: -4px;
                    }

                    &:before {
                        border-color: stencilColor('color-textHeading');
                        background: stencilColor('color-textHeading');
                    }
                }
            }
        }

        .home-layout-3 & {
            left: 50%;
            transform: translateX(-50%);
        }
    }

    @media(min-width: 1025px) {
        .card-figcaption-bottom {
            display: none;
        }
    }

    @media(max-width: 1024px) {
        .card-figcaption-bottom {
            position: relative;
            visibility: visible;
            opacity: 1;
            bottom: 0;

            .card-figcaption-button {
                padding: 0 10px;
            }
        }
    }

    @media(max-width: 767px) {
        .card-figcaption-bottom {
            .card-figcaption-button {
                font-size: $fontSize-root - 2px;
            }
        }
    }
}


//
// Alternate card
//
// Purpose: Used to give the card alternative styling
// -----------------------------------------------------------------------------

.card--alternate {
    border: 3px solid stencilColor("card--alternate-borderColor");
    transition: all 0.15s ease;

    .card-body {
        background-color: stencilColor("card--alternate-backgroundColor");
        padding: spacing("half");
        transition: all 0.15s ease;

        @include breakpoint("small") {
            padding: spacing("single");
        }
    }

    &:hover {
        border-color: stencilColor("color-primary");

        // scss-lint:disable SelectorDepth, NestingDepth
        .card-body {
            background-color: stencilColor("color-primary");

            .card-text {
                color: stencilColor("card--alternate-color--hover");
            }
        }

        .card-title>a {
            color: stencilColor("card--alternate-color--hover");
        }
    }
}

.card-link {
    display: block;
    position: relative;
    overflow: hidden;

    .card-img-container {
        display: block;
        position: relative;
        max-width: get-width(stencilString('productgallery_size'));
        @include lazy-loaded-padding('productgallery_size');

        &.card-img-container-2 {
            display: flex;
            align-items: center;
            justify-content: center;

            &:after {
                width: auto;
            }
        }
    }

    .card-image {
        @include lazy-loaded-img;
        opacity: 1;
        backface-visibility: visible;
        border: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;
        margin-top: 0;
        margin-bottom: 0;
        transition: opacity .8s ease;
    }

    .card-image-2 {
        position: absolute;
        top: 50%;
        left: 50%;
        z-index: 3;
        width: 100%;
        height: 100%;
        text-align: center;
        opacity: 0;
        backface-visibility: hidden;
        z-index: -1;
        transform: translate(-50%, -50%);
        transition: opacity .8s ease, transform 2s cubic-bezier(0, 0, .44, 1.18) 0s;

        img {
            display: inline-block;
            vertical-align: top;
            max-height: 100%;
            height: 100%;
            width: 100%;
            object-fit: cover;
            transition: opacity .8s ease, transform 2s cubic-bezier(0, 0, .44, 1.18) 0s;

            &.lazyload,
            &.lazyloading {
                opacity: 0;
                top: auto;
                left: auto;
                transform: none;
                position: relative;
                transition: $anchor-transition;
            }
        }
    }

    .card-no-image {
        position: absolute;
        top: 50%;
        bottom: 0;
        left: 0;
        right: 0;
        transform: translateY(-50%);
        margin: auto;
        opacity: 1;
        backface-visibility: visible;
        border: 0;
        width: 100%;
        height: auto;
        object-fit: cover;
        margin-top: 0;
        margin-bottom: 0;
        transition: opacity .8s ease;
    }

    @media (min-width: 551px) {
        .card-img-container {
            max-width: 100%;
        }
    }

    @media (min-width: 1025px) {
        .card-image-2 {
            opacity: 0;
            z-index: -1;
            overflow: hidden;
        }
    }
}

.card-option {
    min-height: 43px;
    display: block;
    margin: 0;
    @include clearfix;

    &.is-loaded {
        .form-field {
            display: block;
        }
    }

    .form-field {
        text-align: left;
        display: none;
        font-size: 0;
        letter-spacing: 0;
        border: 0;
        padding: 0;
        margin: 0;
        line-height: 1;

        .form-option.form-option-swatch {
            padding: 13px 0 0 0;

            .home-layout-2 & {
                padding: 0;
            }
        }


        .form-option {
            display: inline-block;
            vertical-align: top;
            font-size: 0;
            border: 0;
            margin: 0 5px 0 0;
            padding: 0;
            height: auto;
            line-height: 1;
            position: relative;
            cursor: pointer;

            .form-option-variant {
                position: relative;
                font-size: 0;
                width: 30px;
                height: 30px;
                margin: 0;
                padding: 0;
                border-radius: 50%;
                background-size: cover;
                background-repeat: no-repeat;
                background-position: center;
                border: 1px solid #e3e3e3;
                background-color: $color-white;

                &:after {
                    content: "";
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    z-index: 2;
                    border-radius: 50%;
                    background-color: rgba(255, 255, 255, 0);
                    opacity: 1;
                    visibility: visible;
                    border: 2px solid $color-white;
                    transition: $anchor-transition;

                    .home-layout-2 & {
                        width: 14px;
                        height: 14px;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        border-radius: 50%;
                        background-color: rgba(255, 255, 255, 0);
                        border: none;
                    }
                }

                &.form-option-variant--color2 {
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    overflow: hidden;
                    transform: rotate(45deg);

                    span {
                        display: block;
                        width: 100%;
                        height: 100%;
                        position: relative;
                        border-radius: 0px;
                        border: none;

                        &:after {
                            display: none;
                        }
                    }
                }

                .home-layout-3 & {
                    width: 42px;
                    height: 42px;
                }
            }

            .form-option-tooltip {
                display: none;
                font-size: $fontSize-root - 2px;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 1;
                color: $color-white;
                text-align: center;
                background: stencilColor('color-textBase');
                position: absolute;
                bottom: 80%;
                left: 50%;
                padding: 4px 7px;
                z-index: 2;
                min-width: 40px;
                transform: translateX(-50%);
                margin: 0 0 10px 0;
                transition: $anchor-transition;
                white-space: nowrap;

                &:before {
                    content: " ";
                    position: absolute;
                    bottom: -7px;
                    left: 50%;
                    height: 0;
                    width: 0;
                    border-top: 7px solid stencilColor('color-textBase');
                    border-left: 7px solid transparent;
                    border-right: 7px solid transparent;
                    transform: translateX(-50%);
                }

                .home-layout-2 & {
                    bottom: 95%;
                }
            }

            &:last-of-type {
                margin-right: 0;
            }

            &.active {
                .form-option-variant {
                    border-color: $color-textBase;

                    .home-layout-2 & {
                        border-color: #e3e3e3;

                        &:after {
                            background-color: rgba(255, 255, 255, 1);
                        }
                    }
                }
            }

            .home-layout-2 & {
                margin: 0 8px 0 0;
            }
        }

        .showmore {
            display: inline-block;
            vertical-align: middle;
            color: #969696;
            margin: 0 0 0 7px;
            font-size: $fontSize-root - 2px;
            font-weight: $fontWeight-normal;
            letter-spacing: $letterSpacing-base;
            line-height: 26px;
            width: auto;
            text-align: center;
            padding: 15px 0 0 0;

            .home-layout-2 & {
                margin: 0 0 0 2px;
                font-size: $fontSize-root - 4px;
                line-height: 30px;
                padding: 0;
            }
        }

        &.form-field--size {
            .form-option {
                font-size: $fontSize-root - 2px;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 1;
                text-transform: uppercase;
                color: $color-textSecondary;
            }

            +.form-field--size {
                display: none;
                margin-top: 0;
            }
        }

        +.form-field {
            margin-top: 20px;
        }
    }

    @media (min-width: 551px) {
        .form-field {
            text-align: left;
        }
    }

    @media (min-width: 1025px) {
        .form-field {
            .form-option {
                .form-option-tooltip {
                    display: block;
                    opacity: 0;
                    visibility: hidden;
                    pointer-events: none;
                }

                &:hover {
                    .form-option-tooltip {
                        opacity: 1;
                        visibility: visible;
                        pointer-events: auto;
                    }
                }
            }
        }
    }

    .home-layout-2 & {
        min-height: auto;

        .form-field {
            min-height: 43px;
        }
    }
}

.card-rating {
    margin: 0 0 4px 0;
    font-size: 0;
    letter-spacing: 0;
    line-height: 1;

    .rating--count {
        display: inline-block;
        vertical-align: middle;
        margin: 0 0 0 5px;
        font-size: $fontSize-root - 2px;
        font-weight: $fontWeight-normal;
        letter-spacing: $letterSpacing-base;
        color: $color-textSecondary;
    }
}

.card-price {
    display: flex;
    justify-content: left;
    position: relative;
    margin: 0;
    color: $color-textSecondary;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    line-height: 25px;
    letter-spacing: $letterSpacing-base;

    .home-layout-4 & {
        font-size: $fontSize-root + 2;
    }

    abbr {
        border: 0;
        color: inherit;
        font-size: inherit;
    }

    .price-section {
        margin-right: 10px;
        font-weight: $fontWeight-semibold;

        &.price-section--sale {
            color: stencilColor('sale_price_color');
        }

        &.price-section--withoutTax {
            color: stencilColor('sale_price_color');
        }

        &.non-sale-price--withoutTax,
        &.non-sale-price--withTax {
            text-decoration: line-through;
            color: #969696;
            font-weight: $fontWeight-normal;

            .home-layout-2 & {
                font-size: $fontSize-root - 2;
                font-weight: $fontWeight-normal;
            }
        }

        &.price-none {
            +.price-section--withoutTax {
                color: $color-textSecondary;
            }

            +.price-section--withTax {
                color: $color-textSecondary;
            }
        }

        &.price-section--salebale {
            display: none;
        }

        &:last-child {
            margin-right: 0;
        }

        &.rrp-price--withoutTax {
            display: none;
        }

        .home-layout-2 & {
            font-weight: $fontWeight-medium;
        }

        .home-layout-4 & {
            font-weight: $fontWeight-normal;
        }
    }

    p {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        color: $color-textSecondary;
        line-height: 26px;
        margin: 0;
    }
}

.card-footer {
    display: none;
    position: absolute;
    text-align: left;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #fff;
    z-index: 21;
    width: 100%;
    min-width: 350px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    transition: $anchor-transition;

    &.is-open {
        display: block;
    }

    &.is-loading {
        /*min-width: 200px;
        min-height: 200px;*/

        .loadingOverlay {
            display: block;
        }
    }
}

.productCarousel-slide {
    @media (max-width: 1024px) {
        .card {
            .card-footer {
                left: 0;
                transform: translateY(-50%);
                min-width: 100%;
            }
        }
    }

    @media (max-width: 991px) {
        .card {
            .card-footer {
                min-width: 300px;
                left: 50%;
                transform: translate(-50%, -50%);
            }

            &:nth-child(3n + 1) {
                .card-footer {
                    left: 0;
                    transform: translateY(-50%);
                }
            }

            &:nth-child(3n) {
                .card-footer {
                    left: auto;
                    right: 0;
                    transform: translateY(-50%);
                }
            }
        }
    }

    @media (max-width: 767px) {
        .card {
            &:nth-child(odd) {
                .card-footer {
                    left: 0;
                    transform: translateY(-50%);
                }
            }

            &:nth-child(even) {
                .card-footer {
                    left: auto;
                    right: 0;
                    transform: translateY(-50%);
                }
            }
        }
    }

    @media (max-width: 376px) {
        .card {
            .card-footer {
                min-width: 200%;
            }
        }
    }
}

.productGrid {
    @media (max-width: 1024px) {
        .product {
            .card-footer {
                left: 0;
                transform: translateY(-50%);
                min-width: 100%;
            }
        }
    }

    @media (max-width: 991px) {
        .product {
            .card-footer {
                min-width: 300px;
                left: 50%;
                transform: translate(-50%, -50%);
            }

            &:nth-child(3n + 1) {
                .card-footer {
                    left: 0;
                    transform: translateY(-50%);
                }
            }

            &:nth-child(3n) {
                .card-footer {
                    left: auto;
                    right: 0;
                    transform: translateY(-50%);
                }
            }
        }
    }

    @media (max-width: 767px) {
        .product {
            &:nth-child(odd) {
                .card-footer {
                    left: 0;
                    transform: translateY(-50%);
                }
            }

            &:nth-child(even) {
                .card-footer {
                    left: auto;
                    right: 0;
                    transform: translateY(-50%);
                }
            }
        }
    }

    @media (max-width: 376px) {
        .product {
            .card-footer {
                min-width: 200%;
            }
        }
    }
}

.productList {
    .card-footer {
        min-width: 100%;
        left: 0;
        transform: translateY(-50%);
        max-width: 100%;
    }

    @media (min-width: 551px) {
        .card-footer {
            min-width: 300px;
            max-width: 400px;
        }
    }
}

.slick-slide {
    +.slick-active {
        .card-footer {
            left: 0;
            transform: translateY(-50%);
        }
    }
}

.slick-active {
    &:first-of-type {
        .card-footer {
            left: 0;
            transform: translateY(-50%);
        }
    }

    &:last-of-type {
        .card-footer {
            left: auto;
            right: 0;
            transform: translateY(-50%);
        }
    }

    +.slick-slide {
        .card-footer {
            left: auto;
            right: 0;
            transform: translateY(-50%);
        }
    }

    @media (max-width: 321px) {
        .card-footer {
            min-width: 100% !important;
        }
    }
}

components/stencil/tags/tags.scss:
