        letter-spacing: $letterSpacing-base;
        padding-left: 15px;
        padding-right: 15px;
        background-position: right 10px top 15px;
        color: $color-custom-greyDarker;
    }

    .form-input {
        &[rows] {
            height: 170px;
        }
    }

    .button {
        width: auto;
        min-width: 210px;
        text-transform: uppercase;
    }

    @media (min-width: 768px) {
        position: relative;
        top: -10px;
        margin-top: 10px;
        padding-left: 15px;
        padding-right: 15px;
        width: calc(100% - 410px);
        float: left;
        padding-left: 15px;
    }
}

.writeReview-productImage-container {
    position: relative;

    img {
        @include lazy-loaded-img;
        height: 100%;
        object-fit: contain;
    }

    &:after {
        content: '';
        display: block;
        height: 0;
        width: 100%;
        padding-bottom: 127.63221%;
    }
}

components/stencil/addReturn/addReturn.s:
// =============================================================================
// ADD RETURN REQUEST (CSS)
// =============================================================================


.account--addReturn {
    .table {
        display: block;
        margin-bottom: spacing("double");

        @include breakpoint("small") {
            display: table;
            margin-bottom: spacing("double") * 2;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        tr {
            display: block;
            padding: spacing("single") 0;

            @include breakpoint("small") {
                display: table-row;
                padding: 0;
            }
        }

        th,
        td {
            display: block;
            padding: 0;

            @include breakpoint("small") {
                display: table-cell;
                padding-bottom: spacing("half");
                padding-top: spacing("half");
            }

            // scss-lint:disable NestingDepth
            +th,
            +td {
                padding-left: 0;

                @include breakpoint("small") {
                    padding-left: spacing("single");
                }
            }
        }

        .return-itemTitle+.definitionList {
            margin-top: spacing("eighth");
        }

        .return-itemPrice {
            @include breakpoint("small") {
                text-align: center;
            }
        }

        .return-itemQuantity {
            @include breakpoint("small") {
                text-align: right;
            }
        }
    }

    .table-thead {
        display: none;

        @include breakpoint("small") {
            display: table-header-group;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        th {
            font-size: fontSize("smaller");
        }

        th:first-child {
            width: 60%;
        }
    }

    .table-tbody {
        display: block;

        @include breakpoint("small") {
            display: table-row-group;
        }
    }
}


.return-itemName {
    color: $header-font-color;

    .return-itemTitle {
        font-size: $fontSize-root;
        font-weight: $fontWeight-medium;
        letter-spacing: $letterSpacing-base;
        line-height: 28px;
        margin: 0 0 5px 0;
        text-transform: capitalize;
        display: block;
    }

    .definitionList {

        .definitionList-label,
        .definitionList-description {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 1.75;
            letter-spacing: $letterSpacing-base;
            color: $color-textSecondary;
        }

        .definitionList-label {
            float: left;
            margin: 0 0.375rem 0 0;
        }

        .definitionList-description {
            font-weight: $fontWeight-normal;
            margin: 0;
        }
    }
}

.return-itemPrice {
    font-size: $fontSize-root + 2px;
    font-weight: $fontWeight-medium;
    color: $header-font-color;
    margin-bottom: spacing("quarter");

    @media (min-width: 551px) {
        margin-bottom: 0;
        text-align: center;
    }
}

.return-itemQuantity {
    color: $header-font-color;

    .form-label {
        display: inline;
        margin-right: spacing("quarter");

        @include breakpoint("small") {
            @include u-hidden("visually");
        }
    }
}

.account--addReturn-row {
    @include grid-row($behavior: "nest");
    margin: 0 (-(spacing("base")));
}

.account--addReturn-column {
    @include grid-column(12);
    padding: 0 spacing("base");

    @include breakpoint("small") {
        width: grid-calc(6, $total-columns);
    }
}

components/stencil/navPages/navPages.scs:
// Page Navigation
// -----------------------------------------------------------------------------
//
// Purpose: Contains the styles for the page navigation, deals with 2 viewsdue to
// responsive layout of that navigation
//
// 1. It's not exactly a grid row, as it's a little further down the DOM tree, but
// I use it here to grab the max width centering.
// 2. Prevent document scrolling by clipping its body
// 3. Prevent inertia scrolling of document
//
// -----------------------------------------------------------------------------


//
// Shared styles for sub page navigation
// -----------------------------------------------------------------------------

%navPage-childList {
    border-left: 1px solid $navPage-childList-borderColor;
    margin: spacing("quarter") spacing("half");
    padding-left: spacing("half");
}

//
// When navPages is open
// -----------------------------------------------------------------------------

.has-activeNavPages {
    // scss-lint:disable VendorPrefix
    overflow: hidden; // 2
    -webkit-overflow-scrolling: auto; // 3
}

//
// Top level Pages navigation container
// -----------------------------------------------------------------------------

.navPages-container {
    // scss-lint:disable VendorPrefix
    bottom: 0;
    display: none;
    height: 100%;
    left: 0;
    -webkit-overflow-scrolling: touch;
    position: absolute;
    right: 0;
    z-index: zIndex("lower");

    @include breakpoint("medium") {
        background: transparent;
        display: block;
        height: auto;
        padding: 0;
        position: relative;
    }

    &.is-open {
        display: block;
        padding-top: $header-height;
    }
}

//
// Top level Pages navigation list styles
// -----------------------------------------------------------------------------

.navPages {
    background-color: $navPages-backgroundColor;
    height: 100%;
    overflow-y: auto;
    padding: 0;

    @include breakpoint("medium") {
        @include grid-row(); // 1
        @include nav($style: "navBar", $wrapper: "nav");
        background: none;
        font-size: 0; // 1
        overflow-y: visible;
        padding: 10px 0;
        text-align: center;
        max-width: calc(100% - 50px);
        margin: 0;

        .header-layout-2 & {
            padding: 10px 0 5px;
            max-width: 100%;
        }

        .header-layout-3 & {
            padding: 10px;
            max-width: 100%;
        }

        .header-layout-4 & {
            padding: 12px 0 11px;
            max-width: 100%;
        }
    }

    ul {
        list-style: none;
    }

    .navPage-subMenu-list {
        border-left: 0;
        list-style: none;
        margin-left: 0;
        padding-left: 0;

        @include breakpoint("medium") {
            @include grid-row();
            border-left: 0;
            margin: auto;
            padding-left: 0;
        }

        .navPage-subMenu-item-child {
            @media(min-width: 1025px) {
                &.navPage-subMenu-all {
                    display: none !important;
                }
            }
        }
    }
}

.navPages-list {
    margin: 0;

    @include breakpoint("medium") {
        float: none;
    }
}

.navPages-list-depth-max.subMenu-is-open {
    .is-hidden {
        display: none;

        @include breakpoint("medium") {
            display: inline-block;
        }
    }

    .navPages-item-page {
        display: none;

        @include breakpoint("medium") {
            display: inline-block;
        }
    }
}

.navPages-action-depth-max.has-subMenu.is-root.is-open {
    .navPages-action-moreIcon {
        @include breakpoint("medium") {
            transform: rotate(0);
        }
    }
}

.navPages-action-depth-max.has-subMenu.is-open {
    border-bottom: 1px solid $navPages-list--user-borderColor;
    text-align: center;

    @include breakpoint("medium") {
        border-bottom: 0;
        text-align: left;
    }

    .navPages-action-moreIcon {
        border: solid 1px $navPages-action-moreIcon-borderColor;
        border-radius: 4px;
        float: left;
        height: 35px;
        margin-top: -10px;
        padding: 11.7px; // (33 - 9.6) / 2
        transform: rotate(90deg);
        width: 35px;

        @include breakpoint("medium") {
            @include square(8px);
            border: 0;
            border-radius: 0;
            float: none;
            margin-top: 0;
            padding: 0;
            transform: rotate(-90deg);
        }
    }

    svg {
        height: 9.6px;
        width: 9.6px;

        @include breakpoint("medium") {
            height: 100%;
            width: 100%;
        }
    }
}

.navPages-action {
    color: stencilColor("navPages-color");
    display: block;
    font-size: fontSize("smaller");
    font-weight: fontWeight("normal");
    padding: spacing("half") 0;
    text-decoration: none;
    text-transform: capitalize;
    margin-bottom: 0;

    .home-layout-2 &,
    .home-layout-3 & {
        font-size: fontSize("smaller") + 2px;
    }

    &.has-subMenu {
        .navPages-list:not(.navPages-list-depth-max) & {
            display: flex;
            justify-content: space-between;
            align-items: center;
        }
    }

    &.is-open,
    .collapsible-icon-wrapper.is-open {

        // scss-lint:disable NestingDepth
        svg {
            fill: $navPage-subMenu-item--is-highlighted-color;
            stroke: $navPage-subMenu-item--is-highlighted-color;
        }
    }

    @include breakpoint("medium") {
        display: inline-block;
        padding: 12px 17px 12px 18px;

        &.is-open {
            background-color: $navPage-subMenu-item--is-highlighted-background;
        }

        .home-layout-4 & {
            padding: 12px 10px;
        }
    }

    @media(min-width: 1400px) {
        .home-layout-4 & {
            padding: 12px 15px;
        }
    }

    .Sale,
    .sale {
        span:not(.navPages-label) {
            color: stencilColor('sale_price_color') !important;
        }
    }

    span:not(.navPages-label) {
        color: stencilColor("navPages-color");
        background-image: linear-gradient(currentColor, currentColor);
        background-position: 0 90%;
        background-repeat: no-repeat;
        background-size: 0 2px;
        transition: background-size .5s;
        padding: 0 0 3px 0;

        .home-layout-3 & {
            padding: 0 0 10px 0;
        }
    }

    &.navPages-action-lv2 {
        span:not(.navPages-action-moreIcon) {
            color: stencilColor("navPages-lv2-color");
            background-size: 0 1px;
            padding: 0 0 1px 0;
        }
    }

    &.navPages-action-lv3 {
        span:not(.navPages-action-moreIcon) {
            color: stencilColor("navPages-lv3-color");
            background-size: 0 1px;
            padding: 0 0 1px 0;
        }
    }

    &.navPages-action-custom {
        span {
            background-size: 0 1px;
            padding: 0 0 1px 0;
        }
    }

    @media(min-width: 1025px) {
        &:hover,
        &:focus-within {
            span:not(.navPages-label) {
                color: stencilColor("navPages-color-hover");
            }

            .text {
                span:not(.navPages-label) {
                    background-size: 100% 2px;
                }
            }
        }

        &.no-subMenu {
            &:hover,
            &:focus-within {
                .text {
                    background-size: 100% 2px;
                }
            }
        }

        &.navPages-action-lv2 {
            &:hover,
            &:focus-within {
                span:not(.navPages-action-moreIcon) {
                    color: stencilColor("navPages-lv2-color-hover");
                    background-size: 100% 1px !important;
                }
            }
        }

        &.navPages-action-lv3 {
            &:hover,
            &:focus-within {
                span:not(.navPages-action-moreIcon) {
                    color: stencilColor("navPages-lv3-color-hover");
                    background-size: 100% 1px !important;
                }
            }
        }

        &.navPages-action-custom {
            &:hover,
            &:focus-within {
                .text {
                    span {
                        background-size: 100% 1px;
                    }
                }
            }
        }
    }

    svg {
        fill: stencilColor("navPages-color");
        stroke: stencilColor("navPages-color");
        transition: all 0.15s ease;
    }
}

.navPages-action--storeCredit {
    color: stencilColor("color-textSecondary");
}

.navPages-action--compare {
    display: none;

    &.show {
        display: block;
    }
}

.navPages-action-moreIcon {
    @include square(8px);
    margin-left: spacing("half");
    transform: rotate(-90deg);

    @include breakpoint("medium") {
        transform: none;
    }

    .has-subMenu.is-open &,
    .collapsible-icon-wrapper.is-open & {
        transform: rotate(0);
    }

    .navPages-list:not(.navPages-list-depth-max) & {
        @include square(11px);
        margin: 0;

        @include breakpoint("medium") {
            @include square(8px);
            margin: 0 0 0 spacing("half");
        }
    }

    @media(min-width: 1025px) {
        display: none;
    }
}


// Sub page navigation and children of sub pages
// -----------------------------------------------------------------------------

.navPage-childList {
    @extend %navPage-childList;
    display: none;

    @include breakpoint("medium") {
        border: 0;
        display: block;
    }

    &.is-open {
        display: block;
    }

    .navPage-subMenu-action:not(.navPages-action-depth-max)+& {
        margin-left: spacing("double"); // 

        @include breakpoint("medium") {
            margin-left: 0;
        }
    }
}


// Sub menu, first level down from top level categories
// -----------------------------------------------------------------------------
//
// 1. navBar mixin has lots of decendant child selectors to deal with
//
// -----------------------------------------------------------------------------

.navPage-subMenu {
    display: none;
    outline: 0;

    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        background-color: stencilColor("navPages-subMenu-backgroundColor");
        padding: spacing("single") spacing("single") (spacing("single") + spacing("half"));
        position: absolute;
        text-align: left;

        &.is-open {
            display: block;
            left: 0;
            width: 100%;
        }

        .navPages-action-moreIcon {
            display: none;
        }
    }

    &.is-open {
        display: block;
    }

    .navPages-list:not(.navPages-list-depth-max) .navPages-action.has-subMenu+& {
        .navPage-subMenu-list {
            margin-right: 0;

            @include breakpoint("medium") {
                margin-right: auto;
            }
        }
    }

    &::-webkit-scrollbar {
        width: 10px;
    }

    &::-webkit-scrollbar-track {
        background: #f5f5f5;
    }

    &::-webkit-scrollbar-thumb {
        background: #a6a6a6;
    }
}

.has-subMenu.is-root+.navPage-subMenu-horizontal {
    @include breakpoint("medium") {
        border-left: 0;
        height: $navPage-subMenu-horizontal-height;
        left: inherit;
        margin: auto;
        top: inherit;
        width: $navPage-subMenu-horizontal-width;
    }
}

.navPage-subMenu-horizontal {
    &.is-open {
        @include breakpoint("medium") {
            border-left: 1px solid stencilColor("navPages-subMenu-separatorColor");
            display: inherit;
            height: $navPage-subMenu-horizontal-height;
            margin-left: $navPage-subMenu-horizontal-width;
            padding: 0 !important; // scss-lint:disable ImportantRule
            top: 0;
            width: $navPage-subMenu-horizontal-width + 1;
        }
    }

    .navPages-action-moreIcon {
        @include breakpoint("medium") {
            display: inline-block;
            transform: rotate(-90deg);
        }
    }
}

.navPage-subMenu-item {
    padding: spacing("half") 0;

    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        @include grid-column(3);
        padding: 0 0.75rem; // 1

        .navPages-action {
            text-transform: none;
            width: 100%;
        }

        >.navPage-subMenu-action {
            border-bottom: 1px solid stencilColor("navPages-subMenu-separatorColor");
            margin-bottom: spacing("half") + spacing("quarter");
        }
    }
}

.navPage-subMenu-item-child,
.navPage-subMenu-item-parent {
    width: 100%;

    @include breakpoint("medium") {
        .navPage-subMenu-action {
            padding-left: 0;
            padding-right: 0;
            width: 100%;
        }
    }
}

.navPage-subMenu-action {
    padding: spacing("half") 0;
    font-size: $fontSize-root;

    .navPages-list:not(.navPages-list-depth-max) & {
        padding: 0 0 0 spacing("single");

        @include breakpoint("medium") {
            padding: spacing("half") 0 (spacing("half") + spacing("quarter"));
        }
    }

    @include breakpoint("medium") {
        padding: spacing("half") 0 (spacing("half") + spacing("quarter"));
    }

    .collapsible-icon-wrapper {
        height: 100%;
        width: 100%;
        box-sizing: content-box;
        display: inline-block;
        text-align: right;

        @include breakpoint("medium") {
            display: none;
        }
    }
}

.navPages-list {
    .navPages-item {
        .navPage-subMenu {
            display: block;
            position: absolute;
            left: 130px;
            top: auto;
            z-index: 6;
            height: auto;
            margin: auto;
            width: 245px;
            padding: 0;
            text-align: left;
            opacity: 0;
            visibility: hidden;
            pointer-events: none;
            transition: $anchor-transition;
            background-color: stencilColor('navPages-subMenu-backgroundColor');
            box-shadow: 0px 0px 25px 0px rgba(0, 0, 0, 0.1);
            transform: translate3d(0, 50px, 0);
        }

        &.navPages-item-user {
            .navPages-action {
                .text {
                    .flag {
                        display: inline-block;
                        vertical-align: middle;
                        margin: 0 10px 0 0;
                        width: 16px;
                        height: 16px;

                        img {
                            display: block;
                            width: 100%;
                            height: 100%;
                            object-fit: contain;
                        }
                    }
                }
            }

            >.navPages-action {
                font-weight: $fontWeight-medium;

                .text {
                    >.icon {
                        display: inline-block;
                        vertical-align: middle;
                        fill: stencilColor('navPages-color');
                        stroke: transparent;
                        margin: 0 10px 0 0;
                        min-height: 45px;
                    }
                }
            }

            &:last-child {
                border-bottom: 0;
            }
        }
    }
}


//
// Child pages of sub pages navigation lists
// -----------------------------------------------------------------------------

.navPage-childList-item {
    @include breakpoint("medium") {}
}

.navPage-childList-action {
    font-weight: fontWeight("normal");
    padding: 0 0 spacing("quarter");
}


//
// Quick search inside the mobile nav
// -----------------------------------------------------------------------------

.navPages-quickSearch {
    border-bottom: container("border");
    margin-bottom: spacing("half");

    @include breakpoint("medium") {
        display: none;
    }

    >.container {
        padding: 0;
    }

    .form {
        margin: 0;
    }

    .form-field {
        margin-bottom: 0;
    }
}


//
// NavPages user section for mobile nav panel
// -----------------------------------------------------------------------------

.navPages-list--user {
    @include breakpoint("medium") {
        display: none;
    }

    // scss-lint:disable SelectorDepth, NestingDepth, IdSelector
    #currencySelection2 {
        background-color: color("whites", "bright");
        border: container("border");

        .dropdown-menu-item {
            padding: 0;

            // scss-lint:disable NestingDepth
            &:not(:last-child) {
                border-bottom: container("border");
            }
        }

        a {
            padding: spacing("half") spacing("single");
        }
    }
}

.navPages-item {
    >.navPages-action {
        font-weight: $fontWeight-medium;
        text-transform: uppercase;

        .home-layout-2 &,
        .home-layout-3 & {
            font-weight: $fontWeight-normal;
            text-transform: capitalize;
        }

        .home-layout-4 & {
            font-weight: $fontWeight-extrabold;
        }
    }

    .navPage-subMenu-item-child>.navPages-action:hover,
    .navPage-subMenu-item-parent>.navPages-action:hover,
    .navPage-subMenu-item-child>.navPages-action:focus-within,
    .navPage-subMenu-item-parent>.navPages-action:focus-within {
        color: stencilColor("navPages-color-hover");
    }
}

// Megamenu
// -----------------------------------------------------------------------------
@media(min-width: 1025px) {
    .navPages-container {
        position: static;

        .navPages-list {
            >.navPages-item {
                >.navPages-action {
                    transition-delay: .1s;
                    transition-property: transform, opacity;
                    transition-timing-function: ease;
                    transform: translatey(-45px);
                    opacity: 0;
                }
            }

            .navPages-item {
                @for $i from 0 through 100 {
                    &:nth-child(n + #{$i}) {
                        >.navPages-action {
                            transition-duration: .5s + $i*0.05;
                        }
                    }
                }
            }
        }

        .navPages-item {
            position: relative;

            >.navPages-action {
                position: relative;
            }

            &.has-dropdown:not(.has-megamenu) {
                .navPage-subMenu {
                    width: 245px;
                    padding: 0;
                    opacity: 0;
                    visibility: hidden;
                    pointer-events: none;
                    transform: translate3d(-50px, 0, 0);
                    -webkit-transition: $anchor-transition;
                    -moz-transition: $anchor-transition;
                    -ms-transition: $anchor-transition;
                    -o-transition: $anchor-transition;
                    transition: $anchor-transition;
                    -webkit-box-shadow: 0px 1px 50px 0px rgba(0, 0, 0, 0.1);
                    box-shadow: 0px 1px 50px 0px rgba(0, 0, 0, 0.1);
                    background-color: $navPages-backgroundColor;

                    .navPage-subMenu-item-child {
                        padding: 0 20px;

                        >.navPage-subMenu-action {
                            padding: 12px 15px 12px 0;

                            .navPages-action-moreIcon {
                                width: 12px;
                                height: 12px;
                                position: absolute;
                                top: 50%;
                                right: 18px;
                                margin-left: 0;
                                transform: translateY(-50%) rotate(-90deg);
                            }
                        }

                        +.navPage-subMenu-item-child .navPage-subMenu-action {
                            border-top: 1px solid $color-custom-grey;
                        }
                    }

                    .home-layout-2 & {
                        transform: translate3d(0, -10px, 0);
                    }

                    .home-layout-3 &,
                    .home-layout-4 & {
                        transform: translate3d(0, -20px, 0);
                    }
                }

                &:hover,
                &:focus-within,
                .navPage-subMenu-item-child:hover,
                .navPage-subMenu-item-child:focus-within {
                    >.navPage-subMenu {
                        .menu-is-load & {
                            opacity: 1;
                            visibility: visible;
                            pointer-events: auto;
                            transform: translate3d(0, 0, 0);
                        }
                    }
                }

                .navPage-subMenu-item-child {
                    position: relative;

                    >.navPage-subMenu {
                        top: 0;
                        left: 95%;
                    }
                }
            }

            &.has-megamenu>.navPage-subMenu>.cateArea>.navPage-subMenu-list>.navPage-subMenu-item-child>.navPage-subMenu>.navPage-subMenu-list .navPage-subMenu-item-child>.navPage-subMenu {
                display: none !important;
            }

            .navPage-subMenu {
                display: block;
            }

            >.navPage-subMenu {
                left: 0;
                opacity: 0;
                visibility: hidden;
                pointer-events: none;
                transform: translate3d(-50px, 0, 0);
                -webkit-transition: $anchor-transition;
                -moz-transition: $anchor-transition;
                -ms-transition: $anchor-transition;
                -o-transition: $anchor-transition;
                transition: $anchor-transition;
                margin-top: 0;
                z-index: 1;

                .navPage-subMenu-item-parent {
                    display: none;
                }

                .navPage-subMenu {
                    padding-left: 0;
                    padding-right: 0;
                }

                .home-layout-2 & {
                    transform: translate3d(0, -10px, 0);
                }

                .home-layout-3 &,
                .home-layout-4 & {
                    transform: translate3d(0, -20px, 0);
                }
            }

            &.has-megamenu {
                position: static;

                >.navPage-subMenu {
                    padding: 40px calc((100% - 1800px)/2) 60px;
                    box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.1);
                    background-color: $navPages-backgroundColor;
                    z-index: 1;

                    &:before {
                        content: '';
                        width: 100%;
                        height: 1px;
                        background-color: $color-custom-grey;
                        position: absolute;
                        left: 0;
                        right: 0;
                        top: 10px;
                        z-index: 1;
                        cursor: pointer;
                    }
                }

                &.style-2 {
                    >.navPage-subMenu {
                        padding: 30px 0 0;
                    }

                    .cateArea {
                        padding: 0 calc((100% - 1800px)/2);
                    }
                }

                .navPage-subMenu-links {
                    .navPage-subMenu-title {
                        display: none;
                    }

                    >.navPage-subMenu-item-child {
                        padding-left: 15px;
                        padding-right: 15px;

                        .navPage-subMenu-action {
                            margin-bottom: 0;
                        }

                        .navPage-subMenu-item-child {
                            padding-left: 0;
                            padding-right: 0;
                        }
                    }
                }
            }

            &:hover,
            &:focus-within {
                >.navPages-action {
                    position: relative;

                    &:after {
                        content: '';
                        width: 100%;
                        height: 14px;
                        background-color: transparent;
                        position: absolute;
                        left: 0;
                        right: 0;
                        bottom: -14px;
                        z-index: 1;
                    }
                }

                >.navPage-subMenu {
                    top: auto;
                    opacity: 1;
                    visibility: visible;
                    pointer-events: auto;
                    transform: translate3d(0, 0, 0);
                }
            }
        }
    }

    header.header.is-sticky {
        .navPages-container .navPages-item>.navPage-subMenu {
            margin-top: 0;
        }
    }

    .menu-is-load {
        .navPages-container {
            .navPages-list {
                >.navPages-item {
                    >.navPages-action {
                        transform: translatey(0);
                        opacity: 1;
                    }
                }
            }
        }
    }
}

