// Mega Menu Image
// -----------------------------------------------------------------------------
.has-megamenu {
    .image {
        display: block;
        overflow: hidden;
        position: relative;
        text-align: center;

        img {
            width: 100%;
            object-fit: cover;

            &.lazyload,
            &.lazyloading {
                width: auto;
                height: auto;
                margin: auto;
                object-fit: contain;
            }

            &.lazyloaded {
                transition: all .7s ease;
            }
        }
    }

    @media (min-width: 1025px) {
        .image {
            &:before {
                content: "";
                width: 0;
                height: 100%;
                top: 0;
                left: 0;
                left: auto;
                bottom: auto;
                z-index: 2;
                transform: none;
                position: absolute;
                -webkit-transition: all 0.5s ease;
                -moz-transition: all 0.5s ease;
                -ms-transition: all 0.5s ease;
                -o-transition: all 0.5s ease;
                transition: all 0.5s ease;
                background-color: rgba(246, 246, 246, .2);
            }

            &:hover,
            &:focus-within {
                &:before {
                    width: 100%;
                }
            }

            .home-layout-2 & {
                &:before {
                    width: 100%;
                    height: 0;
                }

                &:hover,
                &:focus-within {
                    &:before {
                        width: 100%;
                        height: 100%;
                    }
                }
            }
        }
    }
}

