        position: relative;
        height: 26px;
        line-height: 26px;
        left: 40px;

        &:before {
            content: '';
            position: absolute;
            border: 7px solid transparent;
            top: 50%;
            right: calc(100% - 1px);
            transform: translateY(-50%);
        }

        &.sale-label {
            &:before {
                border-right-color: stencilColor('mega_menu_sale_label_bgColor');
            }
        }

        &.new-label {
            &:before {
                border-right-color: stencilColor('mega_menu_new_label_bgColor');
            }
        }

        &.hot-label {
            &:before {
                border-right-color: stencilColor('mega_menu_hot_label_bgColor');
            }
        }
    }
}

.halo-menu-sidebar {
    .navPages {
        display: block;
        font-size: 0;
        letter-spacing: 0;
        @include clearfix;
    }

    .navPages-container {
        display: block;
        height: 100%;
        overflow-y: auto;
        overflow-x: hidden;
        padding: 0;
    }

    .navPages-list {
        position: relative;
        transition-duration: 0.4s;
        transform: none;
        transition-property: transform;
        background: $color-white;

        .has-activeNavPages & {
            display: block;
        }
    }

    .navPages-item {
        width: 100%;
        margin: 0;
        border-bottom: 1px solid $color-custom-grey;
        position: relative;

        .navPages-action {
            font-size: $fontSize-root - 2px;
            color: $color-textBase;
            margin: 0;
            padding: 0 15px;
            display: flex;
            cursor: pointer;
            align-items: center;
            text-transform: capitalize;
            background-color: transparent;
            justify-content: space-between;
            line-height: 45px;
            letter-spacing: $letterSpacing-base;
            font-weight: $fontWeight-medium;

            .text {
                display: flex;
                align-items: center;
                padding: 0;
                color: inherit;
                position: relative;
                background: transparent !important;

                .icon {
                    color: $color-textBase;
                    fill: $color-textBase !important;
                }
            }

            .navPages-action-moreIcon {
                width: auto;
                height: 45px;
                z-index: 10;
                top: auto;
                display: flex;
                align-items: center;
                justify-content: center;
                order: 0;
                text-align: right;
                font-size: 0;
                pointer-events: none;
                transform: none;
                margin-left: 0;

                svg {
                    fill: $color-textBase;
                    color: $color-textBase;
                    stroke: $color-textBase;
                    pointer-events: none;
                    display: inline-block;
                    vertical-align: middle;
                    transition: $anchor-transition;
                    transform: rotate(-90deg);
                    width: 13px;
                    height: 13px;
                }
            }

            &:not(.navPages-action-end) {
                .text {
                    pointer-events: none;
                }
            }

            +.level-3 {
                .navPages-action {
                    color: stencilColor('navPages-level-2-color');

                    .text {
                        color: stencilColor('navPages-level-2-color');
                    }

                    .navPages-action-moreIcon {
                        svg {
                            fill: stencilColor('navPages-level-2-color');
                            color: stencilColor('navPages-level-2-color');
                            stroke: stencilColor('navPages-level-2-color');
                        }
                    }

                    @media(min-width: 1025px) {
                        &:hover,
                        &:focus-within {
                            color: stencilColor('navPages-level-2-color-hover');

                            .text {
                                color: stencilColor('navPages-level-2-color-hover');
                            }

                            .navPages-action-moreIcon {
                                svg {
                                    fill: stencilColor('navPages-level-2-color-hover');
                                    color: stencilColor('navPages-level-2-color-hover');
                                    stroke: stencilColor('navPages-level-2-color-hover');
                                }
                            }
                        }
                    }
                }
            }
        }

        >.navPages-action {
            font-weight: $fontWeight-medium;
            line-height: 45px;
            text-transform: uppercase;

            .text {
                white-space: nowrap;
            }
        }

        .navPage-subMenu {
            top: 0;
            right: 100%;
            left: auto;
            width: 100%;
            height: 100%;
            opacity: 1;
            pointer-events: auto;
            visibility: visible;
            transform: none;
            box-shadow: none;
        }

        >.navPage-subMenu {
            >.navPage-subMenu-list {
                >.navPage-subMenu-item-child {
                    >.navPages-action {
                        font-weight: $fontWeight-medium;
                    }
                }
            }
        }

        .navPage-subMenu-item-child {
            padding-right: 0;

            >.navPages-action {
                .navPages-action-moreIcon {
                    right: 16px;
                }
            }

            &.navPage-subMenu-all {
                display: inline-block;
            }

            .navPages-action {
                border-bottom: 1px solid stencilColor('navPages-subMenu-separatorColor');
                font-size: $fontSize-root;
            }
        }

        .navPage-subMenu-title {
            >.navPages-action {
                font-weight: $fontWeight-medium;
                color: stencilColor('navPages-level-1-color');
                background-color: #f5f5f5;

                .navPages-action-moreIcon {
                    display: flex;
                    align-items: center;
                    justify-content: flex-start;
                    left: 30px;
                    right: auto;

                    svg {
                        transform: unset;
                        width: 25px;
                        height: 25px;
                        margin: 0;
                        fill: stencilColor('navPages-level-1-color');
                    }
                }

                .text {
                    max-width: calc(100% - 70px);
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
                    display: inline-block;
                    vertical-align: top;
                    width: 100%;
                    margin: 0 auto;
                    text-align: center;
                    color: stencilColor('navPages-level-1-color');
                    text-transform: uppercase
                }

                @media(min-width: 1025px) {
                    &:hover,
                    &:focus-within {
                        color: stencilColor('navPages-level-1-color-hover');

                        .navPages-action-moreIcon {
                            svg {
                                fill: stencilColor('navPages-level-1-color-hover');
                            }
                        }

                        .text {
                            color: stencilColor('navPages-level-1-color-hover');
                        }
                    }
                }
            }
        }

        +.navPages-item {
            margin-left: 0;
        }
    }

    .socialLinks {
        margin: 10px 0 0 0 !important;
    }

    .socialLinks-item {
        display: inline-block;
        vertical-align: top;
        margin: 0 0 15px 0 !important;

        svg {
            width: 19px !important;
            height: 19px !important;
            fill: $color-textBase !important;
        }

        +.socialLinks-item {
            margin-left: 15px !important;
        }
    }

    .is-open {
        border-bottom: 0;

        >.navPages-action {
            display: none !important;
        }

        >.navPage-subMenu {
            position: relative;
            top: 0;
            right: 0;
            margin: 0;

            &#navPages-currency {
                top: -36px;
            }

            >.navPage-subMenu-list {
                padding-left: 0;

                >.navPage-subMenu-item-child {
                    &.navPage-subMenu-title {
                        display: block;
                    }
                }
            }

            .cateArea {
                >.navPage-subMenu-list {
                    >.navPage-subMenu-item-child {
                        &.navPage-subMenu-title {
                            display: block;
                        }
                    }
                }
            }

            .centerArea {
                >.navPage-subMenu-list {
                    >.navPage-subMenu-item-child {
                        &.navPage-subMenu-title {
                            display: block;
                        }
                    }
                }
            }
        }
    }

    .is-hidden {
        display: none !important;
    }

    #currency_selector {
        margin-top: -26px;
    }

    @media(min-width: 1025px) {
        .halo-sidebar-header {
            display: none;
        }
    }

    @media(max-width: 1024px) {
        .halo-sidebar-header {
            .close {
                right: 5px;
            }
        }
    }

    &.halo-menu-megamenu {
        .navPages-list {
            .has-megamenu {
                &.style-1 {
                    .imageArea {
                        .image {
                            img {
                                margin-bottom: 0px;
                            }
                        }

                        .megamenu-right-item {
                            width: 100%;
                            text-align: center;
                            padding: 0;
                            margin-top: 0;

                            .image {
                                padding-bottom: 15px;
                            }

                            .megamenu-title {
                                font-size: 16px;
                                font-weight: $fontWeight-semibold;
                                text-align: left;
                                margin-top: 0;
                                padding-bottom: 10px;
                                border-bottom: 1px solid $color-greyLighter;
                            }

                            .navPage-subMenu-item-child {
                                border-top: none;
                            }

                            .navPage-subMenu-action {
                                padding: 5px 0;
                                border-bottom: none;
                                justify-content: flex-start;

                                .text {
                                    margin-left: 15px;
                                }
                            }
                        }

                        @media(max-width: 375px) {
                            display: block;

                            .megamenu-right-item {
                                width: 100%;
                            }
                        }
                    }
                }

                &.style-2 {
                    .cateArea {
                        flex-direction: column;
                        flex-wrap: unset;

                        .navPage-subMenu-list:not(.navPage-subMenu-links) {
                            order: -1;
                        }
                    }

                    .imageArea {
                        .item {
                            margin: 0 0 15px 0;
                        }
                    }

                    .megamenu-right-item {
                        padding: 15px 15px 0;

                        .item {
                            margin: 0;
                        }
                    }

                    .megamenu-title {
                        font-size: 16px;
                        font-weight: $fontWeight-semibold;
                        text-align: left;
                        margin-top: 0;
                        padding-bottom: 10px;
                        border-bottom: 1px solid $color-greyLighter;
                    }

                    .megamenu-brands {
                        padding: 5px 0;

                        ul {
                            margin-left: -10px;
                            margin-right: auto;

                            li {
                                display: inline-block;

                                a {
                                    color: #505050;
                                    text-decoration: none;
                                    font-size: stencilNumber("fontSize-root");
                                    font-weight: fontWeight("normal");
                                    text-transform: uppercase;
                                    padding: 0 10px;
                                    line-height: 20px;
                                    pointer-events: none;

                                    span {
                                        background-image: linear-gradient(currentColor, currentColor);
                                        background-position: 0 88%;
                                        background-repeat: no-repeat;
                                        background-size: 0 1px;
                                        transition: background-size .5s;
                                    }

                                    &:hover span,
                                    &:focus-within span {
                                        background-size: 100% 1px;
                                    }
                                }

                                &.has-letter a {
                                    color: stencilColor('navPages-color');
                                    font-weight: $fontWeight-semibold;
                                    pointer-events: auto;
                                }
                            }
                        }

                        .view-all {
                            display: inline-block;
                            text-decoration: none;
                            color: stencilColor('navPages-color');
                            font-size: stencilNumber("fontSize-root");
                            font-weight: fontWeight("normal");
                            margin-top: 10px;
                            background-image: linear-gradient(transparent 96%, rgba(stencilColor('navPages-color'), .6) 4%);
                            background-repeat: repeat-x;
                            background-position-y: -5px;
                        }
                    }
                }

                &.style-3 {
                    .cateArea {
                        display: flex;
                        flex-direction: column;

                        .navPage-subMenu-list:not(.navPage-subMenu-links) {
                            order: -1;
                        }
                    }

                    .megamenu-right-item {

                        .image {
                            padding: 15px 15px 0;
                        }
                    }

                    .megamenu-custom-list {
                        position: relative;
                        width: 100%;

                        .megamenu-bottom {
                            display: block;
                            background-color: $color-custom-linen;
                            color: $color-textBase;
                            font-size: $fontSize-root;
                            font-weight: $fontWeight-normal;
                            letter-spacing: $letterSpacing-base;
                            text-align: center;
                            line-height: 1.625;
                            padding: 12px 0;
                        }

                        .text {
                            position: relative;

                            &:before {
                                position: absolute;
                                left: 0;
                                width: 100%;
                                bottom: 2px;
                                height: 1px;
                                content: '';
                                background: #4a4743;
                            }
                        }
                    }
                }
            }
        }
    }
}

#navPages-currency {
    margin: 10px 0 30px;

    .navPage-subMenu-item {
        padding: 0;

        @media(max-width: 1199px) {
            width: 100%;
            float: none;

            .navPage-subMenu-action {
                border: none;
                margin-bottom: 0;
            }
        }

        strong {
            background-image: linear-gradient(transparent 96%, rgba(stencilColor('navPages-color'), .6) 4%);
            background-repeat: repeat-x;
            background-position-y: 0px;
        }
    }
}

components/stencil/productCarousel/produ:
// PRODUCT CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the product carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display product
//
// 1. If there's no JS layout the products out based on the grid.
//
// -----------------------------------------------------------------------------

.productCarousel {
    // 1
    display: block;
    position: relative;
    padding: 0;
    margin: 0 -5px;
    width: auto !important;

    .slick-dots {
        bottom: -50px;
    }

    .product-sample * {
        font-size: 0;
    }

    .home-layout-2 &,
    .home-layout-3 & {
        margin: 0 0 0 -5px;

        .slick-dots {
            display: none !important;
        }

        @media (max-width: 550px) {
            .slick-arrow {
                top: 35%;
            }
        }

        @media (min-width: 1025px) {
            margin: 0 0 0 -15px;
        }
    }

    .home-layout-4 & {
        @media (min-width: 1025px) {
            margin: 0 0 30px -15px;
        }
    }
}

.productCarousel-slide {
    // 1
    margin: 0;
    padding: 0 8px;

    @media (min-width: 1025px) {
        padding-left: 5px;
        padding-right: 5px;

        .home-layout-2 &,
        .home-layout-3 &,
        .home-layout-4 & {
            padding-left: 15px;
            padding-right: 15px;
        }
    }
}

components/stencil/heroCarousel/heroCaro:
// HERO CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the hero carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display hero images.
//
// 1. Use object-fit on the image tag to keep aspect ratio and cover the space
//    created by the outer div. The JS will detect browsers that do not support
//    object-fit and instead copy the image src to a the background-image of the
//    wrapper div and add the compat-object-fit class.
//
// 3. Allows image to scale on large screens while preventing the top and bottom
//    from becoming cut off.
// -----------------------------------------------------------------------------

[data-page-type="default"] {
    .body {
        margin-top: 15px;
    }

    .heroCarousel {
        margin-top: 0;
        margin-bottom: 35px;
    }

    &.home-layout-2 {
        .heroCarousel {
            margin-bottom: 0;
        }
    }

    &.home-layout-4 {
        .heroCarousel {
            margin-bottom: 0;
        }
    }
}

[data-page-type="page"] {
    .body {
        margin-top: 15px;
    }

    .heroCarousel {
        margin-top: 0;
        margin-bottom: 7px;
    }
}

.heroCarousel {
    width: 1px;
    min-width: 100%;
    margin-bottom: 30px;
    margin-top: -(spacing("single")); // 3

    @include breakpoint("medium") {
        margin-top: -(spacing("single") + spacing("base")); // 3
    }

    &:not(.slick-initialized) :not(.heroCarousel-slide--first).heroCarousel-slide {
        display: none;
    }

    &:not(.slick-initialized) .heroCarousel-image-wrapper>.heroCarousel-image {
        opacity: 0;
    }

    &.slick-initialized .heroCarousel-image-wrapper>.heroCarousel-image {
        opacity: 1;
        animation-name: fadeIn;
        animation-duration: 0.8s;

        .home-layout-2 & {
            animation-name: animationRotate;
        }
    }

    a {
        text-decoration: none;
    }

    .slick-arrow {
        background-color: transparent;
        border-color: transparent;
        height: 50px;
        width: 30px;

        .home-layout-3 &,
        .home-layout-4 & {
            border: 1px solid $color-white;
        }

        &.slick-prev {
            left: 15px;

            &:before {
                @include carousel-arrow-color($color-white, 'prev');
                height: 20px;
                width: 10px;
            }
        }

        &.slick-next {
            right: 15px;

            &:before {
                @include carousel-arrow-color($color-white, 'next');
                height: 20px;
                width: 10px;
            }
        }

        &:before,
        &:after {
            transition: $anchor-transition;
        }

        &:after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            border-radius: 10px;
            opacity: .2;
            background-color: $color-textBase;
            border-color: $color-textBase;
            z-index: -1;

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                content: none;
            }
        }

        @media(min-width: 1025px) {
            &.slick-prev {
                pointer-events: none;
                opacity: 0;
                visibility: hidden;
                transform: translateX(-100%) translateY(-50%);
                transition: transform .3s ease;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    left: 0;
                    transform: translateX(0) translateY(-50%);
                }

                &:hover,
                &:active,
                &:focus {
                    &:before {
                        @include carousel-arrow-color($color-textBase, 'prev');
                    }

                    .home-layout-2 &,
                    .home-layout-3 &,
                    .home-layout-4 & {
                        background-color: $color-white;
                        border-color: $color-white;
                    }
                }
            }

            &.slick-next {
                pointer-events: none;
                opacity: 0;
                visibility: hidden;
                transform: translateX(100%) translateY(-50%);
                transition: transform .3s ease;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    right: 0;
                    transform: translateX(0) translateY(-50%);
                }

                &:hover,
                &:active,
                &:focus {
                    &:before {
                        @include carousel-arrow-color($color-textBase, 'next');
                    }

                    .home-layout-2 &,
                    .home-layout-3 &,
                    .home-layout-4 & {
                        background-color: $color-white;
                        border-color: $color-white;
                    }
                }
            }

            &:hover {
                &:after {
                    background-color: $color-white;
                    border-color: $color-white;
                    opacity: .5;

                    .home-layout-2 &,
                    .home-layout-3 &,
                    .home-layout-4 & {
                        content: none;
                    }
                }
            }
        }

        @media (max-width: 550px) {
            &.slick-prev {
                left: 32%;
            }

            &.slick-next {
                right: 32%;
            }

            &.slick-prev,
            &.slick-next {
                width: 24px;
                height: 40px;
                margin: 0;
                transition: all .3s ease;
                background-color: transparent;
                visibility: visible;
                opacity: 1;
                cursor: pointer;
                border: 0;
                padding: 8px;
                fill: #fff;
                stroke: #fff;
                top: initial;
                transform: translate(0);
                bottom: 15px;
                box-shadow: none;

                &:after {
                    background-color: transparent;
                }
            }

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                &.slick-prev {
                    left: 32%;
                }

                &.slick-next {
                    right: 32%;
                }
            }
        }
    }

    @media(min-width: 1025px) {
        &:hover {
            .slick-arrow {

                &.slick-prev,
                &.slick-next {
                    pointer-events: auto;
                    opacity: 1;
                    visibility: visible;
                    transform: translateX(0) translateY(-50%);
                }

                &.slick-prev {

                    .home-layout-2 &,
                    .home-layout-3 &,
                    .home-layout-4 & {
                        transform: translateX(30px) translateY(-50%);
                    }
                }

                &.slick-next {

                    .home-layout-2 &,
                    .home-layout-3 &,
                    .home-layout-4 & {
                        transform: translateX(-30px) translateY(-50%);
                    }
                }
            }
        }
    }

    .slick-next,
    .slick-prev {
        top: 50%;
        transform: translateY(-50%);
        margin: 0;
    }

    .slick-next {
        right: 15px;

        @include breakpoint("medium") {
            right: 25px;
        }
    }

    .slick-prev {
        left: 15px;

        @include breakpoint("medium") {
            left: 25px;
        }
    }

    .slick-dots {
        bottom: 40px;
        height: 20px;
        width: auto;
        left: 50%;
        transform: translateX(-50%);
        border-radius: 5px;
        @include carouselOpaqueBackgrounds($slick-dot-bgColor);

        .home-layout-2 & {
            background-color: transparent;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        li {
            height: 20px;
            width: 24px;
            padding: 4px 6px;

            &:first-child,
            &:last-child {
                width: 20px;
                padding: 4px;

                @media(min-width: 551px) {

                    .home-layout-2 & {
                        width: 60px;
                    }
                }
            }

            button {
                width: 12px;
                height: 12px;
                display: block;
                margin: 0 auto;

                &:before {
                    width: 12px;
                    height: 12px;

                    .home-layout-4 & {
                        background: transparent;
                    }
                }

                @media(min-width: 551px) {
                    top: -8px;

                    .carousel-tooltip {
                        display: block;
                    }

                    span {
                        display: none;
                    }
                }
            }

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                @media(min-width: 551px) {
                    button {
                        border: 1px solid $color-white;
                    }

                    &.slick-active {
                        button {
                            border: 1px solid $color-white;

                            &:before {
                                background: $color-white;
                            }
                        }
                    }
                }

                @media(min-width: 1025px) {
                    button {
                        &:hover {
                            &:before {
                                background: $color-white;
                            }
                        }
                    }
                }
            }
        }

        @media (min-width: 551px) and (max-width: 767px) {
            display: none !important;
        }

        @media (max-width: 550px) {
            position: absolute;
            top: inherit;
            left: 49%;
            bottom: 30px;
            margin: 0;
            padding: 0;
            width: 30px;
            background-color: transparent !important;

            &:before {
                content: '';
                position: absolute;
                bottom: -13px;
                height: 20px;
                width: 190px;
                height: 36px;
                transform: translateX(-50%);
                border-radius: 5px;
                background-color: rgba(0, 0, 0, 0.3);
            }

            li {
                width: auto;
                height: auto;
                padding: 0;
                text-align: center;
                width: 100%;
                display: none;
                width: 20px;
                padding: 4px;

                button {
                    font-size: $fontSize-root;
                    font-weight: $fontWeight-normal;
                    letter-spacing: $letterSpacing-base;
                    line-height: 24px;
                    color: $color-white;
                    border: 0;
                    border-radius: 0;
                    width: 30px;
                    height: auto;
                    position: relative;
                    transition: $anchor-transition;
                    border-bottom: 1px solid transparent;

                    span {
                        width: 10px;
                        display: inline-block;
                    }

                    &:before {
                        display: none;
                    }
                }

                &.slick-active {
                    display: block;

                    button {
                        color: $color-white;
                    }
                }
            }
        }
    }

    .slick-slide {
        &.is-square-image-type {
            .heroCarousel-image-wrapper {
                height: 100vw;
            }
        }

        &.is-vertical-image-type {
            .heroCarousel-image-wrapper {
                height: 110vw;
            }
        }

        &.is-square-image-type,
        &.is-vertical-image-type {
            .heroCarousel-image-wrapper {
                @include breakpoint("small") {
                    height: 56.25vw;
                }
            }
        }

        &.is-image-error {
            background: url("../img/hero-carousel-image-load-error.svg") center center no-repeat;
            background-size: contain;
        }
    }

    .slick-slide {

        .heroCarousel-subtitle,
        .heroCarousel-title,
        .heroCarousel-description,
        .heroCarousel-action {
            visibility: hidden;
            animation-delay: 0ms;
            animation-name: fadeOutDown;
        }
    }

    .slick-current {
        .heroCarousel-subtitle {
            visibility: visible;
            animation-delay: 50ms;
            animation-name: fadeInUp;
        }

        .heroCarousel-title {
            visibility: visible;
            animation-delay: 100ms;
            animation-name: fadeInUp;
        }

        .heroCarousel-description {
            visibility: visible;
            animation-delay: 150ms;
            animation-name: fadeInUp;
        }

        .heroCarousel-action {
            visibility: visible;
            animation-delay: 200ms;
            animation-name: fadeInUp;
        }
    }
}

.heroCarousel-slide {
    position: relative;

    a {
        text-decoration: none;
    }

    .heroCarousel-image {
        @include lazy-loaded-img;
        border: 0;
        width: 100%;
        max-height: 100%;
        margin: 0 auto;

        @media(max-width: 550px) {
            &:before {
                content: "";
                width: 100%;
                height: 100%;
                top: 0;
                right: 0;
                left: auto;
                z-index: 2;
                transform: none;
                position: absolute;
                transition: $anchor-transition;
                background-color: rgba(0, 0, 0, .6);
                background: linear-gradient(to bottom, transparent 0, rgba(0, 0, 0, .6) 100%);

                .home-layout-3 & {
                    content: none;
                }
            }
        }
    }

    &.stretch {
        .heroCarousel-image {
            object-fit: cover;
            object-position: 50% 50%;
        }

        &.compat-object-fit {

            // 1
            .heroCarousel-image {
                width: 100%;
                height: 100%;
            }
        }
    }

    &.compat-object-fit {
        // 1
        overflow: hidden;

        .heroCarousel-image {
            width: auto;
        }
    }

    .heroCarousel-image-wrapper {
        position: relative;
        display: block;
        overflow: hidden;
        background: $color-custom-loading;

        &:after {
            content: '';
            display: block;
            height: 0;
            width: 100%;
            padding-bottom: 43.5276836158192%;

            .home-layout-4 & {
                padding-bottom: 41.66667%;
            }
        }

        .heroCarousel-image-mobile {
            display: none;
            @include lazy-loaded-img;
            border: 0;
            width: 100%;
            max-height: 100%;
            margin: 0 auto;
        }

        @media(min-width: 551px) and (max-width: 1024px) {
            .home-layout-4 & {
                padding-bottom: 50%;

                img {
                    object-fit: cover;
                    height: 100%;
                }
            }
        }

        @media(max-width: 550px) {
            .heroCarousel-image-mobile {
                display: block;

                +.heroCarousel-image {
                    display: none;
                    width: 1px;
                    height: 1px;
                }
            }

            &:after {
                padding-bottom: 130.33898%;

                .home-layout-4 & {
                    padding-bottom: 200%;
                }
            }
        }
    }
}

.heroCarousel-image {
    img {
        display: none;

        &:not(.image-mobile) {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate3d(-50%, -50%, 0);
        }

        &.image-mobile {
            display: block;
        }

        &.image-noMobile {
            min-height: 400px;
            object-fit: cover;
        }

        &.lazyload,
        &.lazyloading {
            width: auto;
            height: auto;
            max-height: 100vh;
            margin: 0 auto;
            object-fit: scale-down;

            @media(max-width: 550px) {
                max-height: 100%;
                height: 100%;
            }
        }
    }

    @media (min-width: 551px) {
        img {
            display: block;
            width: 100%;

            &.lazyload,
            &.lazyloading {
                width: auto;
            }

            &.image-mobile {
                display: none;
            }
        }
    }

    @media (max-width: 550px) {
        img {
            &:not(.image-mobile) {
                @include lazy-loaded-img;
                border: 0;
                object-fit: contain;
                width: 1px;
                height: 1px;
            }
        }
    }
}

.heroCarousel-video {
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 0;
    padding-bottom: 56.75%;
    -ms-transform: translateY(-50%);
    transform: translateY(-50%);
    opacity: 0;
    visibility: hidden;
    transition: opacity .3s ease;

    .slick-active & {
        visibility: visible;
    }

    .slick-slider--playvideo & {
        opacity: 1;
        visibility: visible;
    }

    iframe {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        width: 100%;
        height: 100%;
    }
}

.heroCarousel-content {
    position: absolute;
    background-color: stencilColor('carousel-bgColor');
    top: 50%;
    left: 0;
    right: 0;
    z-index: 3;
    width: 100%;
    max-width: 500px;
    text-align: center;
    padding: 0 15px;
    transform: translateY(-50%);
    white-space: normal;
    margin: 0 auto;
    padding-bottom: 8px;

    &.heroCarousel-content--notEmpty {
        padding: 15px;
        max-width: 500px;
    }

    @media (min-width: 768px) {
        right: unset;
        left: 50%;
        transform: translate(-50%, -50%);
    }

    @media (min-width: 992px) {
        padding-left: 0;
        padding-right: 0;

        &.heroCarousel-content--empty {
            background-color: transparent;
        }
    }

    @media (min-width: 1025px) {
        max-width: 430px;

        .home-layout-2 & {
            max-width: 480px;
        }

        .home-layout-4 & {
            max-width: 520px;
            top: 60%;
        }

        &.heroCarousel-content--left {
            left: 100px;
            text-align: center;
            transform: translateY(-50%);

            .heroCarousel-countdown {
                justify-content: flex-start;
            }

            .home-layout-2 & {
                top: auto;
                bottom: 130px;
                transform: unset;
                text-align: left;
            }
        }

        &.heroCarousel-content--center {
            left: 50%;
            transform: translate(-50%, -50%);

            .home-layout-2 & {
                top: auto;
                bottom: 130px;
                transform: translate(-50%, 0);
            }
        }

        &.heroCarousel-content--right {
            left: auto;
            right: 100px;
            text-align: center;
            transform: translateY(-50%);

            .heroCarousel-countdown {
                justify-content: flex-end;
            }

            .home-layout-2 & {
                top: auto;
                bottom: 130px;
                transform: unset;
                text-align: right;
            }
        }
    }

    @media (min-width: 1600px) {
        &.heroCarousel-content--left {
            left: calc((100% - 1470px)/ 2);
        }

        &.heroCarousel-content--right {
            right: calc((100% - 1470px)/ 2);
        }
    }

    @media (min-width: 1920px) {
        &.heroCarousel-content--left {
            left: calc((100% - 1570px)/ 2);

            .home-layout-2 & {
                left: 160px;
            }
        }

        &.heroCarousel-content--right {
            right: calc((100% - 1570px)/ 2);

            .home-layout-2 & {
                right: 160px;
            }
        }
    }
}

.heroCarousel-subtitle {
    display: inline-block;
    vertical-align: top;
    width: 100%;
    color: $carousel-title-color;
    font-size: $fontSize-root + 2px;
    margin: 0 0 10px 0;
    position: relative;
    text-transform: capitalize;
    letter-spacing: .02rem;

    .home-layout-2 & {
        font-size: $fontSize-root;
    }

    .home-layout-4 & {
        margin-bottom: 3px;
    }

    @media (min-width: 381px) {
        margin-bottom: 15px;
    }

    @media (min-width: 1025px) {
        margin-bottom: 13px;

        .home-layout-2 & {
            margin-bottom: 5px;
        }
    }
}

.heroCarousel-title {
    display: inline-block;
    vertical-align: top;
    width: 100%;
    font-size: $fontSize-hero - 12px;
    font-weight: $fontWeight-bold;
    color: $carousel-title-color;
    letter-spacing: 0;
    line-height: 1.2;
    margin: 0 0 10px 0;

    .home-layout-2 & {
        font-weight: $fontWeight-medium;
    }

    .home-layout-4 & {
        font-size: 30px;
        font-weight: $fontWeight-normal;
        margin-bottom: 80px;

        &:before {
            content: "";
            display: block;
            position: absolute;
            top: calc(100% + 5px);
            left: 50%;
            transform: translate(-50%);
            width: 4px;
            height: 60px;
            background-image: url('../img/border-white.png');
            background-position: center;
            background-repeat: repeat-y;
            background-size: contain;
        }
    }

    @media (min-width: 321px) {
        font-size: 30px;
    }

    @media (min-width: 381px) {
        margin-bottom: 15px;
    }

    @media (min-width: 768px) {
        .home-layout-4 & {
            font-size: 50px;
        }
    }

    @media (min-width: 1025px) {
        font-size: $fontSize-hero;
        margin-bottom: 20px;

        .home-layout-2 & {
            margin-bottom: 27px;
        }
    }

    @media (min-width: 1600px) {
        .home-layout-4 & {
            font-size: 80px;
            margin-bottom: 143px;

            &:before {
                height: 115px;
            }
        }
    }
}

.heroCarousel-description {
    display: inline-block;
    vertical-align: top;
    margin: 0 0 10px 0;
    line-height: 28px;
    color: $carousel-description-color;

    .home-layout-4 & {
        line-height: 26px;
    }

    @media (min-width: 381px) {
        margin-bottom: 15px;
    }

    @media (min-width: 1025px) {
        margin-bottom: 22px;

        .home-layout-2 & {
            margin-bottom: 16px;
        }

        .home-layout-4 & {
            margin-bottom: 10px;
        }
    }
}

.heroCarousel-customLink {
    display: block;
    font-size: 0;
    letter-spacing: 0;
    margin: 0 0 10px 0;
    padding: 0;

    li {
        display: inline-block;
        vertical-align: top;
        font-size: $fontSize-root;

        a {
            display: block;
            padding: 10px;
            color: $carousel-description-color;
            line-height: $lineHeight-large;
            transition: unset;
        }

        span {
            display: inline-block;
            vertical-align: top;
            position: relative;

            &:before {
                content: '';
                position: absolute;
                left: 0;
                width: 100%;
                bottom: -1px;
                height: 1px;
                display: block;
                background: $carousel-description-color;
            }
        }
    }

    @media (min-width: 551px) {
        li {
            a {
                padding: 0px 24px 15px 24px;
            }

            &:first-child {
                a {
                    padding-left: 0;
                }
            }
        }
    }

    @media (min-width: 1025px) {
        li {
            &:hover {
                a {
                    color: $color-textLink-hover;
                }

                span {
                    &:before {
                        background: $color-textLink-hover;
                    }
                }
            }
        }
    }

    @media (min-width: 1600px) {
        padding-bottom: 12px;
    }
}

.heroCarousel-action {
    margin: 10px 0 0 0;

    .button {
        min-width: 210px;

        .home-layout-2 & {
            font-size: $fontSize-root + 2;
            font-weight: $fontWeight-medium;
            text-transform: capitalize;
            text-align: left;
            padding: 0;
            background-color: transparent;
            color: $buttonStyle-primary-color;
            border-color: $buttonStyle-primary-color;
            border-width: 0 0 1px 0;
            min-width: auto;
            line-height: 28px;
            height: 30px;
            border-radius: 0;
        }

        .home-layout-4 & {
            min-width: 200px;
            border-color: $buttonStyle-primary-backgroundColor;

            @media (min-width: 1025px) {
                &:hover {
                    border-color: $buttonStyle-primary-backgroundColorHover;
                }
            }
        }

        &.button--custom {
            height: 55px;
            line-height: 52px;
            font-weight: 700;

            .home-layout-2 & {
                &.button--custom {
                    font-size: $fontSize-root + 2;
                    font-weight: $fontWeight-medium;
                    text-transform: capitalize;
                    text-align: left;
                    padding: 0;
                    background-color: transparent;
                    color: $buttonStyle-primary-color;
                    border-color: $buttonStyle-primary-color;
                    border-width: 0 0 1px 0;
                    min-width: auto;
                    line-height: 28px;
                    height: 30px;
                    border-radius: 0;
                }
            }

            .home-layout-4 & {
                height: 50px;
                line-height: 48px;
                border-color: $buttonStyle-primary-backgroundColor;

                @media (min-width: 1025px) {
                    &:hover {
                        border-color: $buttonStyle-primary-backgroundColorHover;
                    }
                }
            }
        }

        @media (max-width: 550px) {
            display: block;
            max-width: 240px;
            margin: 0 auto;

            .home-layout-2 & {
                display: inline-block;
            }

            +.button {
                margin-top: 10px;
                margin-left: auto;

                .home-layout-2 & {
                    margin-top: 0;
                    margin-left: 10px;
                }
            }
        }
    }

    @media (min-width: 551px) {
        margin: 18px 0 0 0;

        .button {
            +.button {
                margin-left: 5px;
                margin-top: 0;

                .home-layout-2 & {
                    margin-left: 45px;
                }
            }
        }
    }
}

