    text-transform: capitalize;
}

.productView-brand {
    font-family: $fontFamily-sans;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    line-height: 25px;
    letter-spacing: $letterSpacing-base;
    margin: 0 0 5px 0;

    a {
        color: $productView-brand-link-color;
        transition: unset;

        span {
            color: $productView-brand-link-color;
            background-image: linear-gradient(transparent 97%, $productView-brand-link-color--hover 3%);
            background-repeat: repeat-x;
            background-position-y: -3px;

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                background-position-y: 0;
            }
        }
    }

    @media (min-width: 992px) {
        position: relative;
        margin-top: -10px;
    }
}

.productView-topInfo {
    display: block;
    font-size: 0;
    letter-spacing: 0;
    margin-bottom: 10px;
    @include clearfix;

    .productView-rating {
        display: inline-block;
        vertical-align: top;

        .icon {
            width: 16px;
            height: 16px;

            +.icon {
                margin-left: 2px;
            }
        }

        .productView-reviewLink {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 25px;
            letter-spacing: $letterSpacing-base;
            color: $color-textBase;
            display: inline-block;
            vertical-align: middle;
            margin: 0 0 0 10px;
            text-transform: capitalize;

            a {
                display: block;
                color: inherit;
                transition: unset;

                span {
                    padding: 0 0 1px 0;
                }

                &.reviewLink-pc {
                    display: none;
                }

                @media(min-width: 992px) {
                    &.reviewLink-pc {
                        display: block;
                    }

                    &.reviewLink-mb {
                        display: none;
                    }
                }
            }

            +.productView-reviewLink {
                margin-left: 25px;

                .home-layout-3 & {
                    margin-left: 20px;
                }
            }
        }
    }

    .productView-soldProduct {
        display: block;
        margin: 10px 0 0 0;

        .icon {
            width: 16px;
            height: 16px;
            top: -5px;
            position: relative;
            fill: stencilColor('sale_price_color');
        }

        .text {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 28px;
            letter-spacing: $letterSpacing-base;
            color: stencilColor('sale_price_color');
            margin: 0 0 0 8px;
        }
    }

    @media (min-width: 551px) {
        .productView-soldProduct {
            display: inline-block;
            vertical-align: top;
            margin-left: 25px;
            margin-top: 0;
        }
    }

    @media (min-width: 1025px) {
        .productView-rating {
            .productView-reviewLink {
                a {
                    &:hover {
                        color: $color-textLink-hover;

                        span {
                            background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                            background-repeat: repeat-x;
                            background-position-y: -3px;

                            .home-layout-2 &,
                            .home-layout-3 &,
                            .home-layout-4 & {
                                background-position-y: 0;
                            }
                        }
                    }
                }
            }
        }
    }

    @media (min-width: 992px) and (max-width: 1199px) {
        .productView-soldProduct {
            display: block;
            margin-top: 10px;
            margin-left: 0;
        }
    }
}

.productView-info {
    display: table;
    margin: 8px 0 12px 0;

    &-item {
        display: block;
        margin: 0;

        span {
            display: inline-block;
            vertical-align: top;
            margin: 0;
            padding: 0;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 28px;
            letter-spacing: $letterSpacing-base;

            &.productView-info-name {
                color: stencilColor('color-textHeading');
                min-width: 100px;
                font-weight: $fontWeight-medium;
            }

            &.productView-info-value {
                color: $color-textBase;
                text-transform: capitalize;
                padding-left: 20px;
            }
        }
    }
}

.productView-desShort {
    margin-bottom: 8px;

    span {
        color: $color-textSecondary;
        line-height: 26px;
        font-size: stencilNumber("fontSize-root");
        font-weight: fontWeight("normal");
        margin-bottom: 0;
    }
}

.productView-countDown {
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    color: $color-textBase;
    letter-spacing: $letterSpacing-base;
    line-height: 25px;
    margin: 6px 0 8px 0;
    text-align: left;
    display: inline-block;
    vertical-align: top;

    .icon {
        display: none;
        margin: 0 5px 0 0;
        fill: $color-textBase;
    }

    .text {
        display: block;
        letter-spacing: $letterSpacing-base;

        span {
            padding: 0 1px 0 0;
            background-image: linear-gradient(transparent 97%, #4c4c4c 3%);
            background-repeat: repeat-x;
            background-position-y: -3px;
            display: block;

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                background-position-y: 0;
            }
        }
    }

    .num {
        color: stencilColor('color-textBase');
        font-weight: $fontWeight-medium;
        text-transform: uppercase;
        letter-spacing: 0;
        font-size: $fontSize-root;
    }

    @media (min-width: 321px) {
        .icon {
            vertical-align: middle;
        }

        .text {
            display: inline-block;
            margin-right: 5px;
        }
    }
}

.productView-price {
    display: block;
    font-size: 0;
    letter-spacing: 0;
    margin: 0 0 4px 0;
    position: relative;

    .price-section {
        font-size: $fontSize-root + 4;
        font-weight: $fontWeight-medium;
        line-height: 25px;
        color: $color-textSecondary;

        &.price-section--withoutTax {
            color: stencilColor('sale_price_color');
        }

        &.rrp-price--withoutTax {
            margin: 0 0 5px 0;
            color: #969696;
            font-size: 16px;
        }

        &.non-sale-price--withoutTax {
            display: inline-block;
            vertical-align: bottom;
            color: #969696;
            margin: 0 13px 0 0;
            font-size: 16px;

            +.price-section--withoutTax {
                display: inline-block;
                vertical-align: bottom;
            }

            &[style="display: none;"] {
                +.price-section {
                    color: stencilColor('color-textHeading');
                }
            }
        }

        &.price-none {
            margin: 0;

            +.price-section--withoutTax {
                margin-left: 13px;
                color: stencilColor('price_color');
            }

            &[style="display: none;"] {
                +.price-section--withoutTax {
                    margin-left: 0;
                }
            }
        }
    }



    >p {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        font-style: italic;
        line-height: 1.75;
        letter-spacing: $letterSpacing-base;
        color: $color-textSecondary;
        margin-bottom: 0;
    }

    .price-section--saving {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        font-style: italic;
        line-height: 25px;
        letter-spacing: 0;
        color: $color-custom-greyDarker;
        margin: 4px 0 0 0;
    }
}

.productView-shortDesc {
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    line-height: 1.75;
    color: $color-textSecondary;
    margin-bottom: 20px;
}

.productView-messages {
    display: block;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    color: $color-textSecondary;
    letter-spacing: $letterSpacing-base;
    line-height: 1.75;
    margin: 5px 0;
    text-align: left;

    a {
        display: inline-block;
        vertical-align: top;
        font-size: $fontSize-root;
        font-style: italic;
        line-height: 1.75;
        color: $color-textSecondary;
        transition: unset;

        span {
            padding: 0 1px 0 0;
        }
    }

    @media (min-width: 1025px) {
        a {
            &:hover {
                color: $color-textLink-hover;

                span {
                    background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                    background-repeat: repeat-x;
                    background-position-y: 0;
                }
            }
        }
    }
}

// Details - Product options
// -----------------------------------------------------------------------------
//
// 1. Align the product options list item radios vertically.
// 2. Resetting font due to inline-block whitespace issue.
// 3. File upload button does not properly display in IE if font-size is 0
//
// -----------------------------------------------------------------------------

.productView-options {
    position: relative;
    width: auto;
    margin: 0 0 12px 0;
    @include clearfix;

    @media(max-width: 550px) {
        &.has-productOptions {
            padding-top: 30px;
        }
    }

    .alertBox {
        -ms-flex-pack: start;
        justify-content: flex-start;

        .alertBox-icon {
            padding: 0 15px 0 0;
            line-height: 24px;
        }
    }

    .productView-releaseDate {
        font-size: $fontSize-root;
        color: $color-textBase;
        line-height: 28px;
        margin: 0 0 28px 0;
    }

    .productView-optionsStock {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        line-height: 24px;
        letter-spacing: $letterSpacing-base;
        color: stencilColor('sale_price_color');
        font-style: italic;
        margin: 5px 0 5px 0;
    }

    .productView-sizeChart {
        display: inline-block;
        vertical-align: top;
        margin: 7px 25px 10px 0;
        @include clearfix;

        &-link {
            display: flex;
            align-items: center;
            transition: unset;

            svg {
                width: 28px;
                height: 17px;
                fill: $color-textSecondary;
                margin-right: 10px;
            }

            .text {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                font-style: normal;
                line-height: 24px;
                letter-spacing: $letterSpacing-base;
                text-transform: capitalize;
                color: $color-textSecondary;
                transition: unset;
            }
        }
    }

    .halo-compareColors {
        display: inline-block;
        vertical-align: top;
        margin: 7px 20px 10px 0;
        @include clearfix;

        &-link {
            display: flex;
            align-items: center;

            .icon {
                display: inherit;
                width: 18px;
                height: 18px;
                margin: 0 10px 0 0;

                img {
                    width: 100%;
                    height: 100%;
                }
            }

            .text {
                color: $color-textSecondary;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                line-height: 24px;
                letter-spacing: $letterSpacing-base;
                text-transform: capitalize;
            }
        }
    }

    .productView-askAnExpert {
        display: inline-block;
        vertical-align: top;
        margin: 7px 0 10px 0;
        @include clearfix;

        &-link {
            display: flex;
            align-items: center;
            transition: unset;

            svg {
                width: 20px;
                height: 20px;
                fill: $color-textSecondary;
                margin-right: 10px;
            }

            .text {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                font-style: normal;
                line-height: 24px;
                letter-spacing: $letterSpacing-base;
                text-transform: capitalize;
                color: $color-textSecondary;
                transition: unset;
            }
        }

        @media (max-width: 1024px) {
            padding-right: 20px;
        }
    }

    .form {
        display: block;
        position: relative;
        width: auto;
        margin: 0;
        padding: 0;

        .form-field {
            display: block;
            letter-spacing: 0;
            font-size: 0;
            margin: 0;
            padding-bottom: 10px;

            &.form-field--increments {
                padding-bottom: 0;
                padding-right: 0;
            }

            .form-label {
                display: flex;
                align-items: center;
                font-family: $fontFamily-sans;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 24px;
                color: stencilColor('color-textBase');
                margin-bottom: 12px;

                small {
                    display: block;
                    color: #f1152f;
                    line-height: inherit;
                    margin: 0 0 0 3px;
                    float: none;
                }

                [data-option-value] {
                    font-size: $fontSize-root;
                    font-weight: $fontWeight-normal;
                    line-height: 24px;
                    letter-spacing: $letterSpacing-base;
                    color: $color-textBase;
                    text-transform: capitalize;
                    margin: 0 0 0 6px;
                    font-style: italic;
                }

                [data-product-stock] {
                    font-size: $fontSize-root;
                    font-weight: $fontWeight-normal;
                    line-height: 1.75;
                    letter-spacing: $letterSpacing-base;
                    color: $color-textSecondary;
                    text-transform: capitalize;
                    margin: 0 0 0 5px;
                }
            }

            &.form-field--stock {
                margin-top: 15px;

                .form-label {
                    margin-bottom: 0;
                }
            }

            +.form-field {
                margin-top: 15px;
            }
        }
    }

    .form-input[type="number"] {
        @include breakpoint("small") {
            width: grid-calc(2, $total-columns);
        }
    }

    input[type="file"] {
        font-size: $fontSize-root;
    }

    .shareLinkSocial {
        position: absolute;
        top: auto;
        left: auto;
        right: 0;
        bottom: 0;
        margin: 0;
        font-size: 0;
        letter-spacing: 0;

        .socialLinks {
            .icon {
                position: unset;
            }
        }

        .icon {
            fill: $color-textBase;
            width: 20px;
            height: 20px;
        }

        &__button {
            display: -ms-flexbox;
            display: flex;
            -ms-flex-align: center;
            align-items: center;
            -ms-flex-pack: center;
            justify-content: center;
            width: 50px;
            height: 50px;
            padding: 0;
        }

        &__popup {
            position: absolute;
            top: 100%;
            right: 0;
            z-index: 9;
            padding: 13px 20px 18px 20px;
            width: 326px;
            background-color: $color-white;
            box-shadow: 0px 1px 20px 0px rgba(0, 0, 0, 0.1);
            margin-top: 10px;

            .title {
                margin-bottom: 12px;
                font-size: $fontSize-root - 3px;
                font-weight: $fontWeight-medium;
                line-height: 24px;
                letter-spacing: $letterSpacing-base;
                color: stencilColor('color-textHeading');
                text-transform: uppercase;
            }

            .shareLinkSocial__link {
                position: relative;
                margin-bottom: 24px;

                input {
                    border: 1px solid #cbcbcb;
                    background-color: #f5f5f5;
                    padding: 11px 18px 10px;
                    display: block;
                    width: 100%;
                    color: #a0a0a0;
                    font-size: $fontSize-root - 1px;
                }

                .shareLinkSocial__close {
                    .icon {
                        width: 14px;
                        height: 14px;
                        position: absolute;
                        top: 1px;
                        right: 2px;
                        left: auto;
                        fill: $header-font-color;
                    }
                }
            }

            .shareLinkSocial__social {
                .title {
                    margin-bottom: 7px;
                }
            }
        }
    }

    .add-to-cart-wallet-buttons {
        margin-top: spacing("half");

        div[data-smart-button-container-id] {
            line-height: 0;
            margin-top: 6px;

            button,
            div {
                width: 100%;
            }
        }

        button.show-more-button {
            color: stencilColor("color-textSecondary");
            display: block;
            margin-top: spacing("quarter");
            padding: spacing("quarter") 0;
            text-align: center;
            text-decoration: underline;
            vertical-align: middle;
            width: 100%;

            &:hover {
                color: stencilColor("color-textSecondary--hover");
            }
        }
    }

    @media (max-width: 550px) {
        &.productView-options-2 {
            .productView-actions {
                .form-actionFix {
                    display: block;
                    position: fixed;
                    width: 100%;
                    z-index: 99;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    margin: 0 auto;
                    padding: 10px 15px;
                    background: $color-white;
                    border-top: 1px solid transparent;
                    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.06), 0 1px 2px 0 rgba(0, 0, 0, 0.1);

                    .form-action {
                        width: calc(100% - 55px);
                    }
                }
            }

            .form-wishlist {
                &[data-wishlist-add] {
                    position: fixed;
                    left: auto;
                    right: 15px;
                    bottom: 10px;
                    z-index: 99;

                    [data-dropdown-content] {
                        top: auto !important;
                        bottom: 66px;
                    }
                }

                &.form-wishlist-2 {
                    position: absolute;
                    top: auto;
                    left: auto;
                    right: 0;
                    bottom: 0;

                    [data-dropdown-content] {
                        top: calc(100% + 6px) !important;
                        bottom: auto;
                    }
                }
            }
        }
    }

    @media (min-width: 1025px) {
        .productView-releaseDate {
            margin-bottom: 20px;
        }

        .productView-sizeChart {
            .productView-sizeChartlink {
                &:hover {
                    .icon {
                        fill: $color-textLink-hover;
                    }

                    .text {
                        color: $color-textLink-hover;
                    }
                }
            }
        }
    }
}

[data-product-option-change] {
    [data-option-name="Size"] {
        position: relative;

        @media(min-width: 1600px) {
            .option-value-size {
                width: 50%;
            }
        }
    }
}

.itemProduct-options {
    position: relative;

    .itemProduct-options-wrapper {
        @media(max-width: 1024px) {
            display: block;
            white-space: nowrap;
            overflow: auto hidden;
            margin: 0 -10px;
            padding: 0 10px;

            &:before {
                content: '';
                display: block;
                position: absolute;
                top: 0;
                left: -10px;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 14px;
                height: 40px;
                z-index: 1;
                transform: rotate(180deg);
            }

            &:after {
                content: '';
                display: block;
                position: absolute;
                top: 0;
                right: -10px;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 50px;
                height: 40px;
                z-index: 1;
            }
        }
    }
}

// Product list (with images)
// -----------------------------------------------------------------------------

.productOptions-list {
    border: $productOptions-list-border;
    list-style: none;
    margin: 0;
    max-height: 400px;
    overflow-y: auto;

    &-item {
        padding: $productOptions-list-item-padding;

        .form-label {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            letter-spacing: $letterSpacing-base;
            line-height: 24px;
            text-transform: none;
            margin: 0 0 4px 0;
            padding-left: 30px;
        }

        +.productOptions-list-item {
            border-top: $productOptions-list-item-borderTop;
        }
    }

    .productOptions-list-item-figure,
    .productOptions-list-item-content {
        display: table-cell;
        vertical-align: middle;
    }

    .productOptions-list-item-figure {
        width: $productOptions-list-item-figure-width;
    }

    .productOptions-list-item-content {
        padding-left: $productOptions-list-item-content-paddingLeft;
    }
}

// Product Action
// -----------------------------------------------------------------------------

.productView-actions {
    display: flex;
    flex-wrap: wrap;
    align-items: flex-end;
    justify-content: flex-start;
    padding: 0 113px 0 0;
    margin: 10px 0 0 0;

    .form-field--increments {
        margin-bottom: 15px !important;
        margin-right: 15px !important;
        padding-right: 15px;

        .form-label {
            display: block;
        }

        .form-input--incrementTotal {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            font-style: normal;
            text-align: center;
            width: 105px;
            color: $color-textSecondary;
            border: 1px solid #cbcbcb;
            padding: 10px 30px;
            line-height: 1;
            max-width: 100%;

            .home-layout-3 & {
                min-height: 50px;
                height: 50px;
            }
        }
    }

    .form-actionFix {
        display: block;
        width: 100%;

        .form-action {
            width: 100%;
        }
    }

    .form-action {
        width: 100%;

        .button {
            width: 100%;
            text-transform: uppercase;

            &.button--buyItNow {
                max-width: 100%;
            }

            .home-layout-3 & {
                height: 50px;
                line-height: 48px;
            }

            @media(max-width: 550px) {
                max-width: 100%;
            }
        }

        &-text {
            font-size: $fontSize-root;
            font-weight: $fontWeight-medium;
            letter-spacing: $letterSpacing-base;
            width: 100%;
            height: 50px;
            line-height: 50px;
            display: inline-block;
            vertical-align: top;
            background: stencilColor("color_bg_product_soldOut_badges");
            color: stencilColor("color_text_product_soldOut_badges");
            border: 1px solid stencilColor("color_border_product_soldOut_badge");
            text-align: center;
            text-transform: uppercase;
            padding: 0 15px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }
    }

    +.productView-actions {
        margin-top: 15px;
        padding-right: 0;

        .form-action {
            width: 100%;
        }
    }

    &.productView-actions-2 {
        margin-top: 15px;
        display: block;
        padding-right: calc(50% + 5px);

        .form-action-text {
            height: 50px;
            line-height: 48px;
        }
    }

    @media (min-width: 551px) {
        flex-wrap: nowrap;

        .form-field--increments {
            margin-bottom: 0 !important;
        }

        .form-actionFix {
            width: calc(100% - 121px);

            &.form-actionFix2 {
                width: 100%;
            }
        }

        .form-action {
            width: calc(100% - 121px);

            &.form-action2 {
                width: 100%;
            }
        }
    }
}

.productAttributes-message {
    margin-top: 15px;
    margin-bottom: 15px;
}

// Wishlist Dropdown
// -----------------------------------------------------------------------------

.form-wishlist[data-wishlist-add] {
    position: absolute;
    top: auto;
    left: auto;
    right: 50px;
    bottom: 0;
    margin: 0;
    font-size: 0;
    letter-spacing: 0;

    .dropdown-menu-button {
        display: flex;
        align-items: center;
        justify-content: center;
        width: 50px;
        height: 50px;
        padding: 0;
        border-radius: 50%;
        background: $color-custom-greyLight;
        border-color: $color-custom-greyLight;

        .text {
            display: none;
        }

        .icon {
            display: inline-block;
            vertical-align: top;
            width: 23px;
            height: 23px;
            position: relative;
            top: 0;
            left: 0;
            z-index: 5;
            margin: 0;
            fill: stencilColor('color-textHeading');
        }

        &.is-open {
            background-color: stencilColor('color-textHeading');

            .icon {
                fill: $color-white;
            }
        }
    }

    [data-dropdown-content] {
        outline: 0;
        width: 100%;
        z-index: 5;
        text-align: left;
        white-space: normal;
        line-height: 22px;
        min-width: 235px;
        box-shadow: 0px 1px 20px 0px rgba(0, 0, 0, 0.1);
        top: calc(100% + 10px) !important;
        left: auto !important;
        right: 0 !important;
        margin: 0;
        padding: 0;
        background: $color-white;

        li {
            display: inline-block;
            vertical-align: middle;
            position: relative;
            width: 100%;
            padding: 4px 0 2px 0;
            line-height: unset;

            a,
            input {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                color: $color-textLink;
                line-height: 1.75;
                letter-spacing: $letterSpacing-base;
                border: 0;
                height: auto;
                padding: 5px 15px 6px 15px;
                text-align: center;
                width: 100%;
                text-transform: capitalize;
                background: transparent;
            }

            +li {
                border-top: 1px solid $color-custom-grey;
            }

            &:hover {

                a,
                input {
                    color: $color-textLink-hover;
                }
            }
        }
    }

    &.form-wishlist-2 {
        width: 40%;
        padding-left: 5px;

        .dropdown-menu-button {
            width: 100%;
            border-radius: 0;
            border: 1px solid $color-textLink;
            background: transparent !important;
            padding: 0 15px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;

            .text {
                display: block;
                position: relative;
                font-size: $fontSize-root;
                font-weight: $fontWeight-medium;
                letter-spacing: $letterSpacing-base;
                line-height: 25px;
                text-align: center;
                text-transform: uppercase;
                color: $color-textLink;
                text-overflow: ellipsis;
            }

            .icon {
                display: none;
            }
        }

        [data-dropdown-content] {
            min-width: calc(100% - 5px);
        }
    }

    &.form-wishlist-3,
    &.form-wishlist-4 {
        bottom: 65px;

        +.shareLinkSocial {
            bottom: 65px;
        }
    }

    @media (min-width: 1025px) {
        .dropdown-menu-button {

            &:hover,
            &:focus {
                background-color: stencilColor('color-textBase');

                .icon {
                    fill: $color-white;
                }
            }
        }

        &.form-wishlist-2 {
            .dropdown-menu-button {

                &:hover,
                &:focus {
                    background-color: $buttonStyle-primary-backgroundColorHover !important;
                    border-color: $buttonStyle-primary-backgroundColorHover;

                    .text {
                        color: $color-white;

                        &:before,
                        &:after {
                            background-color: $color-white;
                        }
                    }
                }
            }
        }
    }
}

// Product View Product
// -----------------------------------------------------------------------------

.productView-ViewingProduct {
    display: flex;
    margin: 0 0 16px 0;
    padding: 7px 0 0 0;
    white-space: nowrap;
    align-items: center;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    color: $color-textBase;
    line-height: 24px;

    .icon {
        width: 20px;
        height: 20px;
        margin: 0 10px 0 0;
        fill: $color-textBase;
    }
}

// Share products
// -----------------------------------------------------------------------------

.productView-share {
    .socialLinks {
        display: inline-block;
        vertical-align: middle;
    }

    .socialLinks-item {
        display: inline-block;
        vertical-align: top;
        margin: 0 15px 15px 0;
        width: 14px;
        height: 14px;

        .icon {
            display: block;
            width: 100%;
            height: 100%;

            svg {
                width: 100%;
                height: 100%;
                fill: $icon-color;
            }
        }

        &:last-child {
            margin-right: 0;
        }
    }
}

// Security Image
// -----------------------------------------------------------------------------

.productView-securityImage {
    display: block;
    margin: 10px 0 0 0;

    ul {
        margin: 0;
        font-size: 0;
        list-style: none;
        overflow-x: auto;
        overflow-y: hidden;
        white-space: nowrap;

        li {
            display: inline-block;
            vertical-align: middle;
            margin-right: 15px;
            margin-bottom: 5px;

            &:last-child {
                margin-right: 0;
            }
        }
    }

    img {
        max-height: 100px;

        &.lazyload,
        &.lazyloading {
            width: auto;
            height: auto;
            margin: auto;
            object-fit: contain;
        }
    }

    @media (min-width: 992px) {
        ul {
            li {
                margin-right: 20px;
                margin-bottom: 20px;
            }
        }
    }

    @media (min-width: 1025px) {
        ul {
            overflow: visible;
        }
    }
}


// Compare Color
// -----------------------------------------------------------------------------

.halo-compareColors {
    &-swatch {
        margin: 0 0 15px 0;

        .form-option {
            &-swatch {
                .form-option-variant {
                    background-size: cover !important;
                }
            }

            &.show-color {
                .form-option-variant {
                    &:after {
                        border-color: #cbcbcb;
                    }
                }
            }
        }
    }

    &-image {
        display: block;
        font-size: 0;
        letter-spacing: 0;
        position: relative;
        white-space: nowrap;
        overflow: auto;
        margin: 0 -15px;
        -webkit-overflow-scrolling: touch;

        .item {
            display: inline-block;
            vertical-align: top;
            width: 50%;
            padding: 0 15px;
            text-align: center;

            .color {
                display: block;
                width: 100%;
                height: 128px;
                margin: 0 0 6px 0;
                border: 0;
                border-radius: 6px;

                &.color2,
                &.color3 {
                    display: flex;
                    justify-content: space-between;
                    overflow: hidden;
                }
            }

            .color2,
            .color3 {
                border: 1px solid $color-custom-grey;
                width: 100%;
                height: 128px;
                margin: 0 0 6px 0;

                span {
                    display: block;
                    width: 100%;
                    height: 100%;
                    position: relative;
                }
            }

            .title {
                display: block;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 28px;
                text-align: center;
                color: $color-textBase;
                text-transform: unset;
                margin: 0 0 15px 0;
            }

            &.item-partern {
                .image {
                    display: block;
                    width: 100%;
                    margin: 0 0 6px 0;
                    border: 0;
                    border-radius: 6px;
                }

                img {
                    width: auto;
                }
            }
        }
    }

    &-text {
        display: block;
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        font-style: italic;
        color: $color-textSecondary;
        letter-spacing: $letterSpacing-base;
        line-height: 1.75;
        text-align: center;
        text-transform: capitalize;
        padding: 15px 0 0 0;
    }

    +.halo-compareColors {
        display: none !important;
    }

    @media (min-width: 551px) {
        &-image {
            .item {
                width: 33.33333333333%;
            }
        }
    }

    @media (min-width: 1025px) {
        &-swatch {
            margin-bottom: 20px;
        }

        &-image {
            .item {
                width: 130px;

                &.item-partern {
                    width: 16.6666667%;
                }
            }
        }
    }
}

// Next/Previous Product
// -----------------------------------------------------------------------------
.productView-nextProducts {
    display: none;

    .next-prev-icons {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }

    .prev-icon,
    .next-icon {
        display: flex;
        align-items: center;
        width: auto;
        padding: 0;
        margin: 0;
        transition: unset;

        .text {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 40px;
            letter-spacing: $letterSpacing-base;
            color: $color-textSecondary;
            display: none;


            span {
                padding: 0 0 1px 0;
            }
        }

        .icon {
            width: 20px;
            height: 20px;
            fill: stencilColor('color-textHeading');
        }

        &.disable {
            opacity: .25;
            cursor: no-drop;
        }
    }

    .prev-icon {
        .icon {
            margin-right: 10px;
        }
    }

    .next-icon {
        margin-left: 3px;

        .icon {
            margin-left: 10px;
        }
    }

    .next-prev-modal {
        display: none;
        position: absolute;
        top: 100%;
        left: auto;
        right: 0;
        z-index: 3;
        padding-top: 8px;

        &.is-active {
            display: block;
        }
    }

    .next-prev-content {
        display: none;
        width: 397px;
        padding: 0 35px 0 0;
        background-color: $color-white;
        border-radius: 0;
        border: 0;
        box-shadow: 0px 1px 20px 0px rgba(0, 0, 0, 0.1);

        .card {
            width: 100%;
            display: flex;
        }

        .card-image {
            width: 70px;
            position: relative;
        }

        .card-content {
            padding-top: 0;
            padding-left: 15px;
            width: calc(100% - 70px);
            display: flex;
            flex-direction: column;
            justify-content: center;
        }

        .card-title {
            top: unset;
            margin: 0;
            line-height: 24px;

            a {
                font-size: $fontSize-root;
            }

            .card-ellipsis {
                -webkit-line-clamp: stencilString('halo_card_title-line');
            }
        }

        &.is-show {
            display: block;
        }
    }

    @media (min-width: 1025px) {
        display: block;
        position: absolute;
        top: -85px;
        right: 0;
        z-index: 10;

        .next-prev-icons {
            >a {

                &:hover,
                &:active,
                &:focus {
                    .text {
                        span {
                            background-image: linear-gradient(transparent 97%, $color-textSecondary 3%);
                            background-repeat: repeat-x;
                            background-position-y: 0;
                        }
                    }
                }
            }
        }
    }

    @media (min-width: 1400px) {

        .prev-icon,
        .next-icon {
            .icon {
                width: 24px;
                height: 24px;
            }
        }
    }
}

// Notify Me: Out Of Stock
// -----------------------------------------------------------------------------
.productView-notifyMe {
    display: block;
    padding: 8px 0 24px 0;
    margin: 0;

    &-form {
        display: block;
        margin: 0;
        padding: 0;
    }

    &-text {
        display: none;
        margin: 20px 0 0 0;
        padding: 0;

        .alertBox {
            margin-bottom: 0;
        }
    }

    .form-field {
        display: flex;
        flex-wrap: wrap;
        margin-bottom: 0;

        +.form-field {
            margin-top: 11px;
        }
    }

    .form-field-checkbox {
        display: inline-block;
        vertical-align: top;
        position: relative;

        input[type="checkbox"] {
            +.form-label {
                position: relative;
                padding-left: 25px;
                margin-bottom: 0;
                font-weight: $fontWeight-normal;
            }
        }

        span {
            border-bottom: 1px solid $color-textLink;
            color: $color-textLink;
        }
    }

    .form-label {
        width: 100%;
        font-size: $fontSize-root;
        font-weight: normal;
        line-height: 24px;
        letter-spacing: $letterSpacing-base;
        color: $color-textBase;
        margin-bottom: 10px;
    }

    .form-input {
        font-style: normal;
        width: calc(100% - 185px);
        padding-left: 20px;
    }

    .button {
        width: 175px;
        margin: 0 0 0 10px;
        text-transform: uppercase;
    }
}

.productView-info-bottom {
    display: flex;
    align-items: center;
    justify-content: space-between;

    .productView-ViewingProduct {
        margin-bottom: 7px;
    }

    @media(max-width: 375px) {
        display: block;
    }
}

.social-mediaBlock {
    .at-icon-wrapper {
        svg {
            width: 18px !important;
            height: 18px !important;
            fill: $header-font-color !important;
        }
    }
}

.at-share-btn-elements .at-icon-wrapper:first-child {
    margin-left: -5px !important;
}

// Product Description
// -----------------------------------------------------------------------------
.productDescription {
    &__block {
        .title {
            margin-bottom: 18px;
        }

        .productDescription__content {
            >p {
                line-height: 26px;
                margin-bottom: 40px;
            }
        }
    }
}

.description-2col {
    margin-bottom: 42px;
}

.des2col-item {
    &:first-child {
        .des2colItem-img {
            margin: 0 15px 15px 0;
        }

        .des2colItem-content {
            margin: 0 0 15px 15px;
        }
    }

    &:last-child {
        .des2colItem-img {
            margin: 15px 0 0 15px;
        }

        .des2colItem-content {
            margin: 15px 15px 0 0;
        }
    }

    .des2colItem-img {
        width: 100%;
        display: flex;
        align-items: center;

        img {
            width: 100%;
        }
    }

    .des2colItem-content {
        width: 100%;
        position: relative;

        img {
            width: 100%;
        }

        .des2colItem-container {
            max-width: 100%;
            height: calc(100% - 60px);
            margin: 30px;
            padding: 65px;
            text-align: center;
            background-color: $color-white;
            display: flex;
            align-items: center;
            justify-content: center;
            flex-direction: column;
            text-align: center;
            position: absolute;
            top: 0;
            left: 0;
        }

        .des2colItem-featured {
            color: $color-textSecondary;
            font-size: 14px;
            font-weight: fontWeight("medium");
            margin-bottom: 10px;
            text-transform: uppercase;
            border-bottom: 1px solid $color-textBase;
            letter-spacing: 0.05rem;
            line-height: 2;
        }

        .des2colItem-title {
            font-size: 40px;
            color: $color-textSecondary;
            font-weight: 700;
            text-transform: capitalize;
            margin-top: 0;
            margin-bottom: 10px;
        }

        p+p {
            margin-top: 1.5rem;
        }

        .des2colItem-des {
            margin-bottom: 0;
            line-height: 26px;

            +p {
                margin-top: 1.5rem;
            }
        }
    }

    @media(min-width: 992px) {
        display: flex;

        .des2colItem-img {
            width: 50%;
        }

        .des2colItem-content {
            width: 50%;
        }
    }

    @media(max-width: 991px) {
        +.des2col-item {
            display: flex;
            flex-direction: column;

            .des2colItem-img {
                order: -1;
            }
        }

        .des2colItem-img {
            margin-left: 0 !important;
        }

        .des2colItem-content {
            margin-left: 0 !important;

            .des2colItem-container {
                height: calc(100% - 60px);
                margin: 30px;
                padding: 30px;
            }

            .des2colItem-title {
                font-size: 30px;
            }
        }
    }

    @media(max-width: 767px) {
        .des2colItem-content {
            margin-left: 0 !important;

            .des2colItem-container {
                height: calc(100% - 30px);
                margin: 15px;
                padding: 15px;
            }

            .des2colItem-title {
                font-size: 20px;
            }
        }
    }
}

.description-twoList,
.description-oneList {
    font-size: 0;
    margin: 0 auto;
}

.desOneList-item {
    margin-bottom: 32px;
    display: inline-block;
    vertical-align: top;
    font-size: stencilNumber("fontSize-root");

    .desOneList-title {
        color: $color-textSecondary;
        font-size: stencilNumber("fontSize-root");
        font-weight: fontWeight("semibold");
        margin-bottom: 12px;
    }

    .desOneList-content {
        line-height: 26px;
        margin-top: 13px;
    }
}

.desTwoList-item {
    margin-bottom: 0;
    width: 100%;
    display: inline-block;
    vertical-align: top;
    font-size: stencilNumber("fontSize-root");

    .desTwoList-title {
        color: $color-textSecondary;
        font-size: stencilNumber("fontSize-root");
        font-weight: fontWeight("semibold");
        margin-bottom: 0;
    }

    .desTwoList-list {
        margin-top: 8px;
        padding-bottom: 0;

        li {
            line-height: 35px;
        }
    }

    &.desTwoList-item--number {
        padding-left: 0;

        .desTwoList-list {
            list-style: auto;
        }
    }

    @media(min-width: 992px) {
        width: 50%;

        &.desTwoList-item--number {
            padding-left: 17px;
        }
    }
}

.description-blockQuote {
    margin: 0 auto 50px;
    padding: 10px 0;
    text-align: left;

    >* {
        margin-left: auto;
        margin-right: auto;
    }

    .desBlockQuote-title {
        margin-top: 0;
        margin-bottom: 22px;
        color: $color-textSecondary;
        font-size: stencilNumber("fontSize-root");
        font-weight: fontWeight("bold");
        font-family: $body-font-family;
        text-transform: capitalize;
    }

    .desBlockQuote-des {
        border: 1px solid $color-custom-grey;
        max-width: 100%;
        padding: 32px 38px 35px 38px;
    }
}

// Product Reviews
// -----------------------------------------------------------------------------
.productView-tab {
    .productReviews {
        display: block;
        border-top: none;

        .page-heading {
            margin: 0;
            font-size: 24px;
            font-weight: $fontWeight-bold;
            text-transform: capitalize;
        }

        .toggleLink-text {
            svg {
                fill: #323232;
            }
        }

        .toggle-title {
            position: relative;
            margin-bottom: 0;
            padding-bottom: 15px;
            border-bottom: 1px solid $color-custom-grey;

            .page-heading {
                display: inline-block;
                vertical-align: middle;
            }

            .number-review,
            .write-review {
                display: inline-block;
                vertical-align: middle;
                color: $color-textSecondary;
                font-size: 16px;
                text-align: center;
                height: 40px;
                line-height: 40px;
                border-radius: 50px;
                text-transform: capitalize;
            }

            .number-review {
                min-width: 116px;
                background-color: $color-custom-greyLight;
                margin-left: 13px;
            }

            .write-review {
                min-width: 163px;
                border: 1px solid $color-textBase;
                margin-left: 8px;
            }

            .toggleLink {
                line-height: 40px;

                .icon {
                    width: 13px;
                    height: 13px;
                    margin-right: 3px;
                    position: relative;
                    top: -2px;
                }

                &.is-open {
                    .icon {
                        transform: rotate(-180deg);
                    }
                }
            }

            .toggleLink-text {
                font-size: 16px;
                background-image: none;
                color: $color-textSecondary;
            }
        }

        .toggle-content {
            padding: 10px 0;
        }

        &.toggle:last-of-type {
            margin-bottom: 0;
        }

        @include breakpoint("small") {
            .page-heading {
                font-size: 24px;
            }
        }

        @media (min-width: 1025px) {
            margin-top: 30px;
            padding-top: 30px;

            &.productView-reviews-wrapper {
                margin-top: 0;
                padding-top: 0;
            }

            .toggle-title {
                padding-bottom: 25px;
            }

            .toggle-content {
                padding: 0;
            }
        }
    }
}

components/stencil/blocker/blocker.scss:
// =============================================================================
// BLOCKER (CSS)
//
// Purpose: Position .blocker element above another element to prevent user event
// =============================================================================


.blocker {
    background: rgba($body-bg, 0.5);
    bottom: 0;
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    z-index: zIndex("highest");
}

.blocker-container {
    position: relative;
}

components/stencil/compare/compare.scss:
// =============================================================================
// COMPARE (CSS)
// =============================================================================

.page-compare {
    .page-content {
        padding-top: 32px;
    }
}

// Compare table layout
// -----------------------------------------------------------------------------
//
// 1. Foundation Magellan plugin.
//
// -----------------------------------------------------------------------------

.compareTable {
    border: 1px solid $color-custom-grey;
    border-collapse: collapse;
    table-layout: fixed;
    width: 100%;
    margin: 0;

    @media (max-width: 1024px) {
        display: none;
    }
}

.compareText {
    display: block;
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    margin: 0;
    line-height: 1.75;
    letter-spacing: $letterSpacing-base;
    color: $color-textSecondary;

    @media (min-width: 801px) {
        display: none;
    }
}

.compareTable-row {
    vertical-align: top;

    .compareTable-item {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        line-height: 1.75;
        letter-spacing: $letterSpacing-base;
        color: $color-textSecondary;
    }

    +.compareTable-row {

        .compareTable-heading,
        .compareTable-item {
            border-top: container("border");
        }

        .compareTable-heading:empty {
            border-top: 0;
        }
    }
}


// Compare table headings
// -----------------------------------------------------------------------------

.compareTable-heading {
    font-size: $fontSize-root;
    font-weight: $fontWeight-medium;
    letter-spacing: $letterSpacing-base;
    color: $color-textBase;
    padding: 11px 15px;
    position: relative;
    text-align: center;
    width: 0;

    @media (min-width: 1025px) {
        width: grid-calc(3, $total-columns);
    }
}

.compareTable-headingText {
    white-space: nowrap;
}


// Compare table item
// -----------------------------------------------------------------------------

.compareTable-item {
    padding: (spacing("double") + spacing("sixth")) spacing("base") (spacing("base") + spacing("sixth"));

    @include breakpoint("medium") {
        padding-top: spacing("base") + spacing("sixth");
    }
}

// Compare action
// -----------------------------------------------------------------------------

.compareTable {

    th,
    td {
        border: 1px solid $color-custom-grey;
        border-spacing: 0px;
    }

    thead {
        text-align: center;
    }

    .compareTable-item.text-center,
    .compareTable-heading.text-center {
        width: 150px;
    }
}

.compareTable-product {
    padding: 2rem 1rem;

    .card {
        border: none;
        text-align: center;

        .card-img-container {
            .card-image {
                border: 0;
            }

            &:before {
                content: none;
            }

            &:after {
                padding-bottom: 0;
            }
        }

        .card-body {
            text-align: center;

            .card-brand {
                margin: 15px 0 5px 0;
                color: $color-textSecondary;
            }

            .card-figcaption-group {
                max-width: 270px;
                margin-top: 20px;
                margin-left: auto;
                margin-right: auto;

                span {
                    border-bottom: none;
                }
            }

            .card-action {
                padding-top: 0;
                padding-bottom: 0;
                line-height: 45px;

                span {

                    &:before,
                    &:after {
                        background-color: $color-white;
                        -webkit-transition: all .5s ease;
                        -moz-transition: all .5s ease;
                        -ms-transition: all .5s ease;
                        -o-transition: all .5s ease;
                        transition: all .5s ease;
                    }
                }

                @media (min-width: 1025px) {
                    &:hover {
                        span {

                            &:before,
                            &:after {
                                background-color: $color-textBase;
                            }
                        }
                    }
                }
            }

            .card-title {
                margin-bottom: 10px;
            }

            .card-price {
                justify-content: center;
            }
        }
    }

    .compareTable-removeProduct {
        position: absolute;
        top: 10px;
        right: 10px;
        width: 32px;
        height: 32px;
        padding: 10px;
        background-color: $buttonStyle-primary-backgroundColor;
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;

        .icon {
            display: block;
            margin: 0;
            width: 100%;
            height: 100%;
            fill: $buttonStyle-primary-color;
        }
    }

    @media (min-width: 1025px) {
        .compareTable-removeProduct {
            &:hover {
                background-color: $buttonStyle-primary-backgroundColorHover;

                .icon {
                    fill: $buttonStyle-primary-colorHover;
                }
            }
        }
    }
}

components/stencil/textTruncate/textTrun:
// =============================================================================
// TEXT TRUNCATE (CSS)
// =============================================================================


// Text Truncate
// -----------------------------------------------------------------------------
//
// -----------------------------------------------------------------------------
.textTruncate {
    padding-bottom: spacing("single");
    position: relative;
    transition: max-height 100ms ease-out;
}

.js .textTruncate {
    max-height: 110px;
    overflow: hidden;
}

.js .textTruncate--visible {
    max-height: remCalc(1000);
}

.textTruncate-viewMore {
    background-color: stencilColor("body-bg");
    bottom: 0;
    padding-top: 10px;
    position: absolute;
    width: 100%;
}

components/stencil/facetedSearch/faceted:
// =============================================================================
// FACET SEARCH (CSS)
// =============================================================================


// Faceted search toggle
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-toggle {
    @include grid-row;
    border: $facetedSearch-toggle-border;
    border-radius: $facetedSearch-toggle-borderRadius;
    border-width: $facetedSearch-toggle-borderWidth;
    display: none;
    margin-bottom: $facetedSearch-toggle-verticalSpacing;
    padding: $facetedSearch-toggle-padding;

    &.is-open {
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
        margin-bottom: 0;
    }
}

.facetedSearch-toggle-text,
.facetedSearch-toggle-indicator {
    display: block;
    font-weight: normal;
}

.facetedSearch-toggle-text {
    @include grid-column(7);
    padding: 0;
    text-align: left;
}

.facetedSearch-toggle-indicator {
    @include grid-column(5);
    font-weight: bold;
    padding: 0;
    text-align: right;
}


// Faceted search nav list
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-navList {
    display: none;

    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        display: block !important; // 1
    }

    &.is-open {
        display: block;

        // scss-lint:disable SelectorDepth, NestingDepth
        .accordion--navList,
        .accordion-block:first-child .accordion-navigation {
            border-top-left-radius: 0;
            border-top-right-radius: 0;
        }
    }

    .facetedSearch-toggle+&,
    .sidebarBlock+& {
        margin-top: $facetedSearch-toggle-verticalSpacing;
    }


    .facetedSearch-toggle+&.is-open,
    .sidebarBlock+&.is-open {
        @include breakpoint("medium") {
            margin-top: $facetedSearch-toggle-verticalSpacing;
        }
    }

    .navList--inner {
        margin-left: spacing("single");
    }
}

.facet-quick-heading {
    text-transform: capitalize;
}

.facetedSearch-optionColumns {
    column-count: 3;
    column-gap: 20px;
}


// Faceted search refine filters
// -----------------------------------------------------------------------------
//
// 1. Only show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-refineFilters {
    display: block;

    .sidebarBlock-heading {
        margin: 0 0 25px 0;
    }
}

// Facted search ratings
.facetedSearch-content--rating {
    .is-selected {
        font-weight: bold;
    }
}

.facetedSearch-noFilters {
    font-size: $fontSize-root;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    line-height: 1.5;
    color: $color-textSecondary;
    background-color: #fcf8e3;
    border-color: #faebcc;
    color: #8a6d3b;
    padding: 10px 30px;
    margin-bottom: 20px;
    border: 1px solid transparent;
    border-width: 0 0 1px;
}

#facetedSearch {
    padding: 0;

    @media (min-width: 1025px) {
        padding: 0;
    }
}

.facetedSearch-navList {
    display: block;
}

.navList.facetedSearch-optionColumns {
    margin: 15px 0 0 0;

    .navList-item {
        width: 100%;
    }

    @media (min-width: 768px) {
        margin-left: -15px;
        margin-right: -15px;
        font-size: 0;
        left: 0;
        display: block;

        .navList-item {
            display: inline-block;
            vertical-align: top;
            padding: 5px 15px;
        }
    }
}

components/stencil/toggleLink/toggleLink:
// =============================================================================
// TOGGLE LINK (CSS)
// =============================================================================


// Toggle link
// -----------------------------------------------------------------------------

$toggle-spacing: spacing("single")+spacing("half");

.toggle {
    border-top: container("border");
    margin-bottom: $toggle-spacing;

    &:last-of-type {
        margin-bottom: $toggle-spacing * 2;
    }
}

.toggle-title {
    font-family: $fontFamily-headings;
    font-size: $h4-font-size;
    font-weight: $fontWeight-normal;
    letter-spacing: $letterSpacing-base;
    line-height: 1;
    color: $color-textBase;
    padding: 0;
    margin: 10px 0 20px 0;
    text-transform: unset;
}

.toggleLink {
    color: stencilColor("color-textSecondary");

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }

    .toggle-title & {
        float: right;
        line-height: 24px;
    }

    &.toggleLink-viewMore {
        position: relative;
        display: inline-block;
        vertical-align: top;
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        font-style: italic;
        line-height: 1.5;
        letter-spacing: $letterSpacing-base;
        text-transform: unset;
        padding: 0;
        transition: unset;
        width: auto;
        color: $color-textLink;

        .toggleLink-text {
            padding: 0 0 1px 0;
            background-image: linear-gradient(transparent 97%, $color-textLink 3%);
            background-repeat: repeat-x;
            background-position-y: -3px;
            display: inline;
            font-size: inherit;
            font-weight: inherit;

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                background-position-y: 0;
            }
        }
    }

    &.toggleLink-clear {
        position: relative;
        display: inline-block;
        vertical-align: top;
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        font-style: italic;
        line-height: 1.5;
        letter-spacing: $letterSpacing-base;
        text-transform: unset;
        padding: 0;
        transition: unset;
        width: auto;
        color: $color-textLink;

        .toggleLink-text {
            padding: 0 0 1px 0;
            background-image: linear-gradient(transparent 97%, $color-textLink 3%);
            background-repeat: repeat-x;
            background-position-y: -3px;
            display: inline;
            font-size: inherit;
            font-weight: inherit;

            .home-layout-2 &,
            .home-layout-3 &,
            .home-layout-4 & {
                background-position-y: 0;
            }
        }
    }

    @media (min-width: 1025px) {
        &:hover {
            color: $color-textLink-hover;

            .toggleLink-text {
                background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    background-position-y: 0;
                }
            }
        }
    }
}

.toggleLink-text {
    font-size: fontSize("smallest");
    font-weight: fontWeight("normal");
}

.toggleLink-text--off {
    display: inline-block;

    .toggleLink.is-open & {
        display: none;
    }
}

.toggleLink-text--on {
    display: none;

    .toggleLink.is-open & {
        display: inline-block;
    }
}

.toggle-content {
    display: none;

    &.is-open {
        display: block;
    }
}

components/stencil/banners/banners.scss:
// =============================================================================
