            border-bottom: 1px solid $color-custom-grey;
        }
    }

    .previewCartItem-image {
        display: inline-block;
        vertical-align: top;
        width: 100px;
        padding: 0;
        position: relative;
        text-align: center;
        @include lazy-loaded-padding('productcartthumb_size');

        img {
            @include lazy-loaded-img;
        }
    }

    .previewCartItem-content {
        display: inline-block;
        vertical-align: top;
        width: calc(100% - 100px);
        padding: 0 0 0 22px;
        position: relative;
        top: 7px;
        text-align: left;
    }

    .previewCartItem-name {
        overflow-wrap: break-word;
        margin: 0 0 7px 0;
        position: relative;
        top: -5px;

        >a {
            font-family: $fontFamily-sans;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            color: $color-textBase;
            letter-spacing: $letterSpacing-base;
            margin: 0;
            line-height: 20px;
            text-transform: capitalize;
            overflow: hidden;
            display: -webkit-box;
            -webkit-line-clamp: stencilString('halo_card_title-line');
            text-overflow: ellipsis;

            @media (min-width: 1025px) {
                &:hover {
                    span {
                        background-image: linear-gradient(transparent 97%, $color-textBase 3%);
                        background-repeat: repeat-x;
                        background-position-y: -2px;

                        .home-layout-2 &,
                        .home-layout-3 &,
                        .home-layout-4 & {
                            background-position-y: 0;
                        }
                    }
                }
            }
        }
    }

    .previewCartItem-options {
        display: inline-block;
        vertical-align: top;
        position: relative;
        padding: 0 30px 0 0;
        width: auto;
    }

    .previewCartItem-variant {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        line-height: 20px;
        color: #616161;
        text-transform: capitalize;
        display: block;
        width: 100%;
        margin: 0 0 7px 0;

        span {
            +span {
                &:before {
                    content: '/';
                    margin: 0 4px 0 0;
                }
            }
        }
    }

    .previewCartItem-edit {
        display: inline-block;
        vertical-align: top;
        position: absolute;
        top: -5px;
        right: 0;

        .icon {
            width: 16px;
            height: 16px;
            fill: #888888;
        }

        @media(min-width: 1025px) {
            &:hover {
                .icon {
                    fill: $color-textBase;
                }
            }
        }
    }

    .previewCartItem-price {
        font-size: $fontSize-root;
        font-weight: $fontWeight-semibold;
        line-height: 20px;
        color: $color-textBase;
        letter-spacing: $letterSpacing-base;
        text-transform: uppercase;
        display: block;
        width: 100%;
        margin: 4px 0 0 0;

        p {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            color: $color-textSecondary;
            letter-spacing: $letterSpacing-base;
            line-height: 20px;
            margin: 0;
            text-transform: capitalize;
        }
    }

    .previewCartItem-qty {
        display: inline-block;
        vertical-align: top;
        width: 100%;
        max-width: 130px;
        margin: 17px 0 0 0;
        padding: 0;
        position: relative;

        .form-label {
            display: inline-block;
            vertical-align: middle;
            width: 100%;
            margin: 0;
            padding: 0;
            color: $color-textBase;
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;

            @media (max-width: 550px) {
                display: block;
                margin-bottom: 10px;
            }
        }

        .form-increment {
            display: inline-block;
            vertical-align: middle;
            width: calc(100% - 30px);

            .form-input--incrementTotal {
                text-align: center;
                width: 100%;
                padding: 5px;
                border: 1px solid $color-greyLighter;
                font-weight: $fontWeight-normal;
                font-size: $fontSize-root;
                font-style: normal;
                min-height: unset;
                height: 50px;
                color: $color-textSecondary;
            }
        }
    }

    .previewCartItem-remove {
        position: absolute;
        margin: 0;
        top: auto;
        transform: unset;
        bottom: 18px;
        left: auto;
        right: 0;
        line-height: 1;
        display: block;
        height: auto;
        width: auto;
        transition: unset;
        padding: 0;
        @include clearfix;

        svg {
            width: 15px;
            height: 15px;
            margin: 0;
            fill: $color-custom-greyDarker;
        }

        @media(min-width: 1025px) {
            &:hover {
                svg {
                    fill: $color-textBase;
                }
            }
        }
    }

    .previewCartTotalsPrice {
        list-style: none;
        margin: 0;
        padding: 20px 0 12px;
    }

    .previewCartTotals {
        display: block;
        font-size: 0;
        padding: 0;
        letter-spacing: 0;
        margin-bottom: 10px;
        @include clearfix;

        .previewCartTotals-label {
            display: inline-block;
            vertical-align: top;
            width: 120px;
            text-align: left;
            color: $color-textBase;
            font-size: $fontSize-root;
            font-weight: $fontWeight-medium;
            line-height: 24px;
            letter-spacing: $letterSpacing-base;
            text-transform: none;
        }

        .previewCartTotals-value {
            display: inline-block;
            vertical-align: top;
            width: calc(100% - 120px);
            text-align: right;
            color: $color-textBase;
            font-size: $fontSize-root;
            font-weight: $fontWeight-semibold;
            letter-spacing: $letterSpacing-base;
            line-height: 24px;
        }

        &.grandTotal {
            .previewCartTotals-label {
                font-size: $fontSize-root + 2px;
            }

            .previewCartTotals-value {
                font-size: $fontSize-root + 2px;
                padding: 0;
                top: -1px;
            }
        }
    }

    .previewCartAction {
        border: 0;
        display: block;
        font-size: 0;
        letter-spacing: 0;
        padding: 3px 0 0 0;
        margin: 0;
        width: auto;

        .previewCartAction-checkout,
        .previewCartAction-viewCart {
            display: inline-block;
            vertical-align: top;
            width: 100%;
            padding: 0;
        }

        .previewCartAction-viewCart {
            margin: 25px 0 0 0;
        }

        .button {
            margin: 0;
            padding: 0;
            width: 100%;
            text-transform: uppercase;
            height: 50px;
            line-height: 48px;
        }
    }

    .productView-info {
        > :first-child {
            margin-top: 0;
        }
    }

    .productView-info {
        margin-top: 5px;
        margin-bottom: 5px;
        width: 100%;

        .productView-info-name {
            min-width: 65px;
            font-weight: 700;
            letter-spacing: 0;
            text-align: left;
            width: auto;
        }

        .productView-info-value {
            margin-bottom: 2px;
            padding-left: 0;
        }

        .productView-info-name,
        .productView-info-value {
            float: left;
            font-family: $body-font-family;
            font-size: 14px;
            text-transform: capitalize;
        }
    }

    .productView-title {
        margin-top: 0;
        font-size: 20px;
        font-weight: 500;
        margin-bottom: 15px;
        line-height: 1.2;
    }

    .productView-brand {
        text-transform: capitalize;
        margin-bottom: 9px;
    }

    .productView-price {
        font-size: 15px;

        @media (min-width: 1024px) {
            font-size: 20px;
        }
    }

    &.previewCart2 {
        padding: 0;
        display: block;
        font-size: 0;
        letter-spacing: 0;

        .halo-free-shipping-message {
            padding-top: 0;
            padding-bottom: 30px;
        }

        .previewCartView {
            .productView-brand {
                margin-bottom: 0;
            }

            .productView-title {
                font-size: $fontSize-root;
            }
        }
    }

    @media (min-width: 551px) {
        .previewCartItem-qty {
            max-width: 193px;

            .form-label {
                width: 75px;
            }

            .form-increment {
                width: calc(100% - 88px);

                .form-input--incrementTotal {
                    text-align: center;
                    padding: 10px;
                }
            }
        }
    }

    @media (min-width: 1025px) {
        .previewCartItem-remove {
            &:hover {
                svg {
                    fill: stencilColor('color-textBase');
                }

                span {
                    color: #ce2b1e;
                    background-image: linear-gradient(transparent 97%, #ce2b1e 3%);
                    background-repeat: repeat-x;
                    background-position-y: 0;
                }
            }
        }
    }
}

.previewCart-emptyBody {
