// Quick Edit Cart
// -----------------------------------------------------------------------------

.product-edit-wrapper {
    display: block;

    .product-edit-item {
        display: block;
        padding: 35px 0;
        margin: 0 40px;

        .product-edit-itemLeft {
            display: flex;
            flex-wrap: wrap;
            align-items: flex-start;
            padding: 0;
            width: 100%;
        }

        .product-edit-itemRight {
            display: block;
            width: 100%;
            padding: 0;
            margin: 30px 0 0 0;
