    .product-edit-image {
        display: block;
        width: 100px;
        margin: 0 15px 0 0;
        position: relative;

        img {
            width: 100%;
        }

        .link {
            position: absolute;
            top: -16px;
            left: -16px;
            border-radius: 50%;
            background: #000;
            width: 32px;
            height: 32px;
            padding: 10px;

            .icon {
                display: block;
                width: 100%;
                height: 100%;
                fill: $color-white;
            }
        }
    }

    .product-edit-content {
        display: block;
        width: calc(100% - 115px);
        margin-bottom: -5px;

        .product-edit-title {
            display: block;
            margin: -6px 0 0 0;


            span,
            .subtitle {
                display: none;
                font-family: $fontFamily-sans;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 20px;
                color: $color-textSecondary;
                position: relative;
            }

            a {
                font-family: $fontFamily-sans;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 1.75;
                color: $color-textSecondary;
                overflow: hidden;
                display: -webkit-box;
                -webkit-line-clamp: stencilString('halo_card_title-line');
                text-overflow: ellipsis;
                text-decoration: none;
                text-transform: capitalize;
                padding-top: 3px;
                padding-bottom: 3px;
            }
        }

        .product-edit-variant {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            color: $color-textBase;
            line-height: 20px;
            letter-spacing: $letterSpacing-base;
            margin: 0 0 7px 0;
            padding: 0;
            text-transform: capitalize;
            opacity: 0.7;

            span {
                display: inline;

                +span {
                    margin-left: 4px;

                    &:before {
                        content: '/';
                        margin: 0 4px 0 0;
                    }
                }
            }
        }

        .product-edit-price {
            display: block;
            font-size: 0;
            letter-spacing: 0;
            margin: 9px 0 0 0;
            position: relative;

            .price-section {
                font-size: $fontSize-root;
                font-weight: $fontWeight-semibold;
                line-height: 25px;
                color: $color-textSecondary;

                &.price-section--withoutTax {
                    color: stencilColor('sale_price_color');
                }

                &.rrp-price--withoutTax {
                    margin: 0 0 5px 0;
                }

                &.non-sale-price--withoutTax {
                    display: inline-block;
                    vertical-align: bottom;
                    color: $color-textSecondary;
                    margin: 0 13px 0 0;

                    +.price-section--withoutTax {
                        display: inline-block;
                        vertical-align: bottom;
                    }
                }

                &.price-none {
                    margin: 0;

                    +.price-section--withoutTax {
                        margin-left: 13px;
                        color: $color-textSecondary;
                    }

                    &[style="display: none;"] {
                        +.price-section--withoutTax {
                            margin-left: 0;
                        }
                    }
                }
            }

            .price--non-sale {
                font-weight: $fontWeight-semibold;
            }

            >p {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                font-style: italic;
                line-height: 1.75;
                letter-spacing: $letterSpacing-base;
                color: $color-textSecondary;
                margin-bottom: 0;
            }

            .price-section--saving {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                font-style: italic;
                line-height: 28px;
                letter-spacing: $letterSpacing-base;
                color: $color-textSecondary;
                margin: 5px 0 0 0;
            }
        }

        .product-edit-increment {
            display: inline-block;
            vertical-align: top;
            width: 100%;
            max-width: 130px;
            margin: 15px 0 0 0;
            padding: 0;
            position: relative;
            font-size: 0;
            letter-spacing: 0;

            .form-label {
                display: inline-block;
                vertical-align: middle;
                width: 100%;
                margin: 0;
                padding: 0;
                color: $color-textSecondary;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                margin: 0 0 10px 0;
            }

            .form-increment {
                display: inline-block;
                vertical-align: middle;
                width: calc(100% - 30px);
                font-style: normal;

                .form-input--incrementTotal {
                    text-align: center;
                    width: 100%;
                    padding: 5px;
                    border: 1px solid $color-greyLighter;
                    font-weight: $fontWeight-normal;
                    font-size: $fontSize-root;
                    font-style: normal;
                    min-height: unset;
                    height: 50px;
                    color: $color-textSecondary;
                }
            }
        }

        .product-action {
            display: block;
            position: relative;
            margin-left: 20px;
            margin-top: 22px;

            .text {
                background-image: linear-gradient(transparent 97%, #4c4c4c 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;
                color: $color-textBase;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    background-position-y: 0;
                }

                &:before,
                &:after {
                    content: "";
                    position: absolute;
                    display: block;
                    line-height: 22px;
                    background-color: $color-textBase;
                }

                &:before {
                    top: 12px;
                    right: calc(100% + 9px);
                    width: 12px;
                    height: 2px;
                }

                &:after {
                    top: 7px;
                    right: calc(100% + 14px);
                    width: 2px;
                    height: 12px;
                }
            }
        }
    }

    .product-edit-remove {
        display: block;
        width: 100%;
        margin: 15px 0 0 0;
        text-align: center;

        .button {
            min-width: 150px;
        }
    }

    .product-edit-change {
        display: block;

        .form {
            margin: 0;
            padding: 0;
        }

        .form-field {
            display: block;
            letter-spacing: 0;
            font-size: 0;
            margin: 0;
            padding: 0;

            >.form-label {
                display: flex;
                align-items: center;
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                letter-spacing: $letterSpacing-base;
                line-height: 20px;
                color: $color-textBase;
                margin-bottom: 13px;

                small {
                    display: block;
                    color: inherit;
                    line-height: inherit;
                    margin: 0;
                    float: none;
                    color: #ea625e;
                }

                [data-option-value] {
                    font-size: $fontSize-root;
                    font-weight: $fontWeight-normal;
                    font-style: italic;
                    line-height: 20px;
                    letter-spacing: $letterSpacing-base;
                    color: $color-textBase;
                    text-transform: capitalize;
                    margin: 0 0 0 3px;
                }
            }

            +.form-field {
                margin-top: 20px;
            }
        }
    }

    .product-edit-optionsStock {
        font-size: $fontSize-root;
        font-weight: $fontWeight-normal;
        line-height: 20px;
        letter-spacing: $letterSpacing-base;
        color: stencilColor('sale_price_color');
        margin: 7px 0 0 0;
    }

    .product-edit-message {
        margin-top: 15px;
        margin-bottom: 0;
    }

    .product-edit-itemFirst {
        .product-edit-content {
            .product-edit-title {
                margin-top: -10px;

                span,
                .subtitle {
                    display: inline-block;
                    vertical-align: middle;
                    background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                    background-repeat: repeat-x;
                    background-position-y: -1px;

                    &+span {
                        font-size: $fontSize-root;
                    }
                }
            }
        }
    }

    @media (min-width: 551px) {
        .product-edit-content {
            .product-edit-increment {
                max-width: 193px;

                .form-label {
                    width: 75px;
                    margin-bottom: 0
                }

                .form-increment {
                    width: calc(100% - 88px);
                }
            }
        }
    }

    @media (min-width: 768px) {
        .product-edit-item {
            display: flex;
            align-items: flex-start;

            .product-edit-itemLeft {
                padding-right: 15px;
                width: 50%;
            }

            .product-edit-itemRight {
                width: 50%;
                margin-top: 0;
                padding-left: 15px;
            }
        }
    }

    @media (min-width: 1025px) {
        .product-edit-image {
            .link {
                &:hover {
                    background-color: #666;

                    .icon {
                        fill: $color-white;
                    }
                }
            }
        }
    }
}

.product-edit-action {
    position: absolute;
    top: auto;
    bottom: 0;
    left: 0;
    width: 100%;
    background: $color-white;
    z-index: 20;
    padding: 0 40px 40px 40px;

    .product-add-more {
        padding: 12px 0;
        border-top: 1px solid $color-custom-grey;

        .link {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            text-transform: unset;
            letter-spacing: 0;
            line-height: 30px;
            position: relative;
            margin: 0 0 0 20px;
            padding: 0;
            color: $color-textLink;
            transition: unset;

            .text {
                background-image: linear-gradient(transparent 97%, $color-textLink 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;

                .home-layout-2 &,
                .home-layout-3 &,
                .home-layout-4 & {
                    background-position-y: 0;
                }
            }

            &:before {
                content: "";
                position: absolute;
                top: 10px;
                width: 12px;
                height: 2px;
                right: calc(100% + 9px);
                background-color: $color-textLink;
            }

            &:after {
                content: "";
                position: absolute;
                top: 5px;
                width: 2px;
                height: 12px;
                right: calc(100% + 14px);
                background-color: $color-textLink;
            }
        }
    }

    .product-edit-button {
        .button {
            width: 100%;
            text-transform: uppercase;
            margin-bottom: 0;
            border-radius: 0;

            &:before {
                content: '';
                display: none;
                width: 20px;
                height: 20px;
                border: 2px solid $color-white;
                border-radius: 100%;
                border-top: 2px solid transparent;
                -webkit-animation: load-animate infinite linear 1s;
                animation: load-animate infinite linear 1s;

                @media(min-width: 1025px) {
                    border-color: $color-textSecondary;
                }
            }

            &.loading {
                pointer-events: none;
                font-size: 0;

                &:before {
                    display: inline-block;
                    vertical-align: middle;
                }
            }

            &[disabled] {
                pointer-events: none;
            }
        }
    }

    @media (min-width: 1025px) {
        .product-add-more {
            .link {
                &:hover {
                    .text {
                        color: $color-textLink-hover;
                        background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                        background-repeat: repeat-x;
                        background-position-y: -3px;

                        .home-layout-2 &,
                        .home-layout-3 &,
                        .home-layout-4 & {
                            background-position-y: 0;
                        }
                    }

                    &:after,
                    &:before {
                        background: $color-textLink-hover;
                    }
                }
            }
        }
    }
}

components/stencil/cookie/cookie.scss:
