components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content{{#unless start_collapsed}} is-open{{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" class="navList haloCustomScrollbar" data-facet="{{facet}}" data-has-more-results="{{has_more_results}}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected }} is-selected{{/if}}{{#if has_sub_categories}} has-children{{/if}}"
                        rel="nofollow"
                        data-id="{{id}}"
                        data-faceted-search-facet>
                            <span class="text">
                                {{{sanitize title}}}
                                {{#if ../show_product_counts}}
                                    {{#if count}}
                                        <span>({{count}})</span>
                                    {{/if}}
                                {{/if}}
                            </span>
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <div class="navView-all">
                <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink toggleLink-viewMore">
                    <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
                </a>
            </div>
        {{/if}}

        {{#if selected}}
            <div class="navView-clear">
                <a href="{{remove_url}}" class="toggleLink toggleLink-clear facetedSearch-clearLink" data-faceted-search-facet>
                    <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.clear'}}</span>
                </a>
            </div>
        {{/if}}
    </div>
</div>
{{/if}}
