pages/search:
---
product_results:
    shop_by_price: true
    limit: {{theme_settings.searchpage_products_per_page}}
products:
    featured:
        limit: {{theme_settings.sidebar_products_per_page}}
    new:
        limit: 25
    top_sellers:
        limit: {{theme_settings.sidebar_products_per_page}}
---
{{inject 'categoryTree' forms.search.category_options}}
{{inject 'searchProductsPerPage' theme_settings.searchpage_products_per_page}}
{{inject 'searchResultsCount' (lang 'search.results.count' count=result_count search_query=(sanitize forms.search.query))}}
{{#partial "head"}}
    {{#if pagination.product_results.previous}}
        <link rel="prev" href="{{pagination.product_results.previous}}">
    {{/if}}
    {{#if pagination.product_results.next}}
        <link rel="next" href="{{pagination.product_results.next}}">
    {{/if}}
{{/partial}}

{{#partial "page"}}
    {{{region name="search_below_header"}}}

    <div class="container">
        <div class="breadcrumb-wrapper">
            {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
        </div>

        <section class="nav">
            {{#if theme_settings.show_top_banner_category}}
                <div class="page-listing-header">
                    <div class="page-listing-header--banner">
                        {{{region name="home_banner_search"}}}
                    </div>
                    <div class="page-listing-header--content">
                        {{#if forms.search.query}}
                            <div id="search-results-heading" class="halo-description haloCustomScrollbar">
                                {{>components/search/heading}}
                            </div>
                        {{/if}}
                    </div>
                </div>
            {{else}}
                <div class="page-listing-header">
                    <div class="page-listing-header--content no-banner">
                        {{#if forms.search.query}}
                            <div id="search-results-heading" class="halo-description haloCustomScrollbar">
                                {{>components/search/heading}}
                            </div>
                        {{/if}}
                    </div>
                </div>
            {{/if}}
            <nav class="navBar navBar--sub search-nav">
                <span id="search-tabs-widget-description" class="aria-description--hidden">
                    {{lang 'search.tabs_accesibility_hint'}}
                </span>
                <ul role="tablist" class="navBar-section account-navigation" data-search-page-tabs>
                    <li role="presentation" class="navBar-item">
                        <a aria-describedby="search-tabs-widget-description" role="tab" aria-controls="product-listing-container" id="search-results-product-count" class="navBar-action" href="{{forms.search.product_url}}" data-product-results-toggle>
                            {{>components/search/product-count}}
                        </a>
                    </li>
                    <li role="presentation" class="navBar-item">
                        <a aria-describedby="search-tabs-widget-description" role="tab" aria-controls="search-results-content" id="search-results-content-count" class="navBar-action" href="{{forms.search.content_url}}" data-content-results-toggle>
                            {{>components/search/content-count}}
                        </a>
                    </li>
                    {{#unless product_results.faceted_search_enabled}}
                        <li class="navBar-item navBar-item--separate">
                            <a class="navBar-action toggleLink" data-collapsible="advanced-search-content" href="#" aria-controls="advanced-search-content" aria-expanded="false">
                                <span class="toggleLink-text toggleLink-text--on">
                                    {{lang 'forms.search.hide'}}
                                </span>
                                <span class="toggleLink-text toggleLink-text--off">
                                    {{lang 'forms.search.show'}}
                                </span>
                            </a>
                        </li>
                    {{/unless}}
                </ul>
            </nav>
            {{#unless product_results.faceted_search_enabled}}
                {{>components/search/advanced-search}}
            {{/unless}}
        </section>

        <section class="page">
            {{#if theme_settings.halo-category-sidebar}}
                <div class="halo-toolbar has-sidebar">
                    <div class="page-sidebar-mobile-wrapper">
                        <div class="page-sidebar-mobile">
                            <span class="text">{{lang 'common.sidebar'}}</span>
                            <svg class="icon" role="img" aria-label="{{lang 'common.sidebar'}}">
                                <title>{{lang 'common.sidebar'}}</title>
                                <desc>{{lang 'common.sidebar'}}</desc>
                                <use xlink:href="#icon-sidebar"></use>
                            </svg>
                        </div>
                    </div>
                    {{#if product_results.products.length}}
                        {{> components/halothemes/gallery/halo-toolbar sort=pagination.category.sort}}
                    {{/if}}
                </div>
            {{else}}
                {{#or (if theme_settings.haloStickyToolbar_viewAs) (if theme_settings.haloStickyToolbar_limit) (if theme_settings.haloStickyToolbar_sort)}}
                    <div class="halo-toolbar">
                        {{#if product_results.products.length}}
                            {{> components/halothemes/gallery/halo-toolbar sort=pagination.category.sort}}
                        {{/if}}
                    </div>
                {{/or}}
            {{/if}}
            {{#if theme_settings.halo-category-sidebar}}
                <aside class="page-sidebar{{#or (if theme_settings.haloStickyToolbar_viewAs) (if theme_settings.haloStickyToolbar_limit) (if theme_settings.haloStickyToolbar_sort)}} has-toolbar{{/or}}" id="faceted-search-container">
                    <div class="page-sidebar-close">
                        <span class="title">{{lang 'common.sidebar'}}</span>
                        <a href="#" class="close">
                            <svg class="icon" role="img" aria-label="{{lang 'common.close'}}">
                                <title>{{lang 'common.close'}}</title>
                                <desc>{{lang 'common.close'}}</desc>
                                <use xlink:href="#icon-close"></use>
                            </svg>
                        </a>
                    </div>
                    {{> components/search/sidebar}}
                </aside>
            {{/if}}
            <div class="page-content">
                <div class="product-listing-content">
                    {{#if forms.search.has_suggestions}}
                        <div class="panel panel--large">
                            <div class="panel-body">
                                {{#if forms.search.suggested_query}}
                                    <div class="search-suggestion">
                                        {{lang 'forms.search.did_you_mean'}} <strong>{{forms.search.suggested_query.query}}</strong>
                                        <a href="{{forms.search.suggested_query.url}}" class="search-refine">{{lang 'forms.search.refine'}}</a>
                                    </div>

                                    {{#if result_count '===' 0}}
                                        <div class="search-suggestion">
                                            <p>
                                                {{lang 'forms.search.your_search_for'}}
                                                "<strong>{{{sanitize forms.search.query}}}</strong>"
                                                {{lang 'forms.search.no_match'}}
                                            </p>
                                        </div>
                                    {{/if}}
                                {{/if}}

                                {{#if category_results.length}}
                                    <div class="search-suggestion">
                                        <h5 class="suggestion-title">{{lang 'forms.search.categories'}}</h5>
                                        <ul class="category-suggestion-list">
                                            {{#each category_results}}
                                                <li class="category-suggestion">
                                                    {{#each this}}
                                                        <a href="{{url}}">
                                                            <span class="text">{{name}}</span>
                                                        </a>
                                                        {{#unless @last}} > {{/unless}}
                                                    {{/each}}
                                                </li>
                                            {{/each}}
                                        </ul>
                                    </div>
                                {{/if}}

                                {{#if brand_results.length}}
                                    <div class="search-suggestion">
                                        <h5 class="suggestion-title">{{lang 'forms.search.brand'}}</h5>
                                        <ul class="category-suggestion-list">
                                            {{#each brand_results}}
                                                <li class="category-suggestion">
                                                    <a href="{{url}}">
                                                        <span class="text">{{name}}</span>
                                                    </a>
                                                </li>
                                            {{/each}}
                                        </ul>
                                    </div>
                                {{/if}}

                                {{#if result_count '===' 0}}
                                    <div class="search-suggestion">
                                        <h5 class="suggestion-title">{{lang 'forms.search.suggestions.title'}}</h5>
                                        <ul>
                                            <li>{{lang 'forms.search.suggestions.line1'}}</li>
                                            <li>{{lang 'forms.search.suggestions.line2'}}</li>
                                            <li>{{lang 'forms.search.suggestions.line3'}}</li>
                                        </ul>
                                    </div>
                                {{/if}}
                            </div>
                        </div>
                    {{/if}}

                    <div role="tabpanel" id="search-results-content" aria-labelledby="search-results-content-count" {{#if forms.search.section '!=' 'content'}}class="u-hidden"{{/if}}>
                        {{> components/search/content-listing}}
                    </div>

                    <div role="tabpanel" id="product-listing-container" aria-labelledby="search-results-product-count" {{#if forms.search.section '!=' 'product'}}class="u-hidden"{{/if}}>
                        {{#if product_results.products}}
                            {{> components/search/product-listing}}
                        {{else}}
                            <p class="page-no-products">{{lang 'search_results.no_products'}} "{{sanitize forms.search.query}}"</p>
                        {{/if}}
                    </div>
                </div>

                {{{region name="home_search_text"}}}

                {{{region name="search_below_content"}}}
            </div>
        </section>
    </div>
{{/partial}}
{{> layout/base}}

components/search/product-listing:
{{inject 'noCompareMessage' (lang 'compare.no_compare')}}

<form action="{{urls.compare}}" method='POST' data-list-name="Search Results" data-product-compare>
    {{#if theme_settings.product_list_display_mode '===' 'grid'}}
        {{> components/products/grid products=product_results.products show_compare=product_results.show_compare event="list" theme_settings=theme_settings}}
    {{else}}
        {{> components/products/list products=product_results.products show_compare=product_results.show_compare event="list" theme_settings=theme_settings}}
    {{/if}}
</form>

{{> components/common/paginator pagination.product_results total=pagination.product_results.total page_type=page_type theme_settings=theme_settings}}

{{#if show_compare}}
    <div class="compare-link">
        <a href="{{urls.compare}}" class="button" data-compare-nav>{{lang 'common.compare'}}
            (<span class="countPill">0</span>)
        </a>
    </div>
{{/if}}

components/halothemes/sidebar/halo-all-c:
{{#if theme_settings.halo-sidebar-all-categories}}
    <div class="sidebarBlock" id="allCategories">
        <h3 class="sidebarBlock-heading">{{lang 'categories.heading'}}</h3>
        {{#if categories}}
            <ul class="all-categories-list">
                {{#each categories}}
                    <li class="navPages-item{{#if is_active}} current-cate is-clicked{{/if}}">
                        {{#if children}}
                            <a class="navPages-action has-subMenu" href="{{url}}">
                                <span class="text">{{name}}</span>
                            </a>
                            <span class="icon-dropdown">
                                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
                            </span>
                            <ul class="dropdown-category-list navPages-level-2">
                                {{#each children}}
                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                        {{#if children}}
                                            <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}">
                                                <span class="text">{{name}}</span>
                                            </a>
                                            <span class="icon-dropdown">
                                                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
                                            </span>
                                            <ul class="dropdown-category-list navPages-level-3">
                                                {{#each children}}
                                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                                        <a class="navPage-subMenu-action navPages-action 123" href="{{url}}">
                                                            <span class="text">{{name}}</span>
                                                        </a>
                                                    </li>
                                                {{/each}}
                                            </ul>
                                        {{else}}
                                            <a class="navPage-subMenu-action navPages-action" href="{{url}}">
                                                <span class="text">{{name}}</span>
                                            </a>
                                        {{/if}}
                                    </li>
                                {{/each}}
                            </ul>
                        {{else}}
                            <a class="navPages-action" href="{{url}}">
                                <span class="text">{{name}}</span>
                            </a>
                        {{/if}}
                    </li>
                {{/each}}
            </ul>
        {{/if}}
    </div>
{{/if}}

components/category/shop-by-price:
{{#and theme_settings.shop_by_price_visibility category.shop_by_price}}
    <div class="sidebarBlock">
        <h2 class="sidebarBlock-heading heading-price" data-shop-by-price>{{lang 'category.shop_by_price'}}</h2>

        {{> components/faceted-search/range-form shop_by_price=theme_settings.shop_by_price_visibility}}

        <div class="reset-filters">
            <a href="{{category.url}}" class="navList-action reset-btn">{{lang 'category.reset'}}</a>
            <span class="reset-message aria-description--hidden">{{lang 'category.filter_reset_announcement'}}</span>
        </div>
    </div>
{{/and}}

components/products/list:
{{inject "isProductListPresented" true}}

<ul class="productList">
    {{#each products}}
    <li class="product">
		{{>components/products/list-item show_compare=../show_compare show_rating=../settings.show_product_rating theme_settings=../theme_settings customer=../customer event=../event settings=../settings position=(add @index 1)}}
    </li>
    {{/each}}
</ul>

components/common/footer:
{{#if banners.bottom}}
    <div class="banners" data-banner-location="bottom">
        {{#each (limit banners.bottom_metadata 1)}}
            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                <div data-event-type="promotion-click">
                    {{{this.content}}}
                </div>
            </div>
        {{/each}}
    </div>
{{/if}}

{{{region name="home_policy_block--global"}}}
{{{region name="home_policy_block"}}}

<footer class="footer" role="contentinfo">
    <div class="container">
        <section class="footer-info{{#all (if theme_settings.footer_info_logo) (if theme_settings.footer-info-1-heading) (if theme_settings.footer-info-2-heading) (if theme_settings.footer-info-3-heading) (if theme_settings.footer-info-4-heading)}} footer-info-show-all{{else}} footer-info-no-show-all{{/all}}">
            {{#if theme_settings.footer_info_logo}}
                <article class="footer-info-col "  data-section-type="storeInfo">
                    {{#if theme_settings.footer_info_logo}}
                        <div class="footer-info__logo">
                           <div class="t4s-footer-raw-html t4s-rte t4s-hidden-mobile-false" style="--animation: ;--delay-animation:s;">
                            <dotlottie-player src="https://cdn.shopify.com/s/files/1/0029/5682/0569/files/Lottie_-_CR_Animation_Green.json?v=1714062504" background="transparent" speed="1" style="width: 300px; height: 300px" direction="1" playMode="normal" loop  autoplay></dotlottie-player>
<dotlottie-player style="display:none;" src="https://cdn.shopify.com/s/files/1/0029/5682/0569/files/Lottie_-_CR_Animation.json?v=1692980413" background="transparent" speed="1" style="width: 300px; height: 300px" direction="1" playMode="normal" loop  autoplay></dotlottie-player>


</div>
                        </div>
                    {{/if}}
                    <address style="display:none;">{{nl2br settings.address}}</address>

                    {{#if theme_settings.halo_homepage_layout_1}}
                        {{#if settings.phone_number}}
                            <p class="footer-info__phone" style="display:none;"><a href="tel:{{settings.phone_number}}"><svg><use xlink:href="#icon-chat" /></svg> <span>{{lang 'footer.call_us' phone_number=settings.phone_number}}</span></a></p>
                        {{/if}}

                        {{#if theme_settings.info_email_support}}
                            <p class="footer-info__phone"><a href="mailto:{{theme_settings.info_email_support}}"><svg><use xlink:href="#icon-mail" /></svg> <span>{{theme_settings.info_email_support}}</span></a></p>
                        {{/if}}
                    {{else}}
                        {{#if settings.phone_number}}
                            <p class="footer-info__phone"><a href="tel:{{settings.phone_number}}"><span>{{lang 'footer.home2_call_us' phone_number=settings.phone_number}}</span></a></p>
                        {{/if}}

                        {{#if theme_settings.info_email_support}}
                            <p class="footer-info__phone"><a href="mailto:{{theme_settings.info_email_support}}"><span>{{lang 'footer.home2_email' email=theme_settings.info_email_support}}</span></a></p>
                        {{/if}}

                        {{#if theme_settings.show_social-links}}
                            <div class="footer-info-col--social">
                                {{> components/common/social-links}}
                            </div>
                        {{/if}}
                    {{/if}}
                </article>
            {{/if}}

            {{#if theme_settings.footer-info-1-heading}}
                <article class="footer-info-col open-dropdown"  data-section-type="footer-categories">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-1-heading}}</h3>
                    <ul class="footer-info-list">
                        {{#each (limit categories theme_settings.categories_show_footer_limit)}}
                            <li>
                                <a href="{{url}}"><span>{{name}}</span></a>
                            </li>
                        {{/each}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-info-2-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="3" data-section-type="footer-webPages">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-2-heading}}</h3>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer-info-2-text1}}
                            <li><a href="{{theme_settings.footer-info-2-link1}}"><span>{{theme_settings.footer-info-2-text1}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text2}}
                            <li><a href="{{theme_settings.footer-info-2-link2}}"><span>{{theme_settings.footer-info-2-text2}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text3}}
                            <li><a href="{{theme_settings.footer-info-2-link3}}"><span>{{theme_settings.footer-info-2-text3}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text4}}
                            <li><a href="{{theme_settings.footer-info-2-link4}}"><span>{{theme_settings.footer-info-2-text4}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text5}}
                            <li><a href="{{theme_settings.footer-info-2-link5}}"><span>{{theme_settings.footer-info-2-text5}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text6}}
                            <li><a href="{{theme_settings.footer-info-2-link6}}"><span>{{theme_settings.footer-info-2-text6}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-2-text7}}
                            <li><a href="{{theme_settings.footer-info-2-link7}}"><span>{{theme_settings.footer-info-2-text7}}</span></a></li>
                        {{/if}}
                        {{#if settings.gift_certificates_enabled}}
                            <li>
                                <a href="{{urls.gift_certificate.purchase}}"
                                   aria-label="{{lang 'common.gift_cert'}}"
                                >
                                    {{lang 'common.gift_cert'}}
                                </a>
                            </li>
                        {{/if}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-info-3-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="4" data-section-type="footer-webPages">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-3-heading}}</h3>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer-info-3-text1}}
                            <li><a href="{{theme_settings.footer-info-3-link1}}"><span>{{theme_settings.footer-info-3-text1}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text2}}
                            <li><a href="{{theme_settings.footer-info-3-link2}}"><span>{{theme_settings.footer-info-3-text2}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text3}}
                            <li><a href="{{theme_settings.footer-info-3-link3}}"><span>{{theme_settings.footer-info-3-text3}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text4}}
                            <li><a href="{{theme_settings.footer-info-3-link4}}"><span>{{theme_settings.footer-info-3-text4}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text5}}
                            <li><a href="{{theme_settings.footer-info-3-link5}}"><span>{{theme_settings.footer-info-3-text5}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text6}}
                            <li><a href="{{theme_settings.footer-info-3-link6}}"><span>{{theme_settings.footer-info-3-text6}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-info-3-text7}}
                            <li><a href="{{theme_settings.footer-info-3-link7}}"><span>{{theme_settings.footer-info-3-text7}}</span></a></li>
                        {{/if}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-info-4-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="5" data-section-type="footer-brands">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-4-heading}}</h3>
                    {{#if shop_by_brand.length}}
                        <ul class="footer-info-list">
                            {{#each (limit shop_by_brand theme_settings.brand_show_footer_limit)}}
                                <li>
                                    <a href="{{url}}" aria-label="{{name}}"><span>{{name}}</span></a>
                                </li>
                            {{/each}}
                            <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                        </ul>
                    {{/if}}
                </article>
            {{/if}}

            {{#or theme_settings.show_social-links settings.show_newsletter_box}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="6" data-section-type="newsletterSubscription">
                    {{#or (if theme_settings.halo_homepage_layout_1) (if theme_settings.halo_homepage_layout_3)}}
                        {{#if theme_settings.show_social-links}}
                            <h3 class="footer-info-heading footer-info-heading--toggle">{{lang 'social.follow'}}</h3>
                            <div class="footer-info-col--social">
                                {{> components/common/social-links}}
                            </div>
                        {{/if}}
                    {{/or}}
                    {{#if settings.show_newsletter_box}}
                        {{#if theme_settings.halo_subscription_form}}
                            {{> components/common/subscription-form}}
                        {{/if}}
                    {{/if}}
                    {{#if theme_settings.h_dApp}}
                        <div class="footer-info-rte">
                            <div class="typography_img">
                                <img src="https://cdn11.bigcommerce.com/s-{{settings.store_hash}}/product_images/uploaded_images/{{theme_settings.h_dImg1}}" alt="{{theme_settings.h_dImg1}}" />
                            </div>
                            <div class="typography_img">
                                <img src="https://cdn11.bigcommerce.com/s-{{settings.store_hash}}/product_images/uploaded_images/{{theme_settings.h_dImg2}}" alt="{{theme_settings.h_dImg2}}" />
                            </div>
                        </div>
                    {{/if}}
                    {{#if theme_settings.halo_homepage_layout_4}}
                        {{#if theme_settings.show_social-links}}
                            <div class="footer-info-col--social">
                                {{> components/common/social-links}}
                            </div>
                        {{/if}}
                    {{/if}}
                </article>
            {{/or}}
        </section>
    </div>

    <div class="footer-bottom">
        <div class="container footer-bottom__container{{#if theme_settings.show_copyright_footer_2}} has-item-center{{/if}}">
            <div class="footer-bottom__left" data-halo-animate="7">
                {{#if theme_settings.show_copyright_footer}}
                    <div class="footer-copyright">
                        <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}}{{lang 'footer.dot'}}</p>
                    </div>
                {{/if}}
                {{#if theme_settings.show_powered_by}}
                    <div class="footer-copyright">
                        {{{theme_settings.powered_by-text}}}
                    </div>
                {{/if}}
            </div>
            <div class="footer-bottom__right" data-halo-animate="8">
                {{#if settings.paypal_commerce_credit_message}}
                <div class="footer-copyright">
                    <p class="paypal-credit">{{nl2br settings.paypal_commerce_credit_message}}</p>
                </div>
                {{/if}}
                {{#if theme_settings.show_payments_footer}}
                    {{> components/common/payment-icons}}
                {{/if}}
            </div>
        </div>
    </div>
