<script>







function get_coll(id){


    var query=`query MyFirstQuery {
        site {
          product(entityId:`+id+`){ 

      metafields(namespace: "ct_metafields", keys: ["colpg"]) {
        edges {
          node {
            id
            key
            value
          }
        }
      }
      id
    }
  }
      }`;

      fetch('/graphql', {
    method: 'POST',
    credentials: 'same-origin',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'Bearer {{ settings.storefront_api.token }}' // use auto-generated token
    },
    body: JSON.stringify({
      query: query
    })
  })

  .then(res => res.json())
  .then(function(data){
       data => data.json();
       console.log(data);
       console.log(data.data.site.product.metafields.edges[0].node.value)
      var edg=data.data.site.product.metafields.edges[0].node.value;
      tt=JSON.parse(edg);
      var title=tt.bubt;
      var tcolor=tt.bubfc;
      var bcolor=tt.bubbg;

         var xt=document.getElementById('card-mta'+id);
         console.log(xt);
        xt.innerHTML=title;
         xt.style.backgroundColor= bcolor;
         xt.style.color=tcolor;



  }) // will log JSON result to browser console
  .catch(error => console.error(error));

}


(function() {
   // your page initialization code here
   // the DOM will be available here
get_coll({{id}});
})();
</script>

