<script>


function get_meta(sku){
    var xx='{{product.id}}';
    var query=`query MyFirstQuery {
        site {
          product(entityId:`+xx+`){ 
            variants{
              edges{
                node{
                    sku
                  metafields(namespace: "ct_metafields", keys: ["desblock"]) {
                    edges{
                      node{

                        key
                        value
                      }
                    }
                  } 
                }
              }
            }
          }
        }
      }`;
    console.log(xx);
      fetch('/graphql', {
    method: 'POST',
    credentials: 'same-origin',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'Bearer {{ settings.storefront_api.token }}' // use auto-generated token
    },
    body: JSON.stringify({
      query: query
    })
  })

  .then(res => res.json())
  .then(function(data){
       data => data.json();
       console.log(data.data.site.product.variants.edges)
      var edg=data.data.site.product.variants.edges;

$.each( edg, function( key, value ) {
    //console.log(value)
 if(value.node.sku==sku){
   var  tt=this.node.metafields.edges[0].node.value;

  tt=$.parseJSON(tt);
  var b1=tt.dbox1;

  var b2=tt.dbox2;
  var vrurl = tt.vrur;
  var bubc=tt.bubc;

  $(".pfcon .pdf .con").html(b1);
  $(".pad .con").html(b2);
$(".viewr").attr('href',vrurl);

$(".pdf").css('background-color',bubc);
$(".pad").css('background-color',bubc);
$(".pbu").css('background-color',bubc);
$(".hwtusecon").css('background-color',bubc);

 }
});

  }) // will log JSON result to browser console
  .catch(error => console.error(error));

}



    const $productId = '{{product.id}}';



    (function() {
var target = document.querySelector('.productView-info-value');

// Create an observer instance.
var observer = new MutationObserver(function(mutations) {
    var sku=target.innerText;
    get_meta(sku);

});

// Pass in the target node, as well as the observer options.
observer.observe(target, {
    attributes:    true,
    childList:     true,
    characterData: true
});

})();



</script>



