components/halothemes/sidebar/halo-all-c:
{{#if theme_settings.halo-sidebar-all-categories}}
    <div class="sidebarBlock" id="allCategories">
        <h3 class="sidebarBlock-heading">{{lang 'categories.heading'}}</h3>
        {{#if categories}}
            <ul class="all-categories-list">
                {{#each categories}}
                    <li class="navPages-item{{#if is_active}} current-cate is-clicked{{/if}}">
                        {{#if children}}
                            <a class="navPages-action has-subMenu" href="{{url}}">
                                <span class="text">{{name}}</span>
                            </a>
                            <span class="icon-dropdown">
                                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
                            </span>
                            <ul class="dropdown-category-list navPages-level-2">
                                {{#each children}}
                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                        {{#if children}}
                                            <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}">
                                                <span class="text">{{name}}</span>
                                            </a>
                                            <span class="icon-dropdown">
                                                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
                                            </span>
                                            <ul class="dropdown-category-list navPages-level-3">
                                                {{#each children}}
                                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                                        <a class="navPage-subMenu-action navPages-action 123" href="{{url}}">
                                                            <span class="text">{{name}}</span>
                                                        </a>
                                                    </li>
                                                {{/each}}
                                            </ul>
                                        {{else}}
                                            <a class="navPage-subMenu-action navPages-action" href="{{url}}">
                                                <span class="text">{{name}}</span>
                                            </a>
                                        {{/if}}
                                    </li>
                                {{/each}}
                            </ul>
                        {{else}}
                            <a class="navPages-action" href="{{url}}">
                                <span class="text">{{name}}</span>
                            </a>
                        {{/if}}
                    </li>
                {{/each}}
            </ul>
        {{/if}}
    </div>
{{/if}}

components/category/shop-by-price:
{{#and theme_settings.shop_by_price_visibility category.shop_by_price}}
    <div class="sidebarBlock">
        <h2 class="sidebarBlock-heading heading-price" data-shop-by-price>{{lang 'category.shop_by_price'}}</h2>

        {{> components/faceted-search/range-form shop_by_price=theme_settings.shop_by_price_visibility}}

        <div class="reset-filters">
            <a href="{{category.url}}" class="navList-action reset-btn">{{lang 'category.reset'}}</a>
            <span class="reset-message aria-description--hidden">{{lang 'category.filter_reset_announcement'}}</span>
        </div>
    </div>
{{/and}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content{{#unless start_collapsed}} is-open{{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" class="navList haloCustomScrollbar" data-facet="{{facet}}" data-has-more-results="{{has_more_results}}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected }} is-selected{{/if}}{{#if has_sub_categories}} has-children{{/if}}"
                        rel="nofollow"
                        data-id="{{id}}"
                        data-faceted-search-facet>
                            <span class="text">
                                {{{sanitize title}}}
                                {{#if ../show_product_counts}}
                                    {{#if count}}
                                        <span>({{count}})</span>
                                    {{/if}}
                                {{/if}}
                            </span>
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <div class="navView-all">
                <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink toggleLink-viewMore">
                    <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
                </a>
            </div>
        {{/if}}

        {{#if selected}}
            <div class="navView-clear">
                <a href="{{remove_url}}" class="toggleLink toggleLink-clear facetedSearch-clearLink" data-faceted-search-facet>
                    <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.clear'}}</span>
                </a>
            </div>
        {{/if}}
    </div>
</div>
{{/if}}

components/faceted-search/facets/rating:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content facetedSearch-content--{{dashcase facet}}{{#unless start_collapsed}} is-open{{/unless}}">
